css:
@-moz-document url-prefix("https://chamilo.hogent.be/") {
body {
 	background-color: #FE1F6F;
  font-family: "Comic Sans MS";
	cursor:url(http://i.imgur.com/QLhhPC5.png) 15 15, move;
}


#header1{
  background: #FE1F6F;
}
.banner{
    content: url('http://37.media.tumblr.com/aa76092b840abbb116146df39f041967/tumblr_mukhrdvGzw1r6d0sao1_500.gif') !important;
}

#outerframe{
  background-image: url("http://media0.giphy.com/media/4ci8d3rJNrdAY/giphy.gif");
}


a:hover{
  cursor:url(http://i.imgur.com/QLhhPC5.png) 15 15, move;
  animation: animationaaaa linear 1s;
  animation-iteration-count: infinite;
  transform-origin: 50% 50%;
  -webkit-animation: animationaaaa linear 1s;
  -webkit-animation-iteration-count: infinite;
  -webkit-transform-origin: 50% 50%;
  -moz-animation: animationaaaa linear 1s;
  -moz-animation-iteration-count: infinite;
  -moz-transform-origin: 50% 50%;
}

@keyframes animationaaaa{
  0% {
    transform:  scaleX(10.00) scaleY(10.00) ;
  }
  100% {
    transform:  scaleX(10.00) scaleY(10.00) ;
  }
}

@-moz-keyframes animationaaaa{
  0% {
    -moz-transform:  scaleX(10.00) scaleY(10.00) ;
  }
  100% {
    -moz-transform:  scaleX(10.00) scaleY(10.00) ;
  }
}

@-webkit-keyframes animationaaaa {
  0% {
    -webkit-transform:  scaleX(10.00) scaleY(10.00) ;
  }
  100% {
    -webkit-transform:  scaleX(10.00) scaleY(10.00) ;
  }
}
 
            


#main{
  animation: animationtab ease-in-out 4s;
  animation-iteration-count: infinite;
  transform-origin: 50% 50%;
  -webkit-animation: animationtab ease-in-out 4s;
  -webkit-animation-iteration-count: infinite;
  -webkit-transform-origin: 50% 50%;
  -moz-animation: animationtab ease-in-out 4s;
  -moz-animation-iteration-count: infinite;
  -moz-transform-origin: 50% 50%;
}            
            
@keyframes animationtab{
  0% {
    transform:  translate(0px,0px)  ;
  }
  50% {
    transform:  translate(800px,0px)  ;
  }
  100% {
    transform:  translate(0px,0px)  ;
  }
}

@-moz-keyframes animationtab{
  0% {
    -moz-transform:  translate(0px,0px)  ;
  }
  50% {
    -moz-transform:  translate(800px,0px)  ;
  }
  100% {
    -moz-transform:  translate(0px,0px)  ;
  }
}

@-webkit-keyframes animationtab {
  0% {
    -webkit-transform:  translate(0px,0px)  ;
  }
  50% {
    -webkit-transform:  translate(800px,0px)  ;
  }
  100% {
    -webkit-transform:  translate(0px,0px)  ;
  }
}

.description > div:first-of-type > img,
#user_account img {
  content: url('http://37.media.tumblr.com/aa76092b840abbb116146df39f041967/tumblr_mukhrdvGzw1r6d0sao1_500.gif') !important; 
}
            
            
ul{
	background-color:pink;
}

.portal_tab{
  -webkit-animation: color_change 1s infinite alternate;
  -moz-animation: color_change 1s infinite alternate;
  -ms-animation: color_change 1s infinite alternate;
  -o-animation: color_change 1s infinite alternate;
  animation: color_change 1s infinite alternate;
}
  
  @-webkit-keyframes color_change {
  from { background-color: blue; }
  to { background-color: red; }
}
@-moz-keyframes color_change {
  from { background-color: blue; }
  to { background-color: red; }
}
@-ms-keyframes color_change {
  from { background-color: blue; }
  to { background-color: red; }
}
@-o-keyframes color_change {
  from { background-color: blue; }
  to { background-color: red; }
}
@keyframes color_change {
  from { background-color: blue; }
  to { background-color: red; }
}
  
.title{
  animation: animationFrames ease-in-out 4s;
  animation-iteration-count: infinite;
  transform-origin: 50% 50%;
  -webkit-animation: animationFrames ease-in-out 4s;
  -webkit-animation-iteration-count: infinite;
  -webkit-transform-origin: 50% 50%;
  -moz-animation: animationFrames ease-in-out 4s;
  -moz-animation-iteration-count: infinite;
  -moz-transform-origin: 50% 50%;
}
  
@keyframes animationFrames{
  0% {
    transform:  translate(0px,0px)  ;
  }
  50% {
    transform:  translate(0px,400px)  ;
  }
  100% {
    transform:  translate(0px,0px)  ;
  }
}

@-moz-keyframes animationFrames{
  0% {
    -moz-transform:  translate(0px,0px)  ;
  }
  50% {
    -moz-transform:  translate(0px,400px)  ;
  }
  100% {
    -moz-transform:  translate(0px,0px)  ;
  }
}

@-webkit-keyframes animationFrames {
  0% {
    -webkit-transform:  translate(0px,0px)  ;
  }
  50% {
    -webkit-transform:  translate(0px,400px)  ;
  }
  100% {
    -webkit-transform:  translate(0px,0px)  ;
  }
}

.portal_block{
  animation: animationportal linear 4s;
  animation-iteration-count: infinite;
  transform-origin: 50% 50%;
  -webkit-animation: animationportal linear 4s;
  -webkit-animation-iteration-count: infinite;
  -webkit-transform-origin: 50% 50%;
  -moz-animation: animationportal linear 4s;
  -moz-animation-iteration-count: infinite;
  -moz-transform-origin: 50% 50%;
}

@keyframes animationportal{
  0% {
    transform:  rotate(0deg) ;
  }
  100% {
    transform:  rotate(360deg) ;
  }
  100% {
    transform:  rotate(360deg) ;
  }
}

@-moz-keyframes animationportal{
  0% {
    -moz-transform:  rotate(0deg) ;
  }
  100% {
    -moz-transform:  rotate(360deg) ;
  }
  100% {
    -moz-transform:  rotate(360deg) ;
  }
}

@-webkit-keyframes animationportal {
  0% {
    -webkit-transform:  rotate(0deg) ;
  }
  100% {
    -webkit-transform:  rotate(360deg) ;
  }
  100% {
    -webkit-transform:  rotate(360deg) ;
  }
}

li{
  animation: animationli linear 0.02s;
  animation-iteration-count: infinite;
  transform-origin: 50% 50%;
  -webkit-animation: animationli linear 0.02s;
  -webkit-animation-iteration-count: infinite;
  -webkit-transform-origin: 50% 50%;
  -moz-animation: animationli linear 0.02s;
  -moz-animation-iteration-count: infinite;
  -moz-transform-origin: 50% 50%;
}

@keyframes animationli{
  0% {
    transform:  translate(0px,0px)  ;
  }
  100% {
    transform:  translate(18px,-14px)  ;
  }
}

@-moz-keyframes animationli{
  0% {
    -moz-transform:  translate(0px,0px)  ;
  }
  100% {
    -moz-transform:  translate(18px,-14px)  ;
  }
}

@-webkit-keyframes animationli {
  0% {
    -webkit-transform:  translate(0px,0px)  ;
  }
  100% {
    -webkit-transform:  translate(18px,-14px)  ;
  }
}



}
