css:
@-moz-document domain("furaffinity.net") {
#admin_notice_do_not_adblock {
    margin: auto !important;
    margin-bottom: 8px !important;
    width: 75% !important;
}

#admin_notice_do_not_adblock h4 {
    background-image: -webkit-gradient( linear, left top, left bottom, color-stop(0, #151A28), color-stop(1, #222736) ) !important;
    border-bottom: 1px solid #494F5F !important;
    font-size: 11px !important;
    margin: -4px !important;
    margin-bottom: 4px !important;
    padding: 4px !important;
    padding-bottom: 2px !important;
}

#admin_notice_do_not_adblock td {
    text-align: justify !important;
}

#admin_notice_do_not_adblock td[width="32"] {
    display: none !important;
}

#frontpage .cat {
    font-size: 12px !important;
}

#frontpage table td.alt1 table.hidden1 {
    margin-top: 16px !important;
}

#gallery > table.maintable, #scraps > table.maintable, #favorites > table.maintable, #stats-submission > table.maintable {
    border-spacing: 0 !important;
}

#logout-link {
    color: #ff9999 !important;
}

#messagecenter-other .no-messages {
    margin-bottom: 34px !important;
    margin-top: 34px !important;
}

#messagecenter-other, #messagecenter-submissions {
    background: #171a22 !important;
    border: 1px solid #494F5F !important;
}

#messagecenter-submissions .no-messages {
    margin-bottom: 34px !important;
    margin-top: 0px !important;
}

#my-username {
    color: #9999ff !important;
}

#pms-form .maintable.container {
    background: #14171F !important;
    border-right: 1px solid #494F5F !important;
    border-spacing: 0 !important;
    border-top: 1px solid #494F5F !important;
}

#pms-form .maintable.container tr td {
    border-bottom: 1px solid #494F5F !important;
    border-left: 1px solid #494F5F !important;
}

#pms-form .maintable.container tr td.cat {
    border-top: none !important;
}

#pms-form .maintable.container tr.checked td.alt1, #pms-form .maintable.container tr.hover.checked td.alt1 {
    background: rgba(128, 128, 192, 0.25) !important;
}

#pms-form .maintable.container tr.hover td.alt1 {
    background: rgba(128, 128, 192, 0.125) !important;
}

#sfw-toggle a {
    text-decoration: line-through !important;
}

#sfw-toggle.active a {
    text-decoration: none !important;
}

#sfw-toggle.active a, font[color="#D2FFE4"] {
    color: #99FF99 !important;
}

.addpad {
    padding: 4px !important;
}

.bdot {
    border-bottom: 1px dotted #494F5F !important;
    text-align: left !important;
}

.block-banners {
    background-image: -webkit-gradient(
        linear,
        left top,
        left bottom,
        color-stop(0, rgb(29,32,40)),
        color-stop(1, rgb(20,23,31))
    ) !important;
    border: 2px solid !important;
    border-bottom-right-radius: 4px !important;
    border-color: #494F5F !important;
    border-left: none !important;
    border-top: none !important;
    box-shadow: 0px 0px 16px black !important;
    left: 0 !important;
    padding-right: 4px !important;
    position: fixed !important;
    top: 0 !important;
    z-index: 10000 !important;
}

.block-banners #fa_header {
    -webkit-transition: background-color 0.125s ease-in-out, box-shadow 0.125s ease-in-out !important;
    background: url("http://forums.furaffinity.net/images/misc/faf_logo.png") !important;
    background-repeat: no-repeat !important;
    background-size: 400px 48px !important;
    left: 0px !important;
    position: relative !important;
}

.block-banners #fa_header:active {
    background-color: #14171F !important;
    box-shadow: -1px 0 0 rgba(255, 255, 255, 0.125) inset, 1px 0 0 rgba(0, 0, 0, 0.125) inset !important;
}

.block-banners, #fa_header {
    height: 48px !important;
    width: 240px !important;
}

.block-menu-top {
    background-image: -webkit-gradient(
        linear,
        left top,
        left bottom,
        color-stop(0, rgb(35,35,35)),
        color-stop(1, rgb(26,26,26))
    ) !important;
    border-bottom: 2px solid #545454 !important;
    box-shadow: 0px 0px 12px black !important;
    left: 0 !important;
    position: fixed !important;
    top: 0 !important;
    width: 100% !important;
    z-index: 9999 !important;
}

.block-menu-top .header_bkg {
    height: 32px !important;
    white-space: nowrap !important;
    float: right !important;
}

.block-menu-top .header_bkg:first-child {
    float: left !important;
}

.block-menu-top tr {
    float: right !important;
    margin: auto !important;
    width: -webkit-calc(100% - 248px) !important;
}

.block-menu-top tr ul {
    margin: 0px 4px !important;
}

.cat {
    background-image: -webkit-gradient(
        linear,
        left top,
        left bottom,
        color-stop(0, rgb(29,32,40)),
        color-stop(1, rgb(20,23,31))
    ) !important;
    border: none !important;
    height: 20px !important;
    margin: 0px !important;
    padding-bottom: 0px !important;
    padding-top: 0px !important;
    vertical-align: middle !important;
}

.cat table td {
    padding: 4px !important;
}

.container-comment {
    background: #33373F !important;
    border: 1px solid #33373F !important;
    color: #A0A0A0 !important;
}

.container-comment .icon {
    height: 100px !important;
    padding: 4px !important;
    width: 100px !important;
}

.container-comment > tbody > tr:first-of-type > td:last-of-type {
    background: none !important;
    border: none !important;
    color: #E0BD2D !important;
    margin-left: 2px !important;
    margin-top: 5px !important;
    position: absolute !important;
}

.container-comment > tbody > tr:first-of-type > td:last-of-type.comment-deleted {
    background-image: -webkit-gradient( linear, left top, left bottom, color-stop(0, #281D20), color-stop(1, #1F1417) ) !important;
    margin: 0 !important;
    position: static !important;
}

.container-comment td.cat {
    border-top: solid 1px #33373F !important;
    height: 20px !important;
    padding-bottom: 0px !important;
    padding-top: 0px !important;
    text-align: left !important;
}

.container-comment td[height="21"] {
    height: auto !important;
}

.container-comment th.lead {
    border: none !important;
    box-shadow: -3px 4px #14171F inset,  1px 5px #33373F inset, -4px -1px #33373F inset !important;
/*    height: auto !important;*/
/*    line-height: -webkit-calc(1em + 1px) !important;*/
    padding: 5px !important;
/*    padding-bottom: 5px !important;*/
/*    padding-top: 9px !important;*/
}

.container-comment th[width="20"], .container-comment td[width="20"], .maintable th[width="20"], .maintable td[width="20"] {
    display: none !important;
}

.footer {
    background: #1c1c1c !important;
    margin-bottom: -8px !important;
    margin-top: 4px !important;
    outline: 2px solid #545454 !important;
    padding-bottom: 12px !important;
}

.footer p {
    background: #1c1c1c !important;
    border-top: 2px solid #545454 !important;
    bottom: 0 !important;
    box-shadow: 0px 0px 12px black !important;
    left: 0 !important;
    margin: 0 !important;
    padding: 4px 0px !important;
    position: fixed !important;
    width: 100% !important;
}

.footer p a {
    padding: 4px !important;
}

.footer p a:active {
    background: #1A1A1A !important;
    box-shadow: -1px 0 0 rgba(255, 255, 255, 0.125) inset, 1px 0 0 rgba(0, 0, 0, 0.125) inset !important;
}

.header_bkg {
    background: none !important;
}

.lead {
    background-image: -webkit-gradient(
        linear,
        left top,
        left bottom,
        color-stop(0, rgb(21,26,40)),
        color-stop(1, rgb(34,39,54))
    ) !important;
    border: solid 1px #33373F !important;
    padding: 4px !important;
}

.links {
    color: #ffffff !important;
}

.maintable, .innertable {
    background-color: #494F5F !important;
    border-radius: 2px !important;
}

.menu li ul {
    -webkit-transition: all 0.25s ease-in-out !important;
    display: block !important;
    opacity: 0 !important;
    visibility: hidden !important;
}

.menu li ul li {
    height: auto !important;
}

.menu ul li ul {
    border: 1px solid #545454 !important;
}

.menu ul li ul li {
    border: none !important;
}

.menu ul li ul li, .menu ul li ul li a {
    padding: 4px 2px !important;
}

.menu:hover li ul {
    opacity: 1 !important;
    visibility: visible !important;
}

.remove-nuke, [name="nuke-journals"] {
    text-shadow: 0 0 8px #F66 !important;
}

.section-controls, .date-divider {
    border: none !important;
    border-bottom: 1px dotted #494F5F !important;
    margin-bottom: 4px !important;
}

.tab {
    margin-left: 8px !important;
    padding-bottom: 1px !important;
}

.tab a {
    background-image: -webkit-gradient(
        linear,
        left top,
        left bottom,
        color-stop(0, rgb(29,32,40)),
        color-stop(1, rgb(20,23,31))
    ) !important;
    border: solid 1px #33373F !important;
    border-bottom: none !important;
    border-top-left-radius: 3px !important;
    border-top-right-radius: 3px !important;
    padding: 4px !important;
}

.tab u {
    background-image: -webkit-gradient(
        linear,
        left top,
        left bottom,
        color-stop(0, rgb(24,34,55)),
        color-stop(1, rgb(14,24,46))
    ) !important;
    border: solid 1px #494F5F !important;
    border-bottom: dotted 1px #494F5F !important;
    border-top-left-radius: 3px !important;
    border-top-right-radius: 3px !important;
    padding: 4px !important;
}

.textbox {
    vertical-align: baseline !important;
}

a {
    -webkit-transition: color 0.125s ease-in-out !important;
    color: #EAEAEA !important;
    text-shadow: 1px 1px #000 !important;
}

a.oldest, a.newest, a.more {
    font-weight: normal !important;
    margin: 1px !important;
    padding: 1px 6px !important;
    text-shadow: none !important;
    width: auto !important;
}

a.oldest:hover, a.newest:hover, a.more:hover {
    color: #000000 !important;
}

a.prev, a.next {
    display: none !important;
}

a:hover, .tab a:hover, center.flow a:hover, .alt1 a:hover, .footer a:hover, .cat a:hover, li a:hover, td a:hover, #my-username:hover, #logout-link:hover, #sfw-toggle.active a:hover, a:active, .menu ul li ul li:hover a {
    color: #EAEAEA !important;
}

a:link, a:visited {
    color: #E0BD2D !important;
    text-decoration: none !important;
}

body {
    border: 2px solid #494F5F !important;
    box-shadow: 0px 0px 8px black !important;
    color: #ffffff !important;
}

body > div {
    background: #14171F !important;
    padding-bottom: 8px !important;
    padding-top: 8px !important;
}

body > div .content {
    text-shadow: 1px 1px black !important;
}

body, body > div {
    border-radius: 4px !important;
}

body, table.content * {
    font-family: "Lucida Grande", Verdana, Helvetica, Arial, sans-serif !important;
}

center.flow a, center.flow span {
    font-size: 7.5pt !important;
}

center.flow b {
    height: auto !important;
    padding-bottom: 4px !important;
}

center.flow b span {
    height: -webkit-calc(2em + 4px) !important;
    max-height: none !important;
}

center.flow.userpage-submissions b, center.flow.userpage-favorites b {
    -webkit-transition: all 0.25s ease-in-out !important;
}

center.flow.userpage-submissions b.active a, center.flow.userpage-favorites b.active a {
    background: none !important;
    border: none !important;
}

center.flow.userpage-submissions b.active, center.flow.userpage-favorites b.active {
    box-shadow: none !important;
    margin-left: 0 !important;
    margin-right: 0 !important;
    opacity: 0 !important;
    visibility: hidden !important;
    width: 0 !important;
}

/* -------------------------------- */

div#messagecenter-other fieldset {
    border: 0 !important;
    margin: 0 !important;
    padding: 0 !important;
}

div#messagecenter-other ul.message-stream li {
    margin-bottom: 2px !important;
    min-height: -webkit-calc(1.0em + 12px) !important;
    padding: 0 4px !important;
}

div#messagecenter-other ul.message-stream li, div#messagecenter-other ul.message-stream table, center.flow.messagecenter b {
    border-radius: 4px !important;
}

div#messagecenter-other ul.message-stream li, div#messagecenter-other ul.message-stream table, center.flow.messagecenter b, #pms-form .maintable.container tr td.alt1 {
    -webkit-transition: background 0.25s ease-in-out, color 0.25s ease-in-out, box-shadow 0.25s ease-in-out, border 0.25s ease-in-out, border-radius 0.25s ease-in-out !important;
}

div#messagecenter-other ul.message-stream li.checked, div#messagecenter-other ul.message-stream li.hover.checked, div#messagecenter-other ul.message-stream table.checked, div#messagecenter-other ul.message-stream table.hover.checked, center.flow.messagecenter b.checked, center.flow.messagecenter b.checked:hover {
    background: rgba(128, 128, 192, 0.25) !important;
    box-shadow: 1px 1px rgba(255, 255, 255, 0.125) inset, -1px -1px rgba(0, 0, 0, 0.25) inset !important;
}

div#messagecenter-other ul.message-stream li.hover, div#messagecenter-other ul.message-stream table:hover, center.flow.messagecenter b:hover {
    background: rgba(128, 128, 192, 0.125) !important;
    box-shadow: 1px 1px rgba(255, 255, 255, 0.125) inset, -1px -1px rgba(0, 0, 0, 0.25) inset !important;
}

div#messagecenter-other ul.message-stream li.section-controls {
    border-radius: 0 !important;
    margin: 0 !important;
    padding: 0 !important;
}

div#messagecenter-other ul.message-stream li.section-controls input.remove, div#messagecenter-other #messages-watches ul.message-stream li.section-controls input.remove, div#messagecenter-other div.global-controls input.remove, .remove-checked {
    text-shadow: 0 0 8px #66F !important;
}

div#messagecenter-other ul.message-stream table td {
    background: none !important;
}

div#submission table[width="1%"], img#submissionImg {
    max-width: 100% !important;
    width: auto !important;
}

fieldset {
    border: 1px solid #494F5F !important;
    border-radius: 2px !important;
}

fieldset ul, h1, h2, h3 {
    margin: 4px auto !important;
}

form[action="/submit/"][name="myform"][method="post"] + br, #browse td > br, #browse + br, #gallery td.cat > table + br, #gallery + br, #scraps td.cat > table + br, #scraps + br, #favorites td.cat > table + br, #favorites + br, #stats-submission td.cat > table + br, #stats-submission + br {
    display: none !important;
}

form[name="replyform"] {
    padding: 4px !important;
}

hr {
    border: none !important;
    box-shadow: 0 -1px rgba(64, 64, 64, 0.5) inset, 0 1px rgba(0, 0, 0, 0.5) inset !important;
    height: 2px !important;
    margin: 4px auto !important;
}

html {
    background: url('http://www.sofurryfiles.com/static/images/common/layout/background.jpg') fixed bottom left no-repeat !important;
    background-size: cover !important;
    padding: 64px 5% !important;
}

html, body, td, .alt1, .alt2 {
    background-color: #14171F !important;
}

input[type="submit"], input[type="button"], select, option, a.oldest, a.newest, a.more, button {
    background-color: #ECECEC !important;
    background-image: -webkit-gradient(
        linear,
     left bottom,
     left top,
     color-stop(1, rgb(254,254,254)),
     color-stop(0, rgb(236,236,236))
    ) !important;
}

input[type="submit"]:active, input[type="button"]:active, a.oldest:active, a.newest:active, a.more:active, button:active {
    background-color: #E0E0E0 !important;
    background-image: -webkit-gradient(
        linear,
     left bottom,
     left top,
     color-stop(0, rgb(254,254,254)),
     color-stop(1, rgb(236,236,236))
    ) !important;
}

span.reply-link a, .container-comment .reply-link {
    background: none !important;
    left: 2px !important;
    padding: 0px !important;
    position: relative !important;
    top: 0px !important;
}

table.content table.maintable table.maintable table.maintable td.alt1 {
    padding: 0 !important;
    padding-bottom: 2px !important;
}

table.content table.maintable table.maintable table.maintable td.alt1[rowspan="3"] {
    border-right: 1px solid #494F5F !important;
    padding: 4px !important;
}

table.content table.maintable table.maintable table.maintable td.lead {
    border: none !important;
    box-shadow: -3px 0px 0px #14171F inset, 0px 1px 0px #494F5F inset,  -4px -1px 0px #494F5F inset !important;
}

table.content.maintable > tbody > tr.innertable > td > table[width="98%"] {
    margin-bottom: 16px !important;
    width: -webkit-calc(100% - 32px) !important;
}

table.content.maintable > tbody > tr.innertable > td > table[width="98%"] > tbody > tr > td > table[width="95%"] {
    width: 100% !important;
}

table.content.maintable > tbody > tr.innertable > td > table[width="98%"] > tbody > tr > td > table[width="98%"].maintable > tbody > tr > td.cat > table[width="100%"] > tbody > tr > td[width="70%"] + td[width="30%"] + td:last-of-type {
    display: none !important;
}

table[width="98%"] {
    margin: 0 !important;
    width: 100% !important;
}

td.alt1 hr {
    margin-left: -3px !important;
    width: -webkit-calc(100% + 6px) !important;
}

td.alt2 {
    padding: 0px !important;
}

td.alt2 > table.maintable {
    background: #14171F !important;
}

td.alt2 > table.maintable > tbody > tr:first-of-type + tr + tr > td {
    margin: 0 !important;
    margin-left: 1px !important;
    position: relative !important;
    top: -20px !important;
}

td.alt2 > table.maintable > tbody > tr:first-of-type + tr > td {
    background: none !important;
    padding: 5px !important;
    padding-right: 7px !important;
    position: relative !important;
    top: -21px !important;
    z-index: -1 !important;
}

td.alt2 td.lead {
    border: none !important;
    box-shadow: -3px 4px #14171F inset,  1px 5px #33373F inset, -4px -1px #33373F inset !important;
    padding: 5px !important;
    padding-bottom: 5px !important;
    padding-top: 9px !important;
}

td.cat {
    padding: 0 !important;
    text-align: center !important;
}

td[width="20%"].ldot {
    border: none !important;
    border-left: 1px dotted #494F5F !important;
    padding: 2px !important;
}

td[width="80%"].ldot {
    border: none !important;
}

textarea, input, a.oldest, a.newest, a.more, select, button {
    background-color: #FDFDFD !important;
    border: 1px solid #A9B8C2 !important;
    border-radius: 2px !important;
    color: #000 !important;
    cursor: default !important;
    font-size: 8pt !important;
    vertical-align: baseline !important;
}

ul.dropdown *, .menu ul li ul li {
    background: none !important;
    opacity: 1 !important;
}

ul.dropdown hr {
    border: none !important;
    border-radius: 0px !important;
    box-shadow: 0 -1px 0 #3C3C3C inset, 0 1px 0 #131313 inset !important;
    margin: 2px 0px !important;
}

ul.dropdown li {
    line-height: 1.5em !important;
}

ul.dropdown li a, ul.dropdown li.noblock a {
    padding: 7px !important;
}

ul.dropdown li a.dir + ul {
    left: auto !important;
    opacity: 0 !important;
    visibility: hidden !important;
    width: auto !important;
}

ul.dropdown li a.dir + ul li a[href="#"] + ul {
    left: -192px !important;
    margin-top: -28px !important;
    opacity: 0 !important;
    position: absolute !important;
    top: auto !important;
    visibility: hidden !important;
}

ul.dropdown li a.dir + ul li a[href="#"] + ul, ul.dropdown li a.dir + ul li a[href="#"] + ul li {
    text-align: right !important;
    width: 184px !important;
}

ul.dropdown li a.dir + ul li, ul.dropdown li a.dir + ul li hr {
    width: 200px !important;
}

ul.dropdown li a.dir + ul, ul.dropdown li a.dir + ul li a[href="#"] + ul {
    -webkit-transition: all 0.25s ease-in-out !important;
}

ul.dropdown li, ul.dropdown li a, div.menu li {
    -webkit-transition: background 0.125s ease-in-out, box-shadow 0.125s ease-in-out !important;
}

ul.dropdown li.noblock {
    padding: 0 !important;
}

ul.dropdown li:active {
    background: #1A1A1A !important;
    box-shadow: -1px 0 0 rgba(255, 255, 255, 0.125) inset, 1px 0 0 rgba(0, 0, 0, 0.125) inset !important;
}

ul.dropdown li:hover a.dir + ul, ul.dropdown li a.dir + ul li:hover a[href="#"] + ul {
    opacity: 1 !important;
    visibility: visible !important;
}

ul.dropdown ul a {
    padding: 4px 8px !important;
}

ul.dropdown ul li, .menu ul li ul li {
    margin: 1px 0px !important;
}

ul.dropdown ul li:active, .menu ul li ul li:active {
    background: #1A1A1A !important;
    box-shadow: 0 -1px 0 rgba(255, 255, 255, 0.125) inset, 0 1px 0 rgba(0, 0, 0, 0.125) inset !important;
}

ul.dropdown ul, .menu ul li ul {
    background-image: -webkit-gradient(
        linear,
        left top,
        left bottom,
        color-stop(0, rgb(40,40,40)),
        color-stop(1, rgb(30,30,30))
    ) !important;
    border: 2px solid #545454 !important;
    border-radius: 2px !important;
    padding: 1px 0px !important;
}

ul.dropdown.dropdown-left > li.noblock:nth-child(3) {
    font-size: 0 !important;
    padding: 0 !important;
    visibility: hidden !important;
}

ul.dropdown.dropdown-left > li.noblock:nth-child(3) > a {
    background-position: 6px 8px !important;
    background-repeat: no-repeat !important;
    font-size: 12.5px !important;
    line-height: 1.5em !important;
    padding-bottom: 7px !important;
    padding-left: 24px !important;
    padding-right: 7px !important;
    padding-top: 7px !important;
    visibility: visible !important;
}

ul.dropdown.dropdown-left > li.noblock:nth-child(3) > a:empty:last-of-type {
    display: none !important;
}

ul.dropdown.dropdown-left > li.noblock:nth-child(3) > a:first-of-type {
    display: none !important;
}

ul.dropdown.dropdown-left > li.noblock:nth-child(3) > a:nth-child(2) {
    background-position: 6px 7px !important;
    padding-left: 24px !important;
}

ul.dropdown.dropdown-left > li.noblock:nth-child(3) > a:only-of-type {
    display: block !important;
