css:
@-moz-document domain(cpp.ch) {
  body{
    background-color: #F0F;
    background-image: url(http://twilight.ponychan.net/chan/arch/src/130045033408.gif);
  }
  img {
    max-width: 100%;
  }
  #liste_titre{
    background-color: #F0F;
  }
  .box tr:nth-child(odd) {background: #F0F}
  #box5_corps tr:nth-child(even),
  #box6_corps tr:nth-child(even),
  #box7_corps tr:nth-child(even)
  {background:#F0F;}
  #box5_corps a:nth-child(even),
  #box6_corps a:nth-child(even),
  #box7_corps a:nth-child(even)
  {background:rgb(235, 0, 235);;}
  #box5_corps a:nth-child(odd),
  #box6_corps a:nth-child(odd),
  #box7_corps a:nth-child(odd)
  {background:#F0F;}

tr.pair		{
  background-color:#F0F;
}
#liste_corps{
	background-color:#F0F;
}
#menu_corps a:nth-child(even) div {background:rgb(235, 0, 235);}
#menu_corps a:nth-child(odd) div {background: #F0F;}
h1{
  border-bottom: 1px dashed #D4318C;
}
#menu_titre {
  background-color: #D4318C;
  border: 1px solid #D4318C;
}
#menu_corps a:nth-child(2n) div {
  background: none repeat scroll 0% 0% #D4318C;
}
#box5_corps a:nth-child(2n), #box6_corps a:nth-child(2n), #box7_corps a:nth-child(2n) {
  background: none repeat scroll 0% 0% #D4318C;
}
#menu_corps{
	border: 1px solid #D4318C;	
}
#menu_titre{
	background-color:#D4318C;
	border: 1px solid #D4318C;
}
#liste_titre{
	background-color:#D4318C;
	border :1px solid #D4318C;
  background-image: none;
}
#liste_corps{
	border :1px solid #D4318C;
}
.box tr:nth-child(2n) {
  background: none repeat scroll 0% 0% #D4318C;
}
body {
  color: #000;
}
#bt_centre a {
    font-weight: bold;
    color: #D4318C;
}
#box2_corps tbody td span {
  color: #000;
}
#box5_corps a {
    color: #000;
}
#conteneurglobal #bt_titre img {
  display: none;
}
#conteneurglobal #bt_titre {
  background: url(http://www.kimsites.net/dreamvalley/8logo-tropical.gif);
  background-repeat: no-repeat;
  height: 150px;
}

  .white {
    background-color: #D4318C;
  }
  #bt_pied {
    border-top: 1px double #D4318C;
}
  #box7 div:first-child, #box5 div:first-child, #box6 div:first-child {
    background-color: #D4318C;
    border-color: #D4318C;
}
  #box7_corps, #box5_corps, #box6_corps  {
    border: 1px solid #D4318C;
}
  .box a {
    color: #000;
  }
}
