css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("analog.com") {
  .search { margin: 0px !important; }
  .container { max-width: 100% !important; }
  .product-title-table tr td.td-titledesc  { max-width: 50% !important; }
  .list-files { top: 0px !important; }
  .margin-top-32 { margin-top: 8px !important; }
  .title-margin-32 { margin-top: 8px !important; margin-bottom: 8px !important; }
  .product-row { margin-bottom: 8px !important; }
  .block { padding: 4px !important; }
  .bar { padding: 3px 0px !important; }
  .footer { padding-top: 0px !important; }
  .sub-category h5, .sub-category .header5  { padding: 8px 0px !important; }
  
  body {
    line-height: 1 !important;
  }
  
  
  .products-packaging-grid table tr td, .products-packaging-grid table tr th { padding: 3px 2px !important; }
  
  /* Cut the crap */
  .adi-slider, .didyouknow, .visible-desktop { display: none !important; }
  #carousel-main-home { diplay: none !important; }
  .bx-pager, .bx-default-pager { display: none !important; }
  .slides, .slides-layout-3 { display: none !important; }
  #trackVisitor { display: none !important; }
  .sublinks { width: 400px !important; float: left !important;}
  
  /* The retarded 960px site width means they couldn't fit their big header titles and items */
  /* So let's move that for the new world, where everyone should have a screen wider than 1000px.. */
  li.active > div.sublinks { left: 250px !important; }
  li.active > div.sublinks div.prnode { left: 400px !important; }
  
  .menu-products .level ul li > a  { padding: 2px 4px !important; }
  .menu-products .level .header p { margin: 0px !important; }
  .menu-products .level .header { padding: 0px 4px 4px !important; }
  .menu-products .level .header h2, .menu-products .level .header .header2 { width: auto !important; }
  div.section-bar .section-heading
  {
    background: none !important;
    color: black !important;
    width: 400px !important;
    float: left !important;
  }
  .section-heading h3, .section-heading h4, .section-heading span { margin: 0px !important; }
  
  /* Product page stuff */
  .col-md-offset-2 { margin-left: 170px !important; }
  .product-feature, .product-details { margin-top: 0px !important; margin-bottom: 0px !important; }
  .link-list { padding-bottom: 4px !important; }
  /* Read more? What do they think - they are a news letter? We always want the full text outline! */
  .product-details .summary { display: none !important; }
  .product-details .details { display: block !important; }
  .product-details .read-less { display: none !important; }
  /* customer reviews? are probably heavily moderated.. */
  .td-product-rating { display: none !important; }
  /* recommended for new designs */
  .product-title-table tr td:nth-child(2) { padding-left: 0px !important; }
  
  /* remove reviews and the big-ass discuss community baloons */
  .ez-discussions,
  div[name="adi_productdetails_mostrecent_reviews_section"],
  div[name="adi_productdetail_reviews_section"] { display: none !important; }
  
  /* unsearchable product tables */
  #jfg_header_right_inner, #jfg_header_right, #jfg_body_right { width: auto !important; }
}
