css:
@namespace url(http://www.w3.org/1999/xhtml);
/**********************改下面括号里的域名***********************/
@-moz-document domain("yande-re"),domain("konachan-com"),domain("konachan-net") {
/*标签*/
.tag-type-circle:after{content:"社团";color:#aa4;}
.tag-type-copyright:after{content:"版权";color:#aa4;}
.tag-type-artist:after{content:"画师";color:#aa4;}
.tag-type-character:after{content:"角色";color:#aa4;}
.tag-type-faults:after{content:"错误";color:#aa4;}
.tag-type-style:after{content:"类型";color:#aa4;}
/*下载大图*/
#highres{color:red;border:1px dashed white;padding:4px 0 4px 4px;margin:4px 0 4px -4px;display:inline-block;width:220px;}
#highres:hover{color:yellow!important;border-color:red;}
/*************大图列表******************/
.sidebar{margin:0!important;max-width:230px!important;}
.action-post-index .content{width:-moz-calc(100% - 230px)!important;}
#content{width : 100%!important}
div.content div[align=center]{display:none!important;}
span.directlink-info{display:none!important;}
span.plid{display:none!important;}
span.directlink-res{display : block!important}
.directlink.smallimg{background : black!important}
#post-list-posts li{width : 310px!important; visibility : visible !important; display : inline-block !important;}
div.inner{width : 310px!important;height : 300px!important}
img.preview{width : auto!important;max-width: 300px!important;height : auto!important;max-height: 300px!important;border : none!important;}

#lsidebar{
  display: none!important;
}
}
