css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);
@-moz-document url-prefix('chrome://') 
{
  #TabsToolbar 
  {
    height: 28px !important;
    box-shadow: ThreeDShadow 0 -1px inset !important;
    background-image: none !important;
    -moz-appearance: none !important;
  }  
  
  .arrowscrollbox-scrollbox 
  {
    padding: 0 !important;
  }

  .tabbrowser-tabs 
  {
    min-height: 26px !important;
    padding-left: 3px !important;
  }

  .tab-background 
  {
    margin: 0 !important;
    background: transparent !important;
  }

  .tab-background-start, .tab-background-end 
  {
    display: none !important;
  }

  .tab-background-middle 
  {
    margin: -4px -2px !important;
    background: transparent !important;
  }

  .tabbrowser-tab:after, .tabbrowser-tab:before 
  {
    display: none !important;
  }
  
  .tabbrowser-tab 
  {
    min-height: 25px !important;
    margin-top: 2px !important;
    padding: 0 4px 1px 3px !important;
    background-image: none !important;
    -moz-appearance: tab !important;
    color: ButtonText !important;
  /*  margin-right: -2px !important;*/
  }

  .tabbrowser-tab[selected=true] 
  {
    margin-top: 0 !important;
    min-height: 28px !important;
    margin-bottom: 0px !important;
    padding: 2px 4px 3px 3px !important;
    position: relative !important;
  }

  .tabbrowser-tab[pinned] + .tabbrowser-tab:not([pinned]), .tabbrowser-tab[pinned] 
  {
    padding: 0 5px 0 3px !important;
    margin-right: -2px !important;
    min-height: 25px !important;
  }

  .tabbrowser-tab[selected=true][pinned] 
  {
  min-height: 22px !important;
  padding-right: 5px !important;
  padding-left: 3px !important;
  min-height: 28px !important;
  }

  .tabbrowser-tab[pinned][titlechanged]:not([selected="true"]) 
  {
    box-shadow: 0 0 0 1em #BCDAEE inset !important;
  }
  .tabbrowser-tab[pinned] .tab-icon-image 
  {
    margin-top: -1px !important;
  }

  #tabbrowser-tabs[overflow="true"] .tabbrowser-tab[pinned] 
  {
    position: fixed !important;
    padding-right: 5px !important;
    padding-left: 3px !important;
    padding-top: 3px !important;
    min-height: 20px !important;
  }

  #tabbrowser-tabs[overflow="true"] .tabbrowser-tab[pinned][selected=true] 
  {
    padding-top: 5px !important;
    min-height: 21px !important;
  }


  .tabbrowser-tab[selected=true][pinned] .tab-icon-image 
  {
    margin-top: -2px !important;
  }
  .tabbrowser-tab[selected=true] .tab-icon-image 
  {
    margin-top: -1px !important;
  }
  .tabbrowser-tab[selected=true] .tab-text 
  {
    margin-top: -1px !important;
  }
.tabbrowser-tab[selected=true] .tab-close-button 
  {
    margin-bottom: 1px !important;
  }
.tab-throbber[progress][selected=true], .tab-throbber[busy][selected=true]
  {
    margin-top: -1px !important;
  }
.tab-throbber[progress][pinned][selected=true], .tab-throbber[busy][pinned][selected=true]
  {
    margin-top: -1px !important;
  }
.tabbrowser-tab[pinned] + .tabbrowser-tab:not([pinned]) .tab-icon-image 
  {
    margin-top: -2px !important;
  }


.tabbrowser-tab:not([selected=true])
  {
    margin-bottom: 1px !important;
  }

  .tabbrowser-tab[pinned] + .tabbrowser-tab:not([pinned]) .tab-close-button 
  {
    margin-top: -2px !important;
  }

  #tabbrowser-tabs[overflow="true"] .tabbrowser-tab[pinned] 
  {
    position: fixed !important;
    padding-right: 5px !important;
    padding-left: 3px !important;
    padding-top: 3px !important;
    min-height: 20px !important;
  }

  #tabbrowser-tabs[overflow="true"] .tabbrowser-tab[pinned][selected=true] 
  {
    padding-top: 5px !important;
    min-height: 21px !important;
  }

  .tabs-newtab-button 
  {
    -moz-appearance: toolbarbutton !important;
    margin-left: 2px !important;
    margin-top: 0px !important;
    width: 32px !important;
  }

  .toolbar-primary.chromeclass-toolbar, #PersonalToolbar, #customToolbars, #navigator-toolbox .chromeclass-toolbar 
  {
    -moz-appearance: toolbar !important;
  }

  .toolbar-primary.chromeclass-toolbar:-moz-lwtheme, #PersonalToolbar:-moz-lwtheme, #customToolbars:-moz-lwtheme, #navigator-toolbox .chromeclass-toolbar:-moz-lwtheme 
  {
  -moz-appearance: none !important;
  }

  .tabbrowser-tab:-moz-lwtheme:not([selected="true"])
  {
    opacity:.85 !important;
  }

  #toolbar-menubar
  {
    padding: 0 !important;
  }
}
