css:
@-moz-document regexp(".*myanimelist\\.net/(?!animelist|mangalist).*") {
*{/*$*/}
/*-----AD AND BLOAT KILLER-----*/
    
    .GoogleActiveViewClass, #roadblock, #_CI_widget_1, #homeAds, #IL_INSEARCH, #_bapw-link, #ingageunit, .adHome300, *[id*=div-gpt-ad] {
        display: none!important;}
    
/*-----GENERAL STYLE OVERRIDES-----*/
    
    * {
        text-decoration: none !important;
        border-radius: 0!important;
        box-shadow: none!important;
        text-shadow: none!important;
        font-family: "Open Sans", "M+ 2p", sans-serif!important;}
    
/*-----TEXTS-----*/
    
    /* normal text */
    *, .dark_text, .dark_text:hover, .dark_text:visited, #nav li a span.count {
        color: #ccc!important;}
    
    /* sub text */
    .lightLink, .lightLink:link, .lightLink:visited, .lightLink:active {
        color: #999!important;}
    
    /* headers and white text */
    h1, h2, .normal_header, .forum_locheader,
    #horiznav_nav ul li .horiznav_active, #horiznav_nav ul li a,
    #horiznav_nav ul li a:hover {
        color: #fff!important;}
    
/*-----LINKS-----*/
    
    .forum_postusername a,
    .lightLink small, .lightLink:link small,
    .lightLink:visited small, .lightLink:active small {
        color: #ccc!important;}
    
    #nav li a, #nav li.hover a, .forum_postusername a:hover,
    .lightLink:hover small, .lightLink:link:hover small,
    .lightLink:visited:hover small, .lightLink:active:hover small,
    .button_form, .button_form:hover, .button_add, .button_add:hover,
    .button_edit, .button_edit:hover, #profileRows a:hover {
        color: #fff!important;}
    
    a, a:visited, .goodresult a:link, #nav li a:hover, #profileRows a,
    .forum_boardrow1 a strong, a strong, #watchText {
        color: #0080ff!important;}
    
    a:hover, .goodresult a:link:hover, .forum_boardrow1 a strong:hover,
    a strong:hover, #watchText:hover {
        color: #80c0ff!important;}
    
/*-----IMAGES-----*/
    
    /* background images */
    /*
       Plain Blue: http://dl.dropboxusercontent.com/s/e4i2gq7p29dayd6/bg_blank.png
       Angel Beats: http://dl.dropboxusercontent.com/s/ayo339ker2zejd6/bg-ab-yurippetenshi.png
       Amagami SS: http://dl.dropboxusercontent.com/s/6oh0tfcptisylao/bg-amagami.png
       Chuunibyou: http://dl.dropboxusercontent.com/s/psefr6vrtxi4t0x/bg-chuu.png
       Date A Live (Kurumi): http://dl.dropboxusercontent.com/s/4n3pzfrp0x6eu76/bg-dal-kurumi.png
       Date A Live (Kurumi and Tohka): http://dl.dropboxusercontent.com/s/9mwf4c9mv1brmvy/bg.png
       Date A Live (Origami): http://dl.dropboxusercontent.com/s/wwkvmq6e9sdvb1b/bg-dal-origami.png
       Gears: http://dl.dropboxusercontent.com/s/hfzq201e1zteavo/bg-gears.png
       Inori Aizawa: http://dl.dropboxusercontent.com/s/03k6kx7jpbhyxeh/bg-inori.png
       Hyperdimension Neptunia: http://dl.dropboxusercontent.com/s/qe251bapic29oaf/bg-neptunia.png
       SAO: http://dl.dropboxusercontent.com/s/tn7xdx294vhtvpt/bg-sao-asunakirito.png
       Shakugan no Shana: http://dl.dropboxusercontent.com/s/zpmfvnp2zl3w2yl/bg-shakugan-shana.png
       To Love Ru: http://dl.dropboxusercontent.com/s/5geo41b34qf0zd1/bg-toloveru-kotegawayami.png
    */
    body {
        background: #222 url('/*[[bg-image]]*/') no-repeat scroll center top !important;}
    
    #fancybox-close {
        background-image: url('http://dl.dropboxusercontent.com/s/sj8fxe136smjtps/fancy_close.png')!important;}
    
    img[src*='ignorethread.gif'] {
        background-image: url('http://dl.dropboxusercontent.com/s/qr79ccddrgpiegi/ignorethread.png')!important;
        box-sizing: border-box!important;
        width: 12px!important;
        height: 12px!important;
        padding-left: 12px!important;}
    
    img[src*='watch_n.gif'] {
        background-image: url('http://dl.dropboxusercontent.com/s/wlqs7h4lrofm90h/watch_n.png')!important;
        box-sizing: border-box!important;
        width: 15px!important;
        height: 15px!important;
        padding-left: 15px!important;}
    
    img[src*='watch_y.gif'] {
        background-image: url('http://dl.dropboxusercontent.com/s/uvurdtokbhfdexs/watch_y.png')!important;
        box-sizing: border-box!important;
        width: 15px!important;
        height: 15px!important;
        padding-left: 15px!important;}
    
/*-----BACKGROUNDS-----*/
    
    /* none */
    div.picSurround img, .forum_boardrowspacer {
        background: none!important;}
    
    /* #111 override */
/* BOOKMARK: modify body or header backgrounds */
    #header, #headerSmall, #rightcontentunder, #horiznav_nav ul li,
    .ra1-pw-popup {
        background: none!important;}
    
    /* #222 override */
    #content, #contentHome,
    .bgColor1, .bgColor2, .row_not_highlighted, .inputtext,
    .forum_boardrow1, .forum_boardrow2, .row_highlighted, #nav li.hover a, .textarea,
    #fancybox-outer, .hoverinfo, .ra1-pw-popup-i, .ra1-pw-popup-footer,
    #panel_left > .container > strong + .borderClass.bgColor1 + .spaceit > a {
        background: #222!important;}
    
    /* forced #222 override */
    .ra1-pw-popup-title {
        background: #222 !important;}
    
    /* #333 override */
    h1, .inboxsent_header, .goodresult, #nav li a:hover, #dialog, #horiznav_nav ul,
    #panel_left > .container > strong + .borderClass.bgColor1 > a, .bgbdrContainer,
    #panel_left > .container > strong + .borderClass.bgColor1 + .spaceit > a:hover,
    #forum_options_container ul li {
        background: #333!important;}
    
    /* #444 override */
    #horiznav_nav ul li a, .quotetext, .codetext, .spoiler,
    #panel_left > .container > strong + .borderClass.bgColor1 > a:hover,
    #forum_options_container ul li:hover {
        background: #444!important;}
    
    /* #555 override */
    #horiznav_nav ul li a:hover, .spoiler input {
        background: #555!important;}
    
    /* #0080ff override */
    #menu, #menu_right, #menu_left, #nav li, #nav li a, .button_edit,
    #horiznav_nav ul li .horiznav_active, .button_form, .button_add,
    #profileRows a:hover, .forum_category, .inputButton {
        background: #0080ff!important;}
    
    /* #80c0ff override */
    .button_edit:hover, #horiznav_nav ul li .horiznav_active:hover, .button_form:hover,
    .button_add:hover, .inputButton:hover {
        background: #80c0ff!important;}
    
    /* forced vivid red override */
    #profileRows a.redButton:hover {
        background: rgba(255,0,0,0.75) !important;}
    
    /* forced translucent red override */
    [id^=fsgComments] div[style='background-color: #f7e0e0!important;'] {
        background: rgba(255,0,0,0.25) !important;}
    
    
/*-----BORDERS-----*/
    
    /* none */
    #dialog, .button_edit, #panel_left > .container > strong + .borderClass.bgColor1,
    #horiznav_nav {
        border: none!important;}
    
    /* 1px solid all sides */
    #fancybox-outer, #nav li ul, .hoverinfo, .button_edit, .button_form, .bgbdrContainer,
    .button_add, #panel_left > .container > strong + .borderClass.bgColor1 > a,
    #horiznav_nav ul, #profileRows a, #profileRows a:hover, .spoiler, .spoiler input,
    .forum_category {
        border: 1px solid!important;}
    
    /* 1px solid top only */
    
    /* 1px solid left only */
    #horiznav_nav ul li a,
    #panel_left > .container > strong + .borderClass.bgColor1 + .spaceit > a {
        border-left: 1px solid!important;}
    
    /* 1px solid right only */
    #horiznav_nav ul li a,
    #panel_left > .container > strong + .borderClass.bgColor1 + .spaceit > a {
        border-right: 1px solid!important;}
    
    /* 1px solid bottom only */
    #panel_left > .container > strong + .borderClass.bgColor1 + .spaceit > a {
        border-bottom: 1px solid!important;}
    
/*-----BORDER COLORS-----*/
    
    /* transparent */
    #panel_left > .container > strong + .borderClass.bgColor1 + .spaceit > a,
    #profileRows a {
        border-color: transparent!important;}
    
    /* forced transparent */
    #profileRows a.redButton {
        border-color: transparent !important;}
    
    /* 10% white */
    h1, h2, #contentHome, #panel_left .container, .borderClass, .normal_header,
    .forum_boardrow1, .forum_boardrow2, #content, .borderLight, .borderDark,
    .profile_leftcell, .row_highlighted, .inboxsent_header, .bgbdrContainer,
    .row_not_highlighted, .bgbdrContainer, .goodresult, #dialog td, .textarea,
    .inputtext, #nav li ul, .button_edit, div.picSurround img, .button_add,
    #horiznav_nav ul li a, .button_form, #profileRows a:hover, .ra1-pw-popup-i,
    #panel_left > .container > strong + .borderClass.bgColor1 > a,
    #panel_left > .container > strong + .borderClass.bgColor1 + .spaceit > a:hover,
    .quotetext, .codetext, .spoiler, .spoiler input, .forum_category,
    .sig, #forum_options_container ul li, .inputButton, #horiznav_nav ul {
        border-color: rgba(255,255,255,0.1)!important;}
    
    /* forced 10% white */
    #profileRows a.redButton:hover {
        border-color: rgba(255,255,255,0.1) !important;}
    
    /* 25% white */
    #fancybox-outer, .hoverinfo {
        border-color: rgba(255,255,255,0.25)!important;}
    
/*-----BOX SHADOWS-----*/
    #menu {
        box-shadow: 0 0 0 1px rgba(0,0,0,0.5), 0 0 0 3px rgba(255,255,255,0.25)!important;}
    
/*-----TEXT SHADOWS-----*/
    
/*-----SPECIFIC OVERRIDES-----*/
    /* make entire page semi transparent */
    /*
    #contentWrapper {
        opacity: 0.9!important;}*/
    
    /* remove body margin on non-panel pages */
    body {
        margin: 0!important;
    }
    
    /* fix menu to top */
    #menu {
        position: fixed!important;
        top: 0!important;
        left: 0!important;
        z-index: 10000!important;
        width: 100%!important;}
    
    /* custom menu buttons */
    #nav li a {
        display: block!important;
        margin: 0!important;
        padding: 0!important;
        height: 34px!important;
        line-height: 34px!important;
        text-align: center!important;}
    
    #nav li ul li a {
        margin: 0!important;
        padding: 0!important;
        height: 24px!important;
        line-height: 24px!important;
        text-align: left!important;}
    
    /* custom menu search */
    #menu_right #searchBar {
        padding: 0!important;
        height: 100%!important;}
    
    #topSearchText {
        position: absolute!important;
        top: 0!important;
        right: 170px!important;
        display: inline-block!important;
        height: 24px!important;
        border: none!important;
        background: none!important;
        padding: 5px!important;
        color: #fff !important;
        font-size: 15px!important;
        text-align: center!important;
        text-transform: lowercase!important;
        width: 100px!important;
        -webkit-transition-property: width, background!important;
        -moz-transition-property: width, background!important;
        -o-transition-property: width, background!important;
        transition-property: width, background!important;
        -webkit-transition-duration: 0.5s!important;
        -moz-transition-duration: 0.5s!important;
        -o-transition-duration: 0.5s!important;
        transition-duration: 0.5s!important;
        -webkit-transition-timing-function: cubic-bezier(0.370, 0, 0.010, 1)!important;
        -webkit-transition-timing-function: cubic-bezier(0.370, -0.170, 0.010, 1.110)!important;
        -moz-transition-timing-function: cubic-bezier(0.370, -0.170, 0.010, 1.110)!important;
        -o-transition-timing-function: cubic-bezier(0.370, -0.170, 0.010, 1.110)!important;
        transition-timing-function: cubic-bezier(0.370, -0.170, 0.010, 1.110)!important;}
    
    #topSearchText:hover {
        background: rgba(0,0,0,0.1)!important;}
    
    #topSearchText:focus {
        background: rgba(0,0,0,0.5)!important;
        border: none!important;
        width: 250px!important;}
    
    #topSearchValue {
        position: absolute!important;
        top: 0!important;
        right: 50px!important;
        display: inline-block!important;
        height: 100%!important;
        width: 120px!important;
        background: none!important;
        border: none!important;
        color: #fff !important;
        font-size: 15px!important;
        text-align: center!important;
        text-transform: lowercase!important;
        -webkit-appearance: none!important;
        -webkit-transition: 0.5s ease!important;
        -moz-transition: 0.5s ease!important;
        -o-transition: 0.5s ease!important;
        transition: 0.5s ease!important;}
    
    #topSearchValue:hover {
        background: rgba(0,0,0,0.1)!important;}
    
    #topSearchValue:focus {
        background: rgba(0,0,0,0.5)!important;}
    
     #topSearchValue option {
         background: #222!important;}
    
    #menu_right #searchBar input[type='image'] {
        position: fixed!important;
        display: inline-block!important;
        top: 0!important;
        right: 0!important;
        width: 50px!important;
        height: 34px!important;
        z-index: 1!important;
        box-sizing: border-box!important;
        padding-left: 50px!important;
        -webkit-transition: 0.5s ease!important;
        -moz-transition: 0.5s ease!important;
        -o-transition: 0.5s ease!important;
        transition: 0.5s ease!important;}
    
    #menu_right #searchBar input[type='image']:hover {
        background: rgba(255,255,255,0.5)!important;}
    
    #menu_right #searchBar:after {
        content: "GO"!important;
        position: absolute!important;
        top: 0!important;
        right: 0!important;
        display: block!important;
        width: 50px!important;
        height: 34px!important;
        line-height: 34px!important;
        text-align: center!important;
        font-size: 15px!important;
        color: #fff!important;}
    
    /* fix header sizes */
    #header, #headerSmall {
        height: 155px!important;}
    
    #header a, #headerSmall a {
        display: block!important;
        width: 100%!important;
        height: 100%!important;}
    
    /* custom buttons */
    #profileRows a {
        text-align: center!important;}
    
    /* custom share buttons */
    .pw-widget {
        text-align: center!important;}
    
    .ra1-pw-popup-i {
        border-radius: 0!important;}
    
    /* custom horizontal navs (tabs)*/
    #horiznav_nav {
        height: 35px!important;
        padding: 0!important;
        margin: 5px !important;}
    
    #horiznav_nav ul {
        display: block!important;
        text-align: center!important;}
    
    #horiznav_nav ul li a {
        display: inline-block!important;
        height: 26px!important;
        line-height: 26px!important;
        padding-left: 8px!important;
        padding-right: 8px!important;}
    
    /* custom welcome message: might break some elements */
    #panel_left .container {
        text-align: center!important;}
    
    #panel_left .container * {
        text-align: left!important;}
    
    /* custom links below welcome message */
    #panel_left > .container > strong + .borderClass.bgColor1 {
        text-align: center!important;
        padding: 0!important;
        margin-bottom: 0 !important;}
    
    #panel_left > .container > strong + .borderClass.bgColor1 > a {
        text-align: center!important;
        display: block!important;
        padding: 5px!important;
        font-size: 18px !important;}
    
    #panel_left > .container > strong + .borderClass.bgColor1 + .spaceit {
        text-align: center!important;
        font-size: 0!important;
        margin-top: 0!important;
        padding: 0 !important;}
    
    #panel_left > .container > strong + .borderClass.bgColor1 + .spaceit > a {
        text-align: center!important;
        font-size: 12px!important;
        display: inline-block!important;
        width: calc(33.33% - 2px)!important;
        height: 25px!important;
        line-height: 25px!important;}
    
    #panel_left > .container > strong + .borderClass.bgColor1 + .spaceit > a:first-of-type {
        float: left!important;}
    
    #panel_left > .container > strong + .borderClass.bgColor1 + .spaceit > a:last-of-type {
        float: right!important;}
    
    /* forum elements */
    
    /* forum header */
    #content a[name='top'] + div {
        font-size: 0!important;
        background: #333!important;
        border: 1px solid rgba(255,255,255,0.1)!important;
        text-align: center!important;}
    
    #content a[name='top'] + div a {
        font-size: 11px!important;
        display: inline-block!important;
        background: #444!important;
        border-left: 1px solid rgba(255,255,255,0.1)!important;
        border-right: 1px solid rgba(255,255,255,0.1)!important;
        padding: 0 50px!important;
        margin: 0 25px!important;
        height: 30px!important;
        line-height: 30px!important;}
    
    #content a[name='top'] + div a:hover {
        background: #555!important;}
    
    #content a[name='top'] + div + div {
        text-align: center!important;}
    
    #content a[name='top'] + div + div + p a[href='index.php'] {
        display: block!important;
        text-align: center!important;
        background: #333!important;
        border: 1px solid rgba(255,255,255,0.1)!important;
        height: 30px!important;
        line-height: 30px!important;}
    
    #content a[name='top'] + div + div + p a[href='index.php']:hover {
        background: #444!important;}
    
    /* forum user side panels */
    div[id^=messageuser] a {
        display: block!important;
        text-align: center!important;
        font-size: 15px!important;}
    
    .forum_boardrow2 {
        text-align: center!important;}
    
    .forum_boardrow2 img[src^='http://cdn.myanimelist.net/images/useravatars/'] {
        border: 1px solid rgba(255,255,255,0.5)!important;
        box-shadow: 0 0 0 3px rgba(255,255,255,0.1)!important;
    }
    
    /* anime listing searchbox */
    #content > #horiznav_nav + div[style='padding: 10px!important;'] > form[method='GET'] {
        position: absolute!important;
        top: 90px!important;
        right: 20px!important;}
    
    #content > #horiznav_nav + div[style='padding: 10px!important;'] > form[method='GET'] + script + div.borderClass + table {
        margin-top: 40px!important;}
    
    /* fancybox recentering */
    #fancybox-wrap {
        position: fixed!important;
        margin: auto!important;
        width: 100% !important;
        height: 100% !important;
        top: 0 !important;
        left: 0 !important;
        background: rgba(0,0,0,0.5)!important;}
    
    #fancybox-outer {
        position: absolute!important;
        display: block!important;
        top: 50%!important;
        left: 50%!important;
        transform: translateX(-50%) translateY(-50%)!important;
        width: 500px !important;
        height: 300px !important;}
    
    #fancybox-inner {
        position: absolute!important;
        display: block!important;
        top: 0 !important;
        left: 0 !important;
        width: 480px !important;
        height: 280px !important;
        padding: 10px!important;}
    
    #fancybox-inner div {
        text-align: center !important;
        margin-top: 15px!important;}
}
