css:
@-moz-document domain("codeskulptor.org") {

html, td/*, tbody, thead, th, tr, blockquote, li, ul, h1, h2, h3, h4, h5, h6, font, strong, p, footer, fieldset*/ {
	color: white !important;
	background: #171813 !important;
}



select, label/*,code, div*/,textarea, dd, option {
	color: black !important; 
}



/*------*/

body {
	margin-top: 2em !important;
}
a:link, a:visited {
	color: #666859 !important;
}

/*------
a:visited {
	color: #006675 !important;
} */


#console {
	background-color: #272822 !important;
    width: 589px;
 
}

#splitbar {
	height: 400px;
	width: 9px;
	background: #171813;
	border-left: 1px solid #000000;
	border-right: 1px solid #000000;
	overflow: hidden;
}

img[src*="jqui/images/ui-icons_00246a_256x240.png"]{ content: url("http://i.imgur.com/MegWbiT.png"); }

/*------*/


.CodeMirror {
	background: #272822 !important;
    color: white !important;
}

.CodeMirror-selected {
	background-color: #373830 !important;
    opacity: 0.9 !important;
}

.CodeMirror-linenumber {
	color: #8f978d !important;
	padding: 0 3px 0 0px!important;
}

.CodeMirror-gutters {
	border-right: 1px solid #43453b;
	background-color: #272822 !important;
	white-space: nowrap;
}

.CodeMirror div.CodeMirror-cursor {
border-left: 1px solid white !important;
  	opacity: 0.7 !important;
}

.CodeMirror span {
  	color: rgb(255, 255, 255) !important;
}

.CodeMirror-foldmarker {
color: blue;
text-shadow: #FFF 1px 1px 2px, #000 -1px -1px 2px, #FFF 1px -1px 2px, #FFF -1px 1px 2px;
font-family: arial;
line-height: .3;
cursor: pointer;
}
  
.activeline {
        background: #8C3434 !important;
/* background: #f62817 !important; */
}

/*------*/

.topbar {
	color: white;
	font-size: 20px;
	line-height: 26px;
	padding: 10px;
	background: #272822;
	background-image: none;
	background-image: none;
	background-image: none;
	background-image: none;
	background-image: none;
	background-image: none;
}

/*-------*/

/*-----*/
.cm-s-default .cm-keyword {
	color: #FF4E84 !important;
}

.cm-s-default .cm-def {
	color: #a6e22e !important;
}

.cm-s-default .cm-string {
	color: #FFCC66 !important;
}

.cm-s-default .cm-comment {
	color: #7e877b !important;
}

.cm-s-default .cm-number {
	color: #CC99FF !important;
}

.cm-s-default .cm-builtin {
	color: #0092b3 !important;
}

.cm-s-default .cm-variable-2 {
color: #6494ed !important;
}

pre, xmp, plaintext, listing {
	background: #1e1f1a ;
  	color : white !important;
	-moz-border-radius: 0px;
	-webkit-border-radius: 0px;
	border-right: 1px solid #43453b;
    border-left: 1px solid #43453b;
    border-top: 1px solid #43453b;
    border-bottom: 1px solid #43453b;
	border-radius: 2px;
	padding: 1em;
    margin: 10px;
    overflow: auto;
;
}    

/*ScrollBar*/

html {
    overflow: auto;
}

::-webkit-scrollbar {
    width: 12px;
    height: 12px;
}
 
::-webkit-scrollbar-track {
    -webkit-box-shadow: inset 0 0 0px rgba(0,0,0,0); 
    -webkit-border-radius: 0px;
    border-radius: 0px;
    background: #272822 !important;
    
   
}
 
::-webkit-scrollbar-thumb {
    -webkit-border-radius: 10px;
    border-radius: 2px;
    background: #42433a; 
    -webkit-box-shadow: inset 0 0 0px #4b4c42; 
}

.CodeMirror-scrollbar-filler, .CodeMirror-gutter-filler {
background-color: #272822;
}
}
