@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("torrent-tv.ru") {
  html, body {background: url(http://ya-vidoc3000.narod.ru/anime/11.jpg) no-repeat fixed; -webkit-background-size: cover; background-size: cover;}
  .features-wrapper, .background-wrapper, .footer-wrapper, #share42 {display: none !important;}
  .header {margin: 0 !important;}
  .header .extended-bar .search input[type="text"] {background: none !important; box-shadow: none !important;}
  .blue-button {background: none !important;}
  /*Убираем правую колонку, там всякая херня!*/
  .right-side {display: none !important;}
  /*Убираем баннер помощи из под меню*/
  .help-wrapper {display: none !important;}
  /*Для всей страницы*/
  .page {clear: both !important;}
  /*Отступ контента страницы от меню*/
  .usual-wrapper {margin: 3px !important;}
  /*Настройки меню*/
  .menu-divider, .menu-divider-vip {background: none !important;}
  .page .menu > ul > li.menu-divider-vip, .page .menu > ul > li.menu-ending-vip {background: none !important; color: white !important;}
  .page .menu {background: none !important; background-color: black !important; opacity: 0.7;}
  .page .menu > ul > li > a:hover {color: white !important;}
  .page .menu > ul > li > ul > li {background-color: black !important;}
  .page .menu > ul > li > ul > li > ul > .column-0 {background-color: black !important;}
  .page .menu > ul > li > ul > li > ul > .column-1 {background-color: black !important;}
  .page .menu > ul > li > ul > li > ul > .column-2 {background-color: black !important;}
  .page .menu > ul > li > ul > li > ul > .column-3 {background-color: black !important;}
  .page .menu > ul > li > ul > li > ul > .column-4 {background-color: black !important;}
  /*Убираем фон вокруг плеера*/
  .background-wrapper-tv-mode {display: none !important;}
  /*Убираем черный фон заголовков страниц*/
  .background-wrapper-secondary {display: none !important;}
  /*Убираем лишние кнопки*/
  .nnerba {display: none !important;}
  /*Какой-то мусор*/
  .XZwYKf58, .XZwYKf58 div, .XZwYKf58 a, .XZwYKf58 a:focus, .XZwYKf58 a:hover, .XZwYKf58 a:link, .XZwYKf58 a:active, .XZwYKf58 img, .XZwYKf58 iframe {display: none !important;}
  /**/
  .extended-bar {background-color: black !important; opacity: 0.7;}
  /**/
  .usual, .usual-content {width: 911px !important; background: none !important; border: none !important;}
  .usual-header {width: 870px !important;}
  .usual .news-wrapper {border: none !important; background: none !important;}
  /*Каналы*/
  .best-channels-header {width: 900px !important;}
  .best-channels {width: 1000px !important; background: none !important; border: none !important;}
  .best-channels-content {background: none !important;}
  .channel-filter {width: 911px !important;}
  /**/
  .usual-header, .best-channels-header, .channel-filter {background: none !important; color: red !important; text-align: left !important; border: none !important;}
}
