css:
@-moz-document domain("enroll-me.iiet.pl") {
  /*login i ogolne*/
  body{
    background: #555 !important;
    color: #DDD !important;
  }
  .hero-unit.desert-form{
    background: #333 !important;;
  }
  
  /*pasek nawigacji*/
  .navbar-inner{
    background: #333 !important;
  }
  
  /*guzik nawigacji, logowania*/
  .btn-primary{
    background: orange !important;
    color: #000 !important;
  }
  .btn-navbar{
    background: #222 !important;
  }
  .btn-danger, .discardButton{
    background: #F30 !important;
    color: #000 !important;
  }
  .btn-success {
    background: green !important;
    color: #FFF !important;
  }
  .btn-group > .btn{
    background: #555 !important;
  }
  .btn-group > .btn:hover, .editButton{
    background: orange !important;
  }

  /*hover nad menu*/
  .nav > li > a:hover{
    background: orange !important;
    color: #000 !important;
  }
  
  /*pola tekstowe i hasla*/
  /*aktywne*/
  input[type="text"]:focus, input[type="password"]:focus{
    border-color: orange !important;
    background: #333;
    color: #DDD;
  }
  
  /*nieaktywne*/
  input[type="text"], input[type="password"]{
    border-color: #DDD !important;
    background: #333;
    color: #DDD;
  }
  .row.well, .header{
    background: #333 !important;
    color: #DDD !important;
  }
  .hero-unit{
    background: #555 !important;
  }
  .nav > li > a:focus{
    color: orange !important;
  }
  .shadow{
    box-shadow: None !important;
  }
  .span9, #j_id_19, #j_id_1f, #add, .span12{
    color: #DDD !important;
  }
  /*lista zapisow*/
  .table-hover tbody tr:hover > td{
    background: #333 !important;
    color: #DDD !important;
  }
  .table, .table-striped tbody > tr:nth-child(2n+1) > th, .table-striped tbody > tr:nth-child(2n+1) > td{
    background: #333 !important;
    color: #DDD !important;
  }
  #mainForm, #j_id_1b, #form, #rulesForm, #progressForm{
    color: #DDD;
  }
  /*tabelka rozdania punktów*/
  #progressForm\:progressPanel_content, #progressForm\:progressPanel{
    background: #555;
  }
  .text-center{
    color: #DDD !important;
  }
  
  /*Input prowadzacych*/
  #j_id_1b\:firstname, #j_id_1b\:lastname, #j_id_1b\:degree, #j_id_1b\:room{
    background: #333;
    color: #DDD;
  }
  
  /*konfiguracja zapisów*/
  .ui-inputfield, .ui-widget-content{
    background: #333 !important;
    color: #DDD !important;
  }
  
  /*LISTA STUDNETOW*/
  /*naglowki listy do zarzadzania studentami*/
  #mainForm\:peopleTable_paginator_top, #mainForm\:peopleTable_head, #mainForm\:peopleTable\:j_id_29, #mainForm\:peopleTable\:j_id_2b, #mainForm\:peopleTable\:j_id_2d, #mainForm\:peopleTable\:j_id_2f, #mainForm\:peopleTable\:j_id_2h, #mainForm\:peopleTable\:j_id_2j, #mainForm\:peopleTable_paginator_bottom{
    background: #333 !important;
    color: #DDD;
  }
  #mainForm\:peopleTable, #mainForm\:peopleTable_data, tr.ui-widget-content{
    color: #DDD;
    background: #555;
  }
  /*guziki 1234-10 oraz strzalki krote mozna kliknac*/
  #mainForm\:peopleTable_paginator_top > span:nth-child(3) > span, #mainForm\:peopleTable_paginator_bottom > span:nth-child(3) > span, #mainForm\:peopleTable_paginator_top > span:nth-child(1), #mainForm\:peopleTable_paginator_top > span:nth-child(2), #mainForm\:peopleTable_paginator_bottom > span:nth-child(1), #mainForm\:peopleTable_paginator_bottom > span:nth-child(2), #mainForm\:peopleTable_paginator_top > span:nth-child(4), #mainForm\:peopleTable_paginator_top > span:nth-child(5), #mainForm\:peopleTable_paginator_bottom > span:nth-child(4), #mainForm\:peopleTable_paginator_bottom > span:nth-child(5){
    color: #000;
    background: #888;
  }
  /*lista - po ile ludzi wyswietlac*/
  #mainForm\:peopleTable_paginator_top > select:nth-child(6), #mainForm\:peopleTable_paginator_bottom > select:nth-child(6){
    color: #000;
    background: #888;
  }
  #mainForm\:peopleTable\:j_id_29\:filter, #mainForm\:peopleTable\:j_id_2b\:filter, #mainForm\:peopleTable\:j_id_2d\:filter, #mainForm\:peopleTable\:j_id_2f\:filter, #mainForm\:peopleTable\:j_id_2h\:filter, #mainForm\:peopleTable\:j_id_2j\:filter{
    background: #888;
    color: #FFF;
  }
  
  /*KALNEDARZ*/
  .accordion-toggle, .accordion-body{
    background: #333 !important;
  }
  
  .badge {
    background: orange !important;
    color: #333 !important;
  }
  
  .ui-progressbar-value.ui-widget-header{
    background: orange !important;
  }
  
  .ui-messages-warn{
    background: orange !important;
    color: #333 !important;
  }
  
  #form\:j_id_2s > div:nth-child(1), #form\:pointsSpinner_input, #form\:j_id_2j > div:nth-child(1), #form\:j_id_2j > div:nth-child(2){
    background: #333;
    color: #DDD;
  }
  #form\:j_id_2s{
    background: #555;
  }
  .ui-progressbar{
    background: #888 !important;
  }
  .ui-widget-header{
    background: #333 !important;
    color: #FFF !important;
    border-color: #555 !important;
  }
  .ui-widget-content{
    color: #FFF !important;
    border-color: #555 !important;
  }
  
  /*niemozliwosc*/
  #form\:reasonPanel{
    background: #333;
  }
  #form\:reasonArea, #form\:multiReasonArea{
    background: #555;
    color: #DDD;
  }
  /*tla kalendarza itp*/
  [class|="fc"] > th {
    background: #333 !important;
    color: #DDD !important;
  }
  .fc-agenda-slots{
    background: #333 !important;
  }

  /*kolor wygaszonego terminu*/
  .fc-event-head, .fc-event-title {
    color: white !important;
    opacity: 1 !important;
  }
  .fc-event-bg{
    opacity: 0 !important;
  }
  /*error dialog?*/
  .ui-widget-header a{
    background: #555 !important;
  }
  /*reset hasla*/
  #password-reset-form\:j_id_2v, #password-reset-form\:j_id_2w{
    background: orange;
  }
  .ui-filter-column{
    background: #333 !important;
    color: #DDD !important;
  }
  #dialogForm1\:confirm, #dialogForm1\:decline{
    background: orange!important;
    color: #333 !important;
  }
}
