css:
@-moz-document url-prefix("https://www.evernote.com") {

Body {
background-color: #C0DCC0 !important;
}

.Menu-activator {
	margin-left: 0px;
}
    
.Menu-activator:hover {
	border-radius:3px;
	background-color: rgb(29,173,22) !important;
}

.noteSnippetTitle {
background-color: #DDDDDD !important;
}

.noteSnippet {
background-color: #C0DCC0 !important;
	}

body.ennote div, body#tinymce div {
        font-family: arial !important;
        background-color:#C0DCC0;
    }

