css:
@-moz-document domain("weibo.com") {
body {
/*font: 12px/1.3 'Arial',sans-serif!important;*/
}

    .FRAME_main {
        background-color: #eee !important;
    }
    .WB_frame {
        background-color: rgba(238, 238, 238, 0.9)!important;
}
    .FRAME_main #plc_main, .FRAME_message #plc_main {
        width: 760px !important;
        margin-left: 40px !important;
        background: #fafafa !important;
        padding: 0 10px 0 20px !important;
        box-shadow: none!important;
        border-radius: 5px !important;
    }
    .send_weibo {
        height: auto !important;
 		padding: 15px 0px 10px!important;
    }
    .send_weibo .kind {
        width: 400px;
    }
    .send_weibo .kind .ficon_more {
        display: inline;
    }
    #v6_pl_content_publishertop .func_area {
        padding-top: 5px;
    }
    #v6_pl_content_homefeed #home_new_feed_tip {
        margin-top: 10px;
        border: 1px solid #d8d8d8;
    }
    .WB_main_c .WB_cardwrap {
        border-radius: 0;
        box-shadow: none !important;
        border-color: #d8d8d8;
        border-radius: 3px;
        border-style: dotted;
        border-width: 0 0 1px 0;
        background: none !important;
    }
    .WB_main_c .send_weibo {
        background: none !important;
        box-shadow: none !important;
    }
    .FRAME_main .WB_handle, .FRAME_message .WB_handle {
        position: relative;
        height: 28px;
        bottom: 20px;
        width: 270px;
        left: 475px;
    }
    .WB_feed_handle .WB_handle {
        bottom: 18px;
    }
    .WB_from {
        position: relative;
        top: 5px;
        left: 0px;
    }
    .scroller .WB_from {
        top: 0px;
    }
	.WB_feed .WB_cardtitle_a {
		margin-top:-10px;
    }
    .WB_func .WB_handle {
        position: relative;
        top: 5px;
        bottom: 0;
        left: 0;
        z-index: 999;
        width: auto;
        height: 25px;
    }
    .WB_repeat .WB_handle ul {
        width: 210px;
    }
    .WB_func .WB_handle ul {
        width: auto;
    }
    .WB_handle ul {
        width: 100%;
    }
    .WB_handle ul li:nth-child(4) {
        position: relative;
        margin-left: -5px !important;
    }
    .WB_handle .WB_row_line .line, .WB_func .WB_handle li .line {
        border-width: 0px !important;
    }
    .WB_feed .WB_feed_handle {
        padding-bottom: 0px;
        margin: -15px 0 -3px 0;
    }
    .WB_feed_handle {
        padding-bottom: 5px;
    }
    #v6_pl_content_homefeed div[class="WB_feed_repeat S_bg1"] {
        position: static !important;
        width: 650px;
        margin: -5px 0 15px 80px;
    }
	#Pl_Official_MyProfileFeed__21 div[class="WB_feed_repeat S_bg1"] {
        position: static !important;
        width: 600px;
        margin: -5px 0 15px 20px;
    }
	#Pl_Official_MyProfileFeed__22 div[class="WB_feed_repeat S_bg1"] {
        position: static !important;
        width: 600px;
        margin: -5px 0 15px 20px;
    }
    div[class="WB_feed_repeat S_bg1 WB_feed_repeat_v3"] {
        margin:0;
        border:none;
    }
    .WB_main_c .WB_feed_repeat .WB_face, .WB_feed_repeat .WB_face img {
        height: 30px;
        width: 30px;
        border-radius: 50%;
    }
    .WB_main_c .WB_feed_repeat .repeat_list {
        padding: 0 20px !important;
    }
    .WB_main_c .WB_feed_repeat .repeat_list .WB_func .WB_handle {
        top: 10px;
    }
    .WB_main_c .WB_feed_repeat .repeat_list .between_line {
        border: none !important;
    }
    .WB_main_c .feed_spe_list_a .list_li_trend {
        width: 740px !important;
    }
    .WB_media_a_mn {
        width: 310px !important;
    }
    .WB_media_wrap .WB_feed_spec_clearfix {
        bottom: 5px !important;
    }
    .WB_expand_media_box .tab_feed_a .tab {
        margin: 10px 5px !important;
    }
    .WB_expand {
        padding: 10px 17px !important;
    }
    #home_new_feed_tip {
        background: #fff8bf !important;
    }
    .WB_feed_together {
        width: 664px;
        margin: 0 0 20px 70px;
        border: 1px solid #d8d8d8 !important;
        border-width: 1px !important;
    }
    .WB_feed_together .WB_sonFeed .WB_feed_repeat .WB_repeat {
        margin: 0 0 0 -50px !important;
        width: 90%;
    }
    .WB_feed_together .WB_sonFeed .WB_feed_repeat {
        background: #f2f2f5;
    }
    .WB_feed_together .WB_sonFeed .arrow {
        visibility: hidden !important;
    }
    .WB_feed_together .wft_hd .wft_info {
        border: none !important;
    }
    .WB_feed_expand .WB_text {
        padding: 5px 0 !important;
    }
    .WB_feed_expand .W_arrow_bor {
        position: relative;
        top: 1px;
        z-index: 999;
    }
    .WB_feed_expand .W_arrow_bor:after {
        position: relative;
        display: inline-block;
        content: '';
        height: 0;
        width: 0;
        overflow: hidden;
        font-size: 0;
        line-height: 0;
        border-color: transparent transparent #f2f2f5 transparent;
        border-style: solid;
        border-width: 11px;
        margin: -21px 0 0 -21px;
    }
	.WB_expand .WB_empty .WB_innerwrap {
		padding: 0px!important;
	}
    
    /* chrome fix positioning */
    @media screen and (-webkit-min-device-pixel-ratio:0) {
        .WB_feed_expand .W_arrow_bor:after {
            border-width: 10px;
            margin: -20px 0 0 -20px;
        }
    }
    .WB_feed_expand .W_arrow_bor i {
        border-width: 10px;
        margin: -10px 0 0 16px !important;
        border-color: transparent transparent #d8d8d8 transparent;
    }
    .WB_screen .screen_box .W_ficon {
        opacity: 0;
    }
    .WB_feed_type:hover .WB_screen .screen_box .W_ficon {
        opacity: 1;
    }
    .S_bg1, blockquote, .SW_fun_bg:hover, .SW_fun_bg_active {
        border: 1px solid #d8d8d8;
    }
    .S_line2 {
        border-color: #fafafa !important;
    }
    .WB_tab_a {
        background: none !important;
  		box-shadow: none !important;
    }
	.WB_tab_a .tab_box .fr_box {
		box-shadow: none !important;
	}
    .tab_li {
    	box-shadow: none !important;
    }
    .WB_tab_a .tab li.curr .b .b1 em i {
    	box-shadow: none !important;
	}
	.WB_tab_a .tab li.S_bg2 {
    	box-shadow: none !important;
    }
	.WB_tab_a .tab_box_a_r5 .tab .b .W_arrow_bor {
   		display:none!important;
    }
	.S_bg2 {
		background-color: transparent;
	}
    .W_fr {
		margin: 0 -4px 0 0;
	}
    #plc_main .WB_main_c {
        width: 750px !important;
    }
    #v6_pl_leftnav_group {
        margin: 180px 0 0 10px !important;
    }
    #v6_pl_leftnav_group a {
        color: #333 !important;
        font-weight: normal !important;
    }
	.WB_left_nav {
		width: 165px!important;
	}
	.WB_left_nav .lev_line fieldset {
	padding: 0 0 0 145px!important;
	}
	.WB_left_nav .lev .W_new_count {
	margin: 10px 0px 0 0!important;
	}
    #v6_pl_rightmod_myinfo {
        position: relative !important;
        right: 995px;
        float: left !important;
        width: 250px;
    }
    #v6_pl_rightmod_myinfo .S_bg2 {
        background: none !important;
        box-shadow: none !important;
    }
    #v6_pl_rightmod_myinfo #skin_cover_s {
        background: none !important;
        margin: 20px 50px 0 0 !important;
        color: #eee !important;
    }
    #v6_pl_rightmod_myinfo .headpic, #v6_pl_rightmod_myinfo .headpic img {
        width: 100px !important;
        height: 100px !important;
    }
    #v6_pl_rightmod_myinfo .headpic img {
        box-shadow: 0 0 2px rgba(0, 0, 0, 0.8) !important;
    }
    #v6_pl_rightmod_myinfo .WB_innerwrap {
        padding: 20px 0 0 54px !important;
    }
    #v6_pl_rightmod_myinfo .WB_innerwrap  .user_atten li {
        width: 45px !important;
    }
    #v6_pl_rightmod_myinfo .WB_innerwrap .user_atten li strong {
        width: 45px !important;
        font-size: 12px !important;
        font-weight: normal !important;
    }
    #v6_pl_rightmod_myinfo .WB_innerwrap .user_atten li span {
        color: #000;
    }
    .WB_main_r {
        margin: 0 !important;
        width: 0 !important;
    }

    .WB_left_nav .lev a:hover, .WB_left_nav .lev_curr, .WB_left_nav .lev_curr:hover, .WB_left_nav .levmore .more {
        background-color: #e0e0e0 !important;
        border-top-left-radius: 5px;
        border-bottom-left-radius: 5px;
    }
    .WB_left_nav .lev_Box .lev a {
        width: 150px !important;
    }
    .WB_left_nav .lev_Box, .WB_left_nav fieldset {
        border-color: #e0e0e0 !important;
    }
    .WB_left_nav .S_txt1, .WB_left_nav a.S_txt1:hover, .WB_left_nav .S_ficon, .WB_left_nav a:hover .S_ficon, .WB_left_nav a.S_ficon:hover, .WB_left_nav .lev .W_new_count {
        color: #000 !important;
    }
    .WB_feed_spec_info {
        padding: 10px 0 0 0 !important;
    }
    .WB_footer, .WB_global_nav .gn_person .W_ficon, #v6_pl_rightmod_myinfo .nameBox, #v6_pl_content_publishertop .title_area .key, #v6_pl_content_publishertop .title_area .title, #v6_pl_rightmod_updatev6, #v6_pl_rightmod_recominfo, .WB_right_module, #v6_pl_content_setskin, .PCD_mplayer, #plc_bot {
        display: none !important;
    }
    




.WB_notes {
	padding: 0!important;
	}


@media screen and (min-width: 1295px) {
	.WB_global_nav .gn_topmenulist_tips {
		right: -70px!important;
	}
    }
    
@media screen and (max-width: 800px) {
.WB_main_l {
display:none!important;
}
.FRAME_main #plc_main, .FRAME_message #plc_main {
margin-left:0 !important;
}
        
}

div[class="NEWUSER_tips"],
div[class="NEWUSER_shake"],
div[class="feed_lists search_notes"] {
    display: none !important;
}
}

@-moz-document domain("weibo.com") {
