    }
    #Pl_Official_MyProfileFeed__24 .WB_feed_repeat .repeat_list .between_line {
        border: none !important;
    }
    #Pl_Official_MyProfileFeed__26 .WB_cardwrap .WB_handle {
        left: 330px !important;
    }
    #Pl_Official_MyProfileFeed__26 .WB_cardwrap .WB_handle ul {
        border: none !important;
    }
    #Pl_Official_MyProfileFeed__26 .WB_func .WB_handle {
        left: 0 !important;
    }
	.WB_timeline {
		margin:0 0 0 500px!important;
    }
	i[title="此条微博无法使用推广功能"] {
		display:none!important;
	}
}
