@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("2ip.ru") {
  a {color: lightgreen !important;}
body {background: url(http://ya-vidoc3000.narod.ru/fon/4-82.jpg) no-repeat fixed !important; -webkit-background-size: cover !important; background-size: cover !important; color: lightgreen !important;}
  [color*="black"] {color: lightgreen !important;}
  [color*="#fff"] {color: lightgreen !important;}
  [color*="#000"] {color: lightgreen !important;}
  [color*="white"] {color: lightgreen !important;}
  /**/
  #wrapper {background: none !important; margin: auto !important;}
  /*Заголовок*/
  #header {background: black !important; opacity: 0.7; margin-bottom: 10px !important;}
  #header #user-bar {background: black !important; opacity: 0.7; color: lightgreen !important;}
  #header #user-bar div {background: none !important; color: lightgreen !important;}
  #header #user-bar ul li.reg a {background: none !important; color: lightgreen !important;}
  #header #user-bar ul li.reg a span {background: none !important; color: lightgreen !important;}
  /*Фон контента*/
  #content .ip-info {background: none !important; background-color: black !important; opacity: 0.7; color: white !important;}
  #content .nav-section .tabs li.active a span {background: none !important;}
  #content .nav-section .tabs {background: none !important; border: none !important;}
  #content .nav-section .tabs li a span {background: none !important;}
  #content .nav-section .tabs li.active a {background: none !important;}
  #content .nav-section .tabs li a {background: none !important;}
  .active div {background: none !important;}
  .active, #serviceMenuSelector, #funMenuSelector {background-color: black !important; opacity: 0.7;}
  .active:hover, #serviceMenuSelector:hover, #funMenuSelector:hover {opacity: 1;}
  #content .nav-section .nav li a {background-color: black !important; opacity: 0.7;}
  #content .content-slider .ajax {background-color: black !important; opacity: 0.7;}
  #footer, .banner_container, #ads, #wide_banner_wrap {display: none !important;}
  #content {background-color: black !important; opacity: 0.7 !important; color: lightgreen !important;}
  #content .ip-info-entry {background: none !important;}
  /**/
  .cookies-notification {background: none !important;}
  /**/
  #content .more {background: none !important; color: lightgreen !important;}
  /*Меню*/
  #aside .nav li.even a {background: black !important;}
  #aside .nav li a {background: black !important;}
  /**/
  #content .speed form {background: black !important;}
  #content .related-content-links {background: none !important;}
  #content .review blockquote p {color: lightgreen !important;}
  #content .speed form .input-text {color: black !important;}
  div.userbar-row textarea {color: black !important;}
  #content .reviews .search {background: none !important; border: none !important;}
  #content .reviews .add-review {background: none !important;}
  blockquote {background: none !important; }
  #content .review .title1 {background: red !important;}
  .rating {background: none !important;}
  tr[style*="background-color: rgb(240, 247, 248);"] {background: black !important;}
  tr[style*="background-color: #F0F7F8"] {background: black !important;}
  #content .user-login {background: none !important;}
  #wrap {padding: 0 !important;}
  /*Мусор*/
  .service-block {display: none !important;}
  #content .speed-b .results tbody th {background: black !important;}
  #content .speed-b .results tbody td {background: black !important;}
  div.userbar-row {border: none !important;}
  #content .speed form {border: none !important;}
  #content .speed form div {border: none !important;}
  table.rating {width: 520px !important;}
  /**/
  .allmark {background: black !important; border: none !important;}
  #content .related-content-links-root li a {color: red !important;}
  #content .related-content-links-root li {background: none !important;}
  #content .reviews .add-review .input-button, #content .reviews .search .input-button, #content .speed form .input-button, table.toolbar .input-button, #content .speed form #checkSpeedButton {text-decoration:none; 
 text-align:center; 
 padding:5px 11px; 
 border:none !important;; 
 -webkit-border-radius:4px;
 border-radius:4px; 
 border-radius: 4px; 
 font:16px Arial, Helvetica, sans-serif; 
 font-weight:bold; 
 color:#E5FFFF; 
 background-color:#47b53f; 
 background-image: -moz-linear-gradient(top, #47b53f 0%, #206e33 100%);  
 -webkit-box-shadow:0px 0px 2px #bababa, inset 0px 0px 1px #ffffff; 
 box-shadow:0px 0px 2px #bababa, inset 0px 0px 1px #ffffff; }
  .input-button {background-image: none !important;}
  #content .spam {background: black !important;}
  #content .related-content-links li {background: none !important;}
  #content .related-content-links li a {color: red !important;}
  #content .reviews .search .input-text {color: black !important;}
  #content .review blockquote {border: none !important;}
  .photo img {border-radius: 10px !important;}
   /*********************************************************************/
}
@-moz-document url-prefix("https://2ip.ru/forum/") {
  /*Форум*/
  body {color: lightseagreen !important;}
  .mainpage {background: none !important;}
  #header {background: none !important;}
  .tableoutborder {background: none !important;}
  table.tableoutborder {width: 100% !important;}
  .mainpage {background: none !important;}
  .tablea {background: none !important; color: white !important;}
  .tableb {background: none !important; color: white !important;}
  /*Цвет форума*/
  .tableoutborder {background: black !important; opacity: 0.8 !important;}
  .tableinborder {background: black !important; opacity: 1 !important;}
  .tabletitle {background: black !important; opacity: 1 !important;}
  .tablecat {background: black !important; opacity: 1 !important; color: lightseagreen !important;}
  /************/
  .tablea_fc {color: white !important;}
  .tableb_fc {color: white !important;}
  .tableinborder {border-collapse: collapse;}
  table.tableinborder td {border: 1px solid black !important;}
  .inposttable {background: black !important;}
}
