css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.bungie.net") {
  
  
  .cf {
    background-color: #363D40;
    color: white;   
    
  }
  
  .container_underHeader {
    background-color: #2F3032;
  }
  
  .container_sort .sort a {
    color: #FFFFFF;
    
  }
  
  .container_sort {
    border-top: 2px solid #363D40;
    border-bottom: 2px solid #2F3032;
    
  }
  
  .topics .title {
    color: #71CAEF;
  }
  
  .container_underHeader .btn_openCreateNewTopic {
    color: white;
    background: url("http://i.imgur.com/0F0iJG2.png") no-repeat scroll 13px 8px rgba(0, 0, 0, 0);
    border: 2px solid #363D40;
  }
  
  .searchedTag {
    color: white;
    border: 2px solid #363D40;
  }
  
  .topics li {
    border-top: 2px solid #2F3032;
    
  }
  
  .PublicForum .column.main {
    background-color: #4B565B;
    border-right: 2px solid #2F3032;
  
  }
  
  .nav_side li.tags {
    background-color: #363D40;
  }
  
  #nav_tags h4 {
    color: white;
  }
  
  .nav_side a {
    color: white;
  }
  
  #nav_tags .container_addTag, #nav_tags .followed.tags.hide ~ .container_addTag {
    background-color: #363D40;
   
  }
  
  #nav_tags {
    border-right: 2px solid #2F3032;
  }
  
  #nav_tags .container_textbox {
    background-color: #2f3032;
   
  }
  
  .container_textbox input {
    color: white;
  }
  
  .nav_side .btn_codeOfConduct {
    background-color: #363D40;
  }
  
  .pager_prevnext {
    background-color: #4b565b;
    border-top: 2px solid #2F3032;
  }
  
  .forumPost article .post_body, .forumPost article .post_urlLinkOrImage {
    color: #BBBBBB;
    
  }
  
  .forumPost article h1 a {
    color: #71caef;
  }
  
  #post > .post {
    background-color: #363d40;
    
  }
  
  .forumPost .post_info {
    border-bottom: 4px solid #2F3032;
  }
  
  .forumPost .header_post {
    background-color: #363d40;
  }
  
  .forumPost .post_title {
    color: white;
  }
  
  .forumPost .post {
    border-top: 2px solid #2F3032;
    
  }
  
  .replies {
    background-color: #363d40;
  }
  
  .bg_separate {
    background-color: #363d40;
    
  }
  
  #forum_post {
    padding-bottom: 0px;
  }
  
  .forumPost article .avatar {
    height: 70px;
    width: 70px;
    box-shadow: 0 0px 0px 0px transparent inset, 0 0px 0 #ffffff;
    border-radius: 3px;
    border: 3px solid #2F3032;
    
  }
  
  
  .forumPost article .avatar:hover:before {
    border: 2px solid rgba(99, 99, 99, 0.9);
  }
  
  .forumPost article .content {
    margin-left: 2.5%;
    padding-bottom: 2%;
   
    
  }
  
  .forumPost .btn_olderBlock, #ClanAnnouncements .btn_olderBlock, .Groups .forumPost .btn_olderBlock, .Groups .column.main .btn_olderBlock {
    margin-left: 150px;
  }
  
  
.forumPost .post_urlLinkOrImage.gated.video {
   background-color: #2F3032;
  
  }
  
  .forumPost .post_urlLinkOrImage.gated {
    background-color: #2F3032;
  }
  
  .forumPost .post_urlLinkOrImage.gated:hover {
    background-color: #4B565B;
  }
  
  
  
  .forumPost .spoiler {
   background-color: #2F3032;
   border-color: #2F3032;
   color: #71caef;
  }
  
  .forumPost .spoiler:after {
    background-color: #4B565B;
    border-color: #2F3032;
    color: #71caef;
  }
  
  .forumPost .spoiler.opened {
    background-color: #4B565B;
    color: #BBBBBB;
  }
  
  .extraInfo_user .about {
    color: #BBBBBB;
    
  }
  
  .extraInfo_user .btns a {
    color: #BBBBBB;
    background-color: #2C2D2F;
    border: 1px solid #2F3032;
  }
  
  .extraInfo_user .btns a:hover {
    background-color: #0096DB;
    color: white;
  }
  
 #content_categories {
   background-color: #363D40;
   margin-bottom: 0px;
 }
  
  .categoryList .content {
    background-color: #4B565B;
  }
  
  .categoryList .content:hover {
    background-color: #2F3032;
    
  }
  
  .categoryList a:hover .title {
    color: white;
  }
  
  #content_categories h2 {
    color: #BBBBBB;
    border-top: 4px solid #2F3032;
  }
  
  .container_voting a {
    background: url("http://i.imgur.com/vPPOQYe.png") no-repeat scroll 0 0 rgba(0, 0, 0, 0);
  }
  
  #list_groups {
    background-color: #4b565b;
  }
  
  .header_clan .title {
    color: #BBBBBB;
  }
  
  .groupCard .container_basic {
    color: #BBBBBB;
    background-color: #363D40;
  }
  
  .groupCard .name a {
    color: #BBBBBB;
  }
  
  #container_ClanContent h4 {
    color: #BBBBBB;
    background-color: #2F3032;
  }
  
 #container_ClanContent {
   padding-bottom: 0px;
   background-color: #2F3032;
 }
  
  .list_clanChat .displayName {
    color: #71caef;
  }
  
  .list_clanChat .body {
    color: #BBBBBB;
  }
  
  .list_clanChat .date {
    color: #BBBBBB;
  }
  
  .Nav_Top article h1 a, .pinned article h1 a {
    color: #71caef;
  }
  
  .Nav_Top article p, .pinned article p {
    color: #BBBBBB;
  }
  
  
  a {
    color: #479CE4;
  }
  
  .clanUsersPage .list_members .displayName a {
    color: #71caef;
  }
  
  body.Clans #mainContent {
    background-color: #363D40;
  }
  
  .forumPost blockquote {
    background-color: #475054;
    color: #BBBBBB;
    border-color: #2F3032 #2F3032 #2F3032 #71caef; 
  }
  
  .contextPane .TabNav_Main {
    background-color: #363D40;
  }
  
  .contextPane .profileTabSection {
    color: #BBBBBB;
  }
  
  .contextPane:before {
    background-color: #4B565B;
    border-left: 1px solid #2F3032;
    border-right: 2px solid #2F3032;
    
  }
  
  .TabNav_Side h4 {
    color: #BBBBBB;
  }
  
  .tabnav_side_list li a {
    color: #BBBBBB;
    border-top: 1px solid #2F3032;
  }
  
  .tabnav_side_list li.on a {
    background-color: #479CE4;
    color: white;
  }
 
  .profileTabSection .container_checkbox label {
    color: #BBBBBB;
  }
  
  .container_select label {
    color: #BBBBBB;
  }
  
  .contextPane.settings .mute .list .displayName {
    color: #BBBBBB;
  }
  
  .TabNav {
    background-color: #363D40;
  }
  
  .forumPost article .post_urlLinkOrImage img {
    background-color: #4B565B;
    border: 2px solid #2F3032;
  }
  
  .container_textbox input {
    color: black;
  }
  
  .column_createPostSide h4, #container_ClanContent .column_createPostSide h4 {
    color: #BBBBBB;
  }
  
  .column_createPostSide .select label {
    color: #BBBBBB;
  }
  
  .container_newPostForm .container_checkbox label {
    color: #BBBBBB;
   
  }
  
  .select .option a {
    background-color: #4B565B;
  }
  
  .PublicForum .column.main:before {
    background-color: #4B565B;
  }
  
  .GroupSettingsForm h4 {
    color: #BBBBBB;
  }
  
  .GroupSettingsForm .container_checkbox label {
    color: #BBBBBB;
  }
  
  .GroupChat .msg_newgroup {
    background-color: #4B565B;
  }
  
  .list_clanChat {
    background-color: #4B565B;
  }
  
  #AdminPages .section h4, .clanUsersPage .section h4 {
    color: #BBBBBB;
  }
  
  #ClanAdmin:before {
    background-color: #4B565A;
    border-left: 1px solid #2F3032;
    border-right: 1px solid #2F3032;
   
  }
  
  .TabNav_Side {
    border-left: 1px solid #2F3032;
    border-right: 1px solid #2F3032;
    background: none repeat scroll 0 0 #2F3032;
  }
  
  
  .tabnav_side_list li a {
    background-color: #4B565A;
  }
  

  
  
  .count {
    /*position: absolute;  
    right: 5.5%;
    bottom: 1%;
    z-index: 2;*/
    display: none;
  }
  
  .cloned .count {
    background-color: #4B565A;
    border-color: #2F3032;
    color: white;
  }
  
  .cloned .count:before {
    background: url("http://i.imgur.com/xJc1GjZ.png") no-repeat scroll left center rgba(0, 0, 0, 0);
  }
  

 
 

}
