css:
@-moz-document url-prefix(http://www.funnyjunk.com/), domain(funnyjunk.com) {
/*==Various Funnyjunk style fixes/changes==*/


/*Content width changes*/

div.cImg img[src$='.jpg']{
  width: 75%;
  max-width: 1000px !important;
  
}

div.cImg img[src$='.png']{
  width: 75%;
  max-width: 1000px !important;
}

div.cImg img[src$='.gif']{
  width: 75%;
  max-width: 800px!important; 
  max-height: 850px;
}


div#flashmovie.player{
  width: 90%!important;
  max-width: 1000px!important;  
  margin: 0 5%!important;
}

video#content-video{
  width: 90%!important;
  max-width: 1000px!important;  
}


#mediaContainer{
  width: 95% !important;
  max-width: 1000px !important;
}
/*--------------------------------*/

/*Search button visable*/
/*I use search page as a neutral page, keeping
fj open without having to load lots of images*/

#topSearchBttn{
  margin-top: -10px!important;
  padding: 0px 0 0px 0 !important;
  display: inline-block!important;
}

/*------------------------------*/


/*Make Scrolling sidebar taller, more content visable*/
.scrollbar.sidebar{
  max-height:800px!important;
}


div .mCSB_1{
  max-height:800px!important;
}

/*------------------------------*/


.left_menu_mature{
    background-color:#57191D!important;
}
  
#contentLeft table.menu td.left_menu_mature, #contentLeft table.menu td.left_menu_mature a {
	color: white;
}
}
