css:
@-moz-document url-prefix("http://www.memory.gov.ua/") {
body {
    font-size: 13px;
    line-height: 1.5;
}

a {
     color: #6e1b1b !important;
     text-decoration: none !important;
 }

a:hover {
    color: #B01515 !important;
    text-decoration: underline !important;
}

hr {
    height: 0;
    border-top: 0;
}

h1.headerTitle {
    font-size: 16px;
    line-height: 16px;
}

.header a {
    letter-spacing: 0;
    font-weight: normal;
    font-style: normal;
    font-size: 16px;
}

.incenter h1.pagetitle {
    font-size: 1.5em;
    text-transform: none;
    color: inherit;
    margin: 0.67em 0;
}

h2 {
    color: inherit;
}

.leftNavi a {
    font-size: 13px !important;
}

.view-id-docs .title {
    font-weight: bold;
}

.item-list .pager li,
.item-list .pager li a {
    border-bottom: 0 !important;
    border-radius: 0!important;
    display: inline-block;
}

.pager li,
.pager li.pager-current,
.pager li a {
    font-size: 13px !important;
}

.pager a {
    color: #fff !important;
}

.pager a:hover {
    color: #fff !important;
    text-decoration: none !important;
}

.pager li.pager-current {
    cursor: default;
}

img[src="sites/all/themes/divilon_vision/img/author_.gif"] {
    display: none !important;
}
}
