@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("animespirit.ru") {
  body {background: url(/*[[USERIMAGE]]*/) no-repeat center center fixed !important; -webkit-background-size: cover !important; background-size: cover !important; color: white !important;}
  /*Вырезаем все лишнее*/
  #header, #CollapsiblePanel_LTBanner_NOHIDE, #CollapsiblePanel_Interesnoe, #CollapsiblePanel_LBBanner_NOHIDE, #CollapsiblePanel_Statistics, #CollapsiblePanel_RTBanner_NOHIDE, #CollapsiblePanel_RBBanner_NOHIDE, #CollapsiblePanel_Recommend, #footer, #CollapsiblePanel_Sitenews, #fb-root, .fb_iframe_widget, #CollapsiblePanel_TagsCloud, #CollapsiblePanel_AnimeRadio, #CollapsiblePanel_NarutoTitle, #CollapsiblePanel_HentaiTitle, #CollapsiblePanel_Chat {display: none;}
  /*Добавляем прозрачность для блоков*/
  #CollapsiblePanel_Profile, #CollapsiblePanel_AnimeRadio, #CollapsiblePanel_TopAnime, #CollapsiblePanel_TagsCloud, #CollapsiblePanel_Sitenews, #CollapsiblePanel_Chat {background-color: black !important; opacity: 0.7;}
  .block, .block-title, .CollapsiblePanel, .CollapsiblePanelOpen, .CollapsiblePanelTab, .CollapsiblePanelContent {}
  /*На весь сайт*/
  h2 {color: black;}
  a {color: white !important;}
  #content {padding-bottom: 0 !important;}
  .navigation a:link, .navigation a:visited, .navigation a:hover, .navigation a:active {background: none !important; background-color: black !important;}
  .navigation span {background-color: white !important; color: black !important;}
  .content-main-block, .content-block {background: none !important; background-color: black !important; opacity: 0.7 !important;}
  .content-main-block {margin-top: 20px !important;}
  .content-block-title h2 {color: white !important;}
  .block-title  {background: none !important; background-color: black !important;}
  .content-block-title {background: none !important; text-align: center !important;}
  .search-block {background: none !important; background-color: black !important; position: absolute !important; right: 0 !important; width: 350px !important; opacity: 0.7 !important; margin-top: -4px !important;z-index: 9999 !important;}
  .search-block-in h2 {display: none !important;}
  .search-block-in {background: none;}
  .s_field {color: white !important; background: none !important; width: 160px !important;}
  .content-block-title h2 {background: none;}
  .block-title h3 {background: none;}
  .block-title h2 {background: none;}
  #cboxdiv {opacity: 1;}
    /*Фиксируем профиль и топ аниме*/
  #CollapsiblePanel_Profile {position: fixed !important;}
  #CollapsiblePanel_TopAnime {position: fixed !important; right: 0 !important; margin-top: 50px !important; width: 390px !important;}
  #CollapsiblePanel_TopAnime:hover, #CollapsiblePanel_Profile:hover, #CollapsiblePanel_AnimeRadio:hover, .search-block:hover, .content-main-block:hover {opacity: 1.0;}
  /*Главное меню*/
  .sub, #nav {background-color: black !important; border: none !important;}
  #nav li ul.sub li a.fly {background: none !important; background-color: black !important;}
  #nav li:hover ul.sub {border: 1px solid black; background-color: black;}
  #nav li a.top_link {color: white;}
  .ctitle {background: none !important; background-color: black !important;}
  #nav li:hover ul.sub li a:hover {background-color: black; border: 1px solid black;}
  #nav li:hover ul.sub li a {background-color: black; border: 1px solid black;}
  #nav li:hover li:hover ul, #nav li:hover li:hover li:hover ul, #nav li:hover li:hover li:hover li:hover ul, #nav li:hover li:hover li:hover li:hover li:hover ul {border: 1px black; background: none !important;}
  #nav {background:none; background-color: black !important;}
  #nav li:hover a.top_link span, #nav li.iehover a.top_link span {background: none; background-color: black !important;}
  #nav li:hover a.top_link, #nav li.iehover a.top_link {background: none !important;}
  #nav li a.top_link {background: none !important;}
  #nav li a.top_link span {background: none !important;}
  #nav li:hover a.top_link span.down, #nav li.iehover a.top_link span.down {background: none !important;}
  #nav li a.top_link span.down {background: none !important;}
  /**/
  div.gsc-control-cse {display: none !important;}
  #lastTorrents {display: none !important;}
  #randomReleaseBlock {display: none !important;}
  .a_block_12, .a_block_63m .a_block_22, .a_block_32, .a_block_42, .a_block_52, .a_block_61, .a_block_63, .a_block_72, .a_block_82  {background: none !important;}
  .a_block_12, .a_block_22, .a_block_61, .a_block_72, .a_block_63, .a_block_82, .a_block_32, .a_block_42, .a_block_52 {display: none !important;}
  .slink, .stext {display: none !important;}
  #dle-content {color: white !important; background: none !important; background-color: black !important; opacity: 0.7 !important;}
  #dle-content table {background-color: black !important;}
  .news {background-color: black !important; color: white !important;}
  .news td {background-color: black !important;}
  .news a {color: white !important;}
  /*Топ аниме*/
  .CollapsiblePanelContent img {width: 50px !important; height: 50px !important; overflow: auto !important;}
  #news_set_sort {display: none !important;}
  /*Спойлеры*/
  .title_spoiler, .text_spoiler {background: none !important;}
  /*Рейтинг*/
  .rating {display: none !important;}
  #nav li.top {position: relative !important; left: 20% !important;}
  .td_t_p {background-color: black !important;}
  .td_t_p:hover {background-color: black !important;}
  .areal_ {color: white !important;}
  .mphotos, .td_t_p, .photoid_ {border: none !important;}
  .accordion h3 {color: black !important;}
  #vk_like {display: none !important;}
  .an_alert {background: none !important; border: none !important;}
  /*Фиксы*/
  div [style="width: 100%; top: 1px; left: 0px; z-index: 3000; position: absolute;"] {z-index: 1 !important;}
  div [style="width:100%; position:absolute; top:1px; left:0px; z-index:3000"] {z-index: 1 !important;}
  .block {background: black !important;}
  .ltitle_small {color: white !important;}
}
