css:
@-moz-document domain(vk.com) {

/* Black smiles and Stickers */
.emoji_block_cont {
    background: #222;
}
.emoji_expanded .emoji_expand_shadow_top,
.emoji_expanded .emoji_expand_shadow{
    background: none !important;
}
.emoji_tabs {
    background: #2f2f2f;
}
.emoji_tab:hover {
    background: #444;
}
.emoji_tabs_r_sc, .emoji_tabs_l_sc {
    background: transparent;
}
.emoji_tabs_l_s,
.emoji_tabs_r_s {
    background: #444;
}
.emoji_tabs_l_s:hover,
.emoji_tabs_r_s:hover {
    background: #444;
}
.emoji_tab_sel, .emoji_tab_sel:hover {
    background: #111;
}
.emoji_shop:hover {
    background: #444;
}
.emoji_pointer {
    background: transparent;
    width: 0;
    height: 0;
    border: 7px transparent solid;
    border-top-color: #2f2f2f;
    bottom: -126px;
}
.emoji_sticker_item:hover,
.emoji_bg{
    background: #444;
}


}
