css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://frame.neptun.bme.hu/hallgatoi/Login.aspx"), url-prefix("https://frame.neptun.bme.hu/hallgatoi/login.aspx") {

  #tableMain{
    background-color: #191919;
    background-position: center; 
    background-image: url("http://wallpoper.com/images/00/35/06/35/anonymous_00350635.jpg");
	  background-repeat:no-repeat;
   	background-attachment: fixed;
  	background-size: cover;
    font-family: Verdana;
  }
   
  
  .formcss table[orgid="hallgato_bme"], .tr_LeftImage td[orgid="hallgato_bme"], .tr_Footer td[orgid="hallgato_bme"]{
    background-image:none;
  }
  
  .login_right_side, .login_content_links, .info_table_center_container_td, .login_line, #lblModuleType, #tdIstitute, .tr_Logo + tr{
    display:none;
  }

  .login_moduletype{
    text-align: center;
    height: 30px;
  }

  #lblServerName{
    color: white;
    text-align: center;
    line-height: 30px;
  }
  
  .login_center{
    width: 500px;
    height: 250px;
    padding: 30px 10px 10px 10px;
   background-color: rgba(31, 31, 31, 0.3);
  }
  
  .login_label{
    color: white;
  }
  
  #btnSubmit{
    background-color: #101010;
    background-image: none;
    border-radius: 5px;
  }
  
  #btnSubmit:hover{
    background-color: #505050;
    cursor: pointer;
  }
 
  
  .tr_Logo td[orgid="hallgato_bme"]{
    background-image: url("http://verychard.com/university/neptun_logo.png");
	  background-repeat:no-repeat;
  
  }
  
}
