/* Firebug Dark Theme Code */
/*** Editor ***/
.cm-s-firebug.CodeMirror {
    background: #222!important;
    color: #a6a6a6!important;
}

.cm-s-firebug.CodeMirror * {
    color: #a6a6a6;
    font-family: "Consolas", "Courier New";
    font-size: 15px;
}

