css:
@-moz-document domain("instasync.com") {
body.dim .navbar-default {
background-image: linear-gradient(to bottom, #64c8ff 0, #2fb5e5 100%);
border-bottom: 1px solid #505050;
}
a {
color: #326496;
text-decoration: none;
}
.navbar-default .navbar-nav>li>a {
color: #fff;
}
.room-container .room-component-row {
box-shadow: 0 6px 12px rgba(0,0,0,0.276);
background-color: #646464;
padding: 0px;
margin: 0px;
}
body.dim .room-container .room-component-row .video-controls .tab-content {
background-color: #646464;
}
.room-container .room-component-row .video-controls .tab-content #tabs_playlist .playlist li.active {
background-color: #505050;
color: #FFFFFF;
}
.room-container .room-component-row .video-controls .tab-content #tabs_playlist .playlist li {
background-color: #c7c7c7;
border-radius: 4px;
border-bottom: 1px solid #b8b8b8;
padding: 4px;
/* font-size: 12px; */
color: #000000;
cursor: pointer;
}
.room-container .room-component-row .chat {
width: 100%;
background-color: #ffffff;
color: #000000
}
body.dim .room-container .room-component-row .video-controls {
-webkit-transition: all 1.5s ease;
-moz-transition: all 1.5s ease;
-o-transition: all 1.5s ease;
transition: all 1.5s ease;
background-color: #c7c7c7;
color: #000000
}
body.dim {
background-image: -webkit-linear-gradient(top,#50c875 0,#50c875 100%);
background-image: -o-linear-gradient(top,#A88FB5 0,#50c875 100%);
background-image: -webkit-gradient(linear,left top,left bottom,from(#A88FB5),to(#50c875));
background-image: linear-gradient(to bottom,#505050 0,#000000 100%);
background-color: #494949;
}
.btn-primary {
background-image: -webkit-linear-gradient(top,#000000 0,#000000 100%);
background-image: -o-linear-gradient(top,#000000 0,#000000 100%);
background-image: -webkit-gradient(linear,left top,left bottom,from(#000000),to(#000000));
background-image: linear-gradient(to bottom,#000000 0,#505050 100%);
border-color: #808080
}
#poll_tab .poll.active, #poll_column .poll.active {
box-shadow: 0 0 8px rgba(0,0,0,0.25);
background-color: white;
background-image: linear-gradient(to bottom,#ffffff 0,#c7c7c7 100%);

}
.text-info {
color: #000000;
}


/* Puts the player more left */
.container{
	margin-left:80px;
}
/* Makes chat bigger*/
.room-component-row .chat .chat-tabs-content{
	height:640px;
    background-color:#646464;
  
}
.room-container .room-component-row .chat .chat-tabs-content .tab-pane{
	height:640px;
    background-color:#646464;
}
/*  Makes the Container(where everything is in) bigger
	Also adds some margin on top (For the fags that love to have player a little bit down)
*/
.room-container{
	width:1800px;
	margin-top:250px;  
}
a{
	color: #000000;
}
/* Info text color*/
.text-info{
	color: #000000;
}
/* Success color */
.text-success{
  color: #00FF04;
}
  /* Greentext */
.room-container .room-component-row .chat #tabs_chat_content .chat-messages .chat-message .greentext{
	color: #B8FF00;
}
/*Chat color*/
.room-container .room-component-row .chat #tabs_chat_content .chat-messages{
	color: whitesmoke;
}
/* Bigger poll (i hope nothing fucks up)*/
.col-sm-4.hidden-xs.column{
	max-height: 660px !important;
}
/*Poll darker*/
.poll.active{
background-image: linear-gradient(to bottom,#646464 0,#646464 100%) !important;
}
.poll-title{
	color: whitesmoke;
}
.poll-text{
	color: whitesmoke;      
}
.poll-votes{
	color: whitesmoke;      
}
/*Bigger playlist*/
.tab-content{
	height: 60px;
}
.playlist{
	height: 600px !important;
}
.title{
	color:whitesmoke;
}
.room-container .room-component-row .video-controls .tab-content #tabs_playlist .playlist li{
	background-color:#646464;
}
.pl-video-info{
	color: whitesmoke;
}
.buttons{
	color:whitesmoke;
}
.buttons a{
 	color:whitesmoke;
}
.playlist-stats{
   	color:whitesmoke !important;
}            
/*Footer*/
.room-container .room-info-row{
	background:#646464 !important;
	color:whitesmoke !important;
}

.room-stats{
  	color:whitesmoke !important;
}
}
