@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("lastfm.ru") {
  /*Ко всему сайту*/
  body {background: url(http://ya-vidoc3000.narod.ru/fon/5.jpg) no-repeat center center fixed !important; -webkit-background-size: cover !important; background-size: cover !important;}
  a {color: black !important;}
  a:hover {opacity: 0.7 !important; color: black !important; text-decoration: none !important;}
  /*Шапка сайта*/
  .masthead {background: none !important; border: none !important; box-shadow: none !important; background-color: #FFE4E1 !important;}
  .btn.btn--header {box-shadow: none !important; background: none !important; border: none !important;}
  .notification-pill--badge:hover + .btn--header, .notification-pill--badge:focus + .btn--header, .btn--header:hover, .btn--header:focus {background: none !important;}
  .user-dropdown {background: none !important; border: none !important; background-color: #FFE4E1 !important; box-shadow: none !important;}
  .user-dropdown-divider-top,.user-dropdown-divider-bottom {border: none !important;}
  .user-badge-avatar {border: none !important;}
  .primary-nav-link:hover, .primary-nav-link:focus {background-color: white !important; opacity: 0.7 !important; border-radius: 20px !important;}
  /*Поиск в шапке*/
  .lfmRed .site-search .search-box {background: none !important; border: 1px solid black !important; box-shadow: none !important; background-color: white !important; color: black !important;}
  .dark-box--columns {background: none !important; background-color: #FF1493 !important; color: black !important;}
  /*Контент*/
  body[style*="background-image"] #content, .skin-active #content {box-shadow: none !important;}
  /*Главная страница*/
  .content {background-color: #FFE4E1 !important;}
  .memo--grey, .memo-grey {background-color: #FAF0E6 !important;}
  /**/
  #content {background: none !important; border: none !important; background-color: #FFE4E1 !important;}
  .fiflufi .page-head.separated ~ .leftCol, .fiflufi .page-head.separated ~ .rightCol {padding-top: 0 !important;}
  .fiflufi .leftCol {background-color: #FFE4E1 !important; background: none !important;}
  .fiflufi .rightCol {background-color: #FFE4E1 !important;}
  .fiflufi, #page {background-color: #FFE4E1 !important;}
  /*Трек лист*/
  #recentTracks tr:hover {background-color: white !important;}
  /*О пользователе*/
  #userBadge {}
  .badgeAvatar {background: none !important; box-shadow: none !important;}
  .photo {border-radius: 30px; border: 3px white; box-shadow: none !important;}
  /*Аватарки юзеров*/
  .userImage {}
  /*Кнопки*/
  .stationbuttonMedium, .stationbuttonMedium span.stationButtonWrapper {background: none !important; background-color: red !important; border-radius: 20px !important; opacity: 1 !important;}
  .stationbuttonMedium:hover, .stationbuttonMedium span.stationButtonWrapper:hover {background-color: red !important; text-decoration: none !important; opacity: 1 !important;}
  .btn {background: none !important; box-shadow: none !important;}
  /*Лучшие исполнители*/
  table.chart tr.odd td {background-color: #FFDAB9 !important;}
  /*Бесплатные треки*/
  table.tracklist tr.odd {background: none !important;}
  .theme-whittle table.tracklist, .theme-whittle table.chart {font-size: 10px !important;}
  .module-body {background: #FFE4E1 !important;}
  .fdl:hover {background: none !important;}
  /*Подвал*/
  .site-footer {background: none !important; background-color: white !important; opacity: 0.7 !important;}
  /**/
  .recommended {background-color: #FFE4E1 !important;}
  #recs h1.heading {background: none !important;}
  .rankItem {padding-bottom: 0 !important; padding-top: 0 !important; border: none !important;}
  .rankedChart.s-compact .rankItem.first .rankItem-wrap2 {overflow: visible !important;}
  .rankedChart.hyped .rankItem-position, .rankedChart.hyped .rankItem-bar-percentage span {background-color: #FF4500 !important;}
  .rankItem-wrap:hover, .rankItem-wrap2:hover {background: none !important; background-color: #FF4500 !important;}
  a.tag, a.tag span {background: none !important; background-color: pink !important;}
  .tags a, .tag a {background-color: pink !important;}
  .btn--primary {text-shadow: none !important;}
  .btn--primary:hover {background-color: pink !important;}
  /*Плеер*/
  #radioFooter, .webRadioHeader, #radioTrackMeta {background-color: pink !important; background: none !important; border: none !important; text-shadow: none !important;}
  #webRadio .starterContent, .stationSection, #webRadio .stationSection h3 {background: none !important; box-shadow: none !important; color: black !important;}
  /*Реклама*/
  .LastAd {display: none !important; visibility: hidden !important;}
  .text-over-image-text, .artist-grid--logged-in-home h3, .artist-similar-to, .artist-similar-to a, .artist-similar-to a:hover {color: white !important;}
  .filterTags li a.selected span, .filterTags li a.selected {background: none !important; background-color: pink !important;}
  #recs #artistRecs div.details {margin: 0 !important;}
  /**/
  .shouts .message {margin: 0 !important; padding-bottom: 0 !important;}
  .message {}
  .shouts .message p {background-color: white !important;}
}
