css:
@-moz-document domain("sig.aquarium-software.com") {
.pageTitle {
	background-color:#e7e7e7 !important;
    color:#d11241 !important;
}
.pageSubtitle,
.sectionTitle {
	background-color:#DAD8D8 !important;
    color:#d11241 !important;
    font-weight:normal !important;
}
.sectionSubtitle {
	background-color: #EAEAEA !important;
    color:#d11241 !important;
}
.sectionSubtitle a,
.sectionSubtitle a:active, 
.sectionSubtitle a:visited {
    color:#d11241 !important;
}
.pageIntroText {
      background:#f2f2f2 !important;
}
.leadNav {
	background-color:#e7e7e7 !important;
    color:#d11241 !important;
    -webkit-border-bottom-left-radius: 20px !important;
	-moz-border-radius-bottomleft: 20px !important;
	border-bottom-left-radius: 20px !important;
}
.menuHeadCollapsed {
    background:#f2f2f2 !important;
    color:#4e4e4e !important;
	border-right:0 !important;
}
.menuHeadExpanded,
.menuHeadSelected {
      background:#1558A4 !important;
    }
.menuHeadHover {
	background:#4E78A8 !important;
	border-right:0 !important;
}
.menuItemCollapsed {
    background:#e7e7e7 !important;
}
.button {
        background-color: #1558a4 !important;
        color:#fff !important;
        -webkit-border-radius: 15px !important;
		-moz-border-radius: 15px !important;
		border-radius: 15px !important;
        padding:2px 10px !important;
        border:0 !important;
}
.button:hover {
	color:#ccc !important;
}
input.button {
        border:none !important;
        height:20px !important;
}
.leadNav .button {
	padding: 2px 10px !important;
}
.MasterTable_Default {
	border: 0px !important;
	border-collapse: separate !important;
	font: 11px tahoma !important;
	color: #000000 !important;
	/* -moz-user-select: none; */
}
.MasterTable_Default thead th, .grid thead th {
    text-align: left !important;
	font-weight: bold !important;
	padding: 10px !important;
	background-image: none !important;
	background-color: transparent !important;
	border: 0;
}
.MasterTable_Default thead, .grid thead {
      background-color: #C8C8C8 !important;
}
.MasterTable_Default tbody td, .grid tbody td {
    text-align: left !important;
	font-weight: normal !important;
	padding: 10px !important;
	border: 0;
}
.RadGrid_Default {
  width:100% !important;
}
.blockButton, .blockButtonText {
      background-color: #e1e1e1 !important;
	-webkit-border-radius: 15px !important;
	-moz-border-radius: 15px !important;
	border-radius: 15px !important;
	padding: 5px !important;
    margin: 10px !important;
	margin-top: 20px !important;
}
#ctl00_cphBodyWithoutUpdatePanel_cphBody_ucEventHistoryList_rgEventHistory_GridData {
  height:210px !important;
}
