css:
@-moz-document domain("player.me") {

/* -- Old Theme -- */

div.card,
div.card-dark,
div.post--container,
div.new-post--container,
div.new-post--container .loading {
  background-color: #FFFFFF !important;
  box-shadow: 0px 1px 6px 0px rgba(17,18,21,.4) !important;
}

div.new-post--container .loading { box-shadow: none !important; border-radius: 4px; }

textarea.comment--writer.real,
pre.comment--writer.mirror,
div.loading.comment--writer.real,
div.loading.comment--writer.mirror,
div.filter-bar,
textarea {
  background: #FFFFFF !important;
}

div.new-post--container .mentions-container,
div.mentions-container .comment--writer {
  background: #FFFFFF !important;
}

button.post--show-more-content {
  background-color: #FBFBFB;
}

div.post--content,
div.post--content-wrapper,
div.post--content-container,
div.comments--show-all-container {
  background-color: #F7F7F9;
}

div.post--content-checkin-container, div.post--meta { background-color: #EBEBF2; }

div.comment--container { border-color: #F7F7F9; }

div.feed-sidebar .stats,
div.feed-sidebar .stats button:first-child,
div.feed-sidebar .online-users li,
div.feed-sidebar .suggestion li,
div.stream-entity .game-name,
div.new-post.card .writer-container,
textarea.comment--writer.real,
pre.comment--writer.mirror,
div.comment--writer-container {
  border-color: #E1E1E1 !important;
}

div.comment--writer-container { border-bottom: none; }

div.new-post--container .mentions-container { border: none; border-bottom: 1px solid #E1E1E1; border-radius: 0px; }

div.new-post--container .comment--writer { border-radius: 0px; }

ul.heading-tabs li a { color: #FFFFFF; }

div.action--count,
div.timeago--entity,
div.post--source-label,
button.post--show-more-content,
div.post--video-container {
  color: #919BAF;
}

div.post--content,
div.checkin--description,
div.post--meta-description {
  color: #7C818B;
}

*.username,
ul.heading-tabs a,
div.feed-sidebar .profile .username,
div.feed-sidebar .online-users .title,
div.feed-sidebar .suggestion .title,
div.feed-sidebar .stats button strong,
a.post--info-username,
div.post--content-container,
div.comment--content,
textarea.comment--writer.real,
pre.comment--writer.mirror,
textarea {
  color: #333333 !important;
}

/* -- New Theme -- */

div#page-feed.theme--dark, div.page-root.dark, div#wrap { background-color: #EBEBF2 !important; }

@media (min-width: 1440px) { div.top-bar--container .container { width: 1400px; } }

ul.heading-tabs li a:hover { background-color: rgba(26,219,227,.5) !important; color: #FFFFFF !important; }

input.navbar--search-input { background-color: rgba(255,255,255,.15); }

div.feed-filters--container { top: -50px; border-top: 50px solid #FFFFFF; margin-bottom: -40px !important; }

div.post--container.post--source-playerme { border-left: 3px solid #00C1C9; }

div.post--container.post--source-youtube { border-left: 3px solid #CD332D; }

div.post--container.post--source-twitch { border-left: 3px solid #6441A5; }

/* -- Dropdowns -- */

div.select3-dropdown { border-radius: 4px; color: #333333; }

div.select3-result-item.highlight { color: #333333; }

div.select3-error { color: #7C818B; }

div.select3-dropdown,
div.select3-search-input-container,
div.messaging--rename-container,
ul.dropdown-menu.dropdown--dark,
div.post--container .dropdown-menu {
  background-color: #FFFFFF;
  background-image: none;
  border-color: #C9C9C9;
  box-shadow: none;
}

div.messaging--rename-container { border: none; }

ul.dropdown-menu.dropdown--dark .divider,
div.post--container .dropdown-menu .divider {
  background-color: #EBEBF2;
}

ul.dropdown-menu.dropdown--dark a,
ul.dropdown-menu.dropdown--dark a:hover,
div.post--container .dropdown-menu a,
div.post--container .dropdown-menu a:hover {
  color: #333333;
}

div.mentions {
  background-color: #FFFFFF;
  background-image: none;
  border-color: #C9C9C9;
  box-shadow: none;
}

div.mentions ul li a strong { color: #333333; }

/* -- Notifications -- */

ul.top-bar--notifications { border-color: #C9C9C9; box-shadow: none; }

ul.notifications--container { background-color: #FFFFFF; background-image: none; }

li.notifications--toolbar { background-color: #F7F7F9; border-bottom: 2px solid #E1E1E1; box-shadow: none; }

span.notification--senders { color: #00C1C9; }

a.notification-image img { border-color: #E1E1E1; }

/* -- Popovers -- */

div.qtip,
div.qtip .qtip-tip {
  background: #FFFFFF;
  border: 2px solid #C9C9C9;
  color: #7C818B;
  text-shadow: 0px 1px #E1E1E1;
}

div.users-popovers a,
div.users-popovers .username {
  color: #333333;
}

div.users-popovers .bottom,
div.users-popovers .middle {
  border-color: #E1E1E1;
}

div.users-popovers .description { color: #7C818B; }

div.users-popovers .avatar.is-online { box-shadow: 0px 0px 15px 2px #66FF66; }

/* -- Modals -- */

div.modal.modal-dark { background-color: rgba(38,43,53,.6); }

div.modal.modal-dark .loading,
div.modal.modal-dark .modal-content {
  background: #FFFFFF;
  color: #333333;
}

div.modal.modal-dark .close { right: -1px; border-bottom-left-radius: 4px; box-shadow: none; }

div.modal.modal-dark input, .modal.modal-dark textarea { color: #333333; }

div.user-list .user a { color: #333333; }

div.mfp-bg { background-color: #262B35; }

li.tag--edit-tag, button.tag--new-button, div.tag--new-button-container { background-color: #F7F7F9; }

button.tag--edit-edit, button.tag--edit-remove { background-color: #EBEBF2; }

/* -- Chat Windows -- */

div.messaging--container, div.messaging--container.minimized { border-top-left-radius: 4px; border-top-right-radius: 4px; box-shadow: 0px 1px 6px 0px rgba(17,18,21,.4); }

div.messaging--header { background-color: #F7F7F9; box-shadow: none; border-top-left-radius: 4px; border-top-right-radius: 4px; border-bottom: 1px solid #C9C9C9; }

div.messaging--has-unread .messaging--header { background: #1ADBE3; box-shadow: none; }

a.messaging--title { color: #333333 !important; }

div.messaging--messages { background: #FFFFFF; box-shadow: none; }

div.messaging--inner-messages { outline: none; }

div.messaging--line.messaging--direction-sent { background: #F7F7F9; }

img.messaging--avatar { border-color: #E1E1E1; }

div.messaging--text { color: #7C818B; word-wrap: break-word; margin-right: 8px; max-width: calc(100% - 46px); line-height: 17px; }

div.messaging--input { border-top: 1px solid #C9C9C9; }

/* -- Browse & Profiles -- */

div#page-profile2,
div#page-browsev2 {
  background-color: #EBEBF2;
}

div.container-fluid { padding-left: 0px; padding-right: 0px; }

div.profile--header-content { width: 100%; }

a.profile--section-count-container { background-color: #FFFFFF; color: #333333; border: 2px solid #C9C9C9; }

a.profile--game-link,
a.profile--media-unit-link {
  box-shadow: 0px 1px 6px 0px rgba(17,18,21,.6);
  border-radius: 4px;
  border: none;
}

a.profile--game-link img,
a.profile--media-unit-link img,
div.profile--media-unit-overlay {
  border-radius: 4px;
}

div.toolbar--search-container { background-color: #FFFFFF; }

div.toolbar--filter label { background-color: #FFFFFF; border-width: 2px !important; font-weight: 500; line-height: 20px; }

div.player--card,
div.profile--long-description-container,
div.browse--card-user-entity {
  background: #FFFFFF;
  box-shadow: 0px 1px 6px 0px rgba(17,18,21,.6);
  color: #7C818B;
}

div.browse--card-container { border-radius: 4px; }

a.player--card-username,
p.profile--description-content,
h2.profile--section-title,
div.browse--card-user-entity .browse--card-title {
  color: #333333;
}

div.profile--section-units-container-empty,
div.browse--card-user-entity .browse--card-description {
  color: #7C818B;
}

img.player--card-avatar,
div.browse--card-user-entity .browse--card-image img {
  border-color: #E1E1E1;
}

/* -- FAQ, Changelog, Feature Vote, etc. -- */

div.feature--details,
div.feature--count {
  background: #FFFFFF;
  box-shadow: 0px 1px 6px 0px rgba(17,18,21,.6);
  color: #7C818B;
}

div#page-votes h1,
div#page-votes .feature--name,
div#page-faq h1,
div#page-faq h2,
div#page-faq table th,
div#page-faq .faq--question,
div#page-faq .spoiler--warning,
div#page-about .second h2,
div#page-about .second h3,
div#page-about .fourth h2,
div#page-contact .contact-form h1,
div#page-changelog h1,
div#page-changelog h2,
div#page-changelog li {
  color: #333333 !important;
}

div#page-votes p,
div#page-faq p,
div#page-faq table td,
div#page-faq .faq--answer,
div#page-about .second p,
div#page-changelog p {
  color: #7C818B !important;
}

div.formatting--guide table { width: 75%; }

div.formatting--guide table th, div.formatting--guide table td { border-color: #C9C9C9; }

div.formatting--guide table th { text-align: center; }

div.feature--overlay { top: -2px; left: -2px; width: calc(100% + 4px); height: calc(100% + 4px); }

form.js-feature-new h4, form.js-feature-new label { color: #333333; }

*.form-control { background: #FFFFFF; color: #333333 !important; }

div#page-contact label { color: #333333; }

/* -- Settings -- */

div#page-user-settings { background-color: #EBEBF2; }

div#page-user-settings .form-control { background: #FFFFFF; color: #333333; }

div#page-user-settings h3,
div#page-user-settings h4,
div#page-user-settings label {
  color: #333333;
}

div#page-user-settings .help-block,
div#page-user-settings .text-center,
div#page-user-settings p {
  color: #7C818B;
}

div#page-user-settings .form-control-static,
div#page-user-settings li {
  color: #919BAF;
}

div#page-user-settings .nav li a { color: #333333; }

div#page-user-settings .nav li a:hover { color: #00C1C9; }

div#page-user-settings .nav li.active a { color: #FFFFFF !important; }

/* -- Dedicated Messages -- */

div.messaging-page--toolbar-wrapper { background-color: #FFFFFF; border-bottom: 2px solid #E1E1E1; box-shadow: none; }

div.messaging-page--wrapper>.container-fluid { box-shadow: 0px 1px 6px 0px rgba(17,18,21,.4); }

div.messaging-page--selector-wrapper { background-color: #FFFFFF; background-image: none; box-shadow: none; border-right: 2px solid #E1E1E1; max-width: 67px; transition: all 500ms; }

div.messaging-page--selector-wrapper .notification--info { visibility: hidden; transition: all 100ms; }

div.messaging-page--selector-wrapper:hover .notification--info { visibility: visible; }

div.messaging-page--selector-wrapper:hover { max-width: 200px; }

@media (min-width: 768px) { div.messaging-page--selector-wrapper:hover { max-width: 250px; } }

@media (min-width: 992px) { div.messaging-page--selector-wrapper:hover { max-width: 300px; } }

@media (min-width: 1200px) { div.messaging-page--selector-wrapper:hover { max-width: 350px; } }

div.messaging-page--selector-wrapper li { height: 75px; }

div.toolbar--selector--wrapper { max-width: 240px; }

div.toolbar--selector--wrapper .flex--spacer { display: none; }

div.messaging-page--messages-wrapper { box-shadow: none; }

div.toolbar--group-name { color: #333333; }

div#page-user-messaging .messaging--player-link:after { content: attr(data-username); position: relative; top: 1px; margin-left: 8px; font-size: 13px; }

/* -- I HATE CHROME'S BLUE OUTLINES! -- */

div:focus, ul:focus, img:focus, a:focus { outline: none; }

/* -- Colorize Player.me Staff -- */

a[href="/sean"].browse--card-title,
a[href="/sean"].js-user-popover,
a[href="/sean"].post--info-username,
a[href="/sean"] .username,
div.username a[href="/sean"],
span.comment--username a[href="/sean"] {
  color: #BC5EBE !important;
}

a[href="/maktouch"].browse--card-title,
a[href="/maktouch"].js-user-popover,
a[href="/maktouch"].post--info-username,
a[href="/maktouch"] .username,
div.username a[href="/maktouch"],
span.comment--username a[href="/maktouch"] {
  color: #BC5EBE !important;
}

a[href="/katie"].browse--card-title,
a[href="/katie"].js-user-popover,
a[href="/katie"].post--info-username,
a[href="/katie"] .username,
div.username a[href="/katie"],
span.comment--username a[href="/katie"] {
  color: #BC5EBE !important;
}

a[href="/jamies"].browse--card-title,
a[href="/jamies"].js-user-popover,
a[href="/jamies"].post--info-username,
a[href="/jamies"] .username,
div.username a[href="/jamies"],
span.comment--username a[href="/jamies"] {
  color: #BC5EBE !important;
}

/* -- Roar! -- */

a[href="/taiga"].browse--card-title,
a[href="/taiga"].js-user-popover,
a[href="/taiga"].post--info-username,
a[href="/taiga"] .username,
div.username a[href="/taiga"],
span.comment--username a[href="/taiga"] {
  color: #FFBF5E !important;
}

/* -- Nanoscroller is fix? -- */

*.nano>.nano-pane { background: rgba(0,0,0,.15); }

*.nano>.nano-pane>.nano-slider { background: rgba(0,0,0,.2); }

/* -- Scrollbars (Chrome only. Suck it Firefox users!) -- */

::-webkit-scrollbar { width: 8px; }

::-webkit-scrollbar-track { background-color: #C9C9C9; }

::-webkit-scrollbar-thumb { background-color: #A0A0A0; border-radius: 3px; }

}
