css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("rustguide.info") {
#header {
    background: none repeat scroll 0% 0% #333;
}
nav li a:link, nav li a:visited {
    color: #DDD;
    display: block;
    font-family: "Stardos Stencil","Times New Roman","Times","Georgia",serif;
    font-weight: bold;
    height: 2em;
    line-height: 2em;
    padding: 0px 0.5em;
    text-decoration: none;
}
#footer {
    background: none repeat scroll 0% 0% #333;
}
body, table, form, input, textarea, select, header, nav, main, #footer {
    color: #DDD;
    font-family: "Questrial","Helvetica Neue","Helvetica","Arial","Verdana",sans-serif;
    font-size: 100%;
    line-height: 1.125em;
}
#footer li a:link, #footer li a:visited {
    color: #DDD;
    display: block;
    font-size: 87.5%;
    line-height: 1.2857em;
    text-decoration: none;
}
.copy {
    clear: both;
    color: #DDD;
    font-size: 62.5%;
    line-height: 1.8em;
}
h1, h2, h3, h4, h5, h6 {
    background: none repeat scroll 0% 0% #333;
    clear: both;
    font-family: "Stardos Stencil","Times New Roman","Times","Georgia",serif;
    font-weight: bold;
}
.catbox h1, .catbox h2 {
    margin: 0px;
}
.catbox > a {
    color: #DDD;
}
html {
    background: none repeat scroll 0% 0% #282B2B;
    background-image: url('http://playrust.com/img/bg.png');
}
#sidebar .itembox > div {
    background: none repeat scroll 0% 0% #333;
    float: none;
    margin: 0px;
    padding: 0px 0.6429em;
}
#sidebar .itembox h3 a {
    color: #DDD;
}
#sidebar .itembox h3 {
    background: none repeat scroll 0% 0% #333;
}
#logo {
    background: url("http://i.imgur.com/M3giXyQ.png") no-repeat scroll 0% 0% transparent;
    display: block;
    float: left;
    height: 5.625em;
    width: 5.625em;
    z-index: 20;
}
#logotype {
    background: url("http://i.imgur.com/LujBEj9.png") no-repeat scroll 0% 0% transparent;
    float: left;
    height: 3.875em;
    left: 0px;
    position: relative;
    top: 0.875em;
    width: 16.375em;
}
#logo:hover {
    background: url("http://i.imgur.com/iSjnRYN.png") no-repeat scroll 0% 0% transparent;
}
.nav_page {
    background: none repeat scroll 0% 0% #333;
    font-family: "Stardos Stencil","Times New Roman","Times","Georgia",serif;
    font-size: 100%;
    font-weight: bold;
    height: 1.125em;
    line-height: 1.125em;
    list-style: outside none none;
    margin: -0.5625em 0px 0px;
}
.list_items a:link, .list_items a:visited {
    color: #DDD;
    text-decoration: none;
}
.list_items li:hover {
    background: none repeat scroll 0% 0% #CE422B;
}
a:hover {
    color: #DDD;
}
.itembox > div {
    background: none repeat scroll 0% 0% #333;
    float: left;
    font-size: 0.875em;
    margin: 0px 0px 0.6429em 0.6429em;
    padding: 0.6429em;
    position: relative;
    width: 20.1429em;
}
.itembox li:hover {
    background: none repeat scroll 0% 0% #CE422B;
}
.itembox button {
    background: none repeat scroll 0% 0% #1D1F1F;
    bottom: -0.75em;
    border: 0px none;
    color: #DDD;
    font-size: 0.8571em;
    left: -0.75em;
    line-height: 1.5em;
    padding-left: 8.25em;
    position: relative;
    text-align: left;
    width: 25em;
}
:target > h3 {
    background: none repeat scroll 0% 0% #CE422B;
    color: #FFF;
}
.nav_page li a:link, .nav_page li a:visited {
    color: #CE422B;
    display: inline-block;
    height: 1.2857em;
    padding: 0px;
    text-decoration: none;
}
.notice {
    background: none repeat scroll 0% 0% #CE422B;
    margin-bottom: 0.5625em;
}
.list_notes > li > ul > li:hover {
    background: none repeat scroll 0% 0% #CE422B;
}
a:link {
    color: #8AB22F;
}
a:visited {
	color: #709027;
}
.nav button {
    background: url("http://i.imgur.com/zpqLA5n.png") no-repeat scroll 50% 50% transparent;
}
article, aside, details, figcaption, figure, footer, header, hgroup, main, nav, section, summary {
    display: block;
}
body {
    background: none repeat scroll 0% 0% #4E4E4E;
}

}
