css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.aywas.com") {

body {
    padding: 10px 0px 0px;
}    
    
#page-header {
    width: 1200px;
}

div.page-nav{
    margin: 0px;
}

div#container {
    margin: 0px auto;
    position: relative;
    width: 1200px;
}

#avt-content {
    overflow: hidden;
    background-repeat: repeat-x;
    border-radius: 20px;
    float: right;
    height: 500px;
}

#avt-dialogue {
    background-color: #FFF;
    border-radius: 20px;
    border: 2px solid #44B7FF;
    text-align: center;
    margin-top: auto;
    float: left;
    Width: 500px;
}

#avt-stage {
    width: 400px;
    height: 400px;
    float: right;
    position: relative;
    box-shadow: 2px 2px 10px #000;
    border-radius: 20px;
    margin-right: 10px;
}
    

}
