css:
/*Автор: Vidoc3000*/
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("userstyles.org") {
  body {background: url(http://ya-vidoc3000.narod.ru/fon/11.jpg) fixed; -webkit-background-size: cover; background-size: cover; font-size: 13px !important; max-width: 100% !important;}
  a {color: black !important;}
  a:hover {color: black !important;}
/*Заголовок*/
  #main-header {background: none !important; padding-top: 20px; color: red !important;}
  #main-header a {background-color: white !important; opacity: 0.7 !important;}
  #Head {background: none !important; color: black !important;}
  #Head a {color: black !important; font-size: 15px !important;}
  #Head a:hover {color: black !important; background: none !important; text-decoration: none !important;}
  .SiteTitle {background: none !important; background-color: white !important;}
  .Row {width: 100% !important;}
  /*Контент сайта*/
  #content-wrapper {}
  #Content {margin-right: 200px !important;}
  #main-article {background: none !important; background: white !important; color: black !important; margin-left: 200px !important; opacity: 0.7 !important; margin-right: 200px !important; padding-left: 20px !important; padding-right: 10px !important;}
  /*Убираем лишнее*/
  .ad, #social {display: none !important;}
  /*Меню сайта*/
  #left-sidebar > * {background: none !important; position: fixed !important; color: black !important; background-color: white !important; opacity: 0.7 !important;}
  #left-sidebar > *:hover {opacity: 1;}
  /*Таблица со стилями пользователя*/
  ul, dl, h2, p, h3 {text-shadow: none !important; font-size:20px; color: black !important; background-color: white !important; opacity: 0.7 !important;}
  #Panel {padding-top: 20px;}
  .style-brief-text {background-color: white !important; opacity: 0.7;}
  #additional-info-text, #info-tables {background-color: white !important; opacity: 0.7; max-height: 50em;}
  #Content {opacity: 0.7 !important; color: black !important; background-color: white !important;}
  footer {float: right !important;}
  footer a {position: relative !important;}
  footer a:hover {}
  .style-brief-text a {color: red;}
  .Discussion:hover, .ItemContent:hover, .DataList:hover {opacity: 1;}
  #Content {font-size: 15px;}
  #Content:hover {opacity: 1;}
  .Item.Read {background-color: white !important;}
  /*какой-то мусор*/
  #related {display: none !important;}
  /**/
  .PageControls {background-color: white !important; opacity: 0.7 !important; text-align: center !important;}
  .HomepageTitle {color: white !important; background-color: white !important;}
  #long-description, #control-panel {background-color: white !important; opacity: 0.7 !important; color: black !important;}
  .author-styles {border: none !important; color: black !important; font-size: 20px !important;}
  .author-styles table {border: none !important; width: 100% !important;}
  .author-styles thead {background-color: white !important;}
  .author-styles tr {background-color: white !important; border: none !important;}
  .author-styles tr:hover {background-color: white !important; border: none !important; color: black !important;}
  /*Кноака на форуме*/
  .Button {background: none !important; border: none !important; text-shadow: none !important; box-shadow: none !important; background-color: white !important;}
  /*Меню на форуме*/
  .UserBox .WhoIs, .MeBox .WhoIs {background-color: white !important; opacity: 0.7 !important;}
  .MenuItems {background-color: white !important;}
  .MenuItems a, .MenuItems a:link, .MenuItems a:visited, .MenuItems a:active {color: black !important;}
  .MenuItems a:hover {background-color: white !important; color: black !important;}
  .FlyoutMenu {background: none !important; background-color: white !important;}
  .HasNew {background-color: white !important;}
  .Discussion:hover, .MessageList:hover, .Message:hover {opacity: 1 !important;}
  code, pre {background-color: white !important;}
  body > #Frame > .Head {padding: 0 !important;}
  table.form-controls, .form-controls > tbody > tr > td, textarea {width: 98% !important;}
  /*Для проверки*/
  @media(max-width:1200px){#left-sidebar{float:none !important; width:auto !important; margin-left:5px !important;	margin-top:1em !important}}
}
