css:
@-moz-document url-prefix("http://craftsite.pl/forum/") {
.unread {
 	background-color: #1F282A;
}
.ipsVerticalTabbed_tabs li a:hover {
    background: #5D7D81;
    color: #FFFFFF;
    transition: 0.5;
}
.ipsUserPhoto {
	padding: 1px;
	border: 1px solid #000000;
	background: #838383;
}
#inbox_link.menu_active {
    background-position: bottom;
    background-color: #6CB2B2}
    ul.post_controls {
    margin: 0px;
}
#user_link_menucontent #links li {
    width: 50%;
    float: left;
    margin: 0px;
    text-shadow: 0px 1px 0 rgba(0, 0, 0, 1);
    white-space: nowrap;
}
.ipsHeaderMenu {
    background: #FFF;
    background: -moz-linear-gradient(top, #fff 0%, #f6f6f6 70%, #ededed 100%);
    background: rgb(26, 26, 26);
}
.post_block.feature_box {
    background-color: #293223;
    border: 1px dotted #333;
    padding: 6px;
    min-height: 60px;
    word-wrap: break-word;
}
    .userpopup h3, .userpopup .side+div {
    padding-left: 110px;
    background-color: rgb(31, 31, 31);
}
    .ipsLayout_smallleft.ipsLayout_withleft {
    padding-left: 150px;
    background-color: rgb(26, 26, 26);
}
    #community_app_menu>li>a:hover, #community_app_menu>li>a.menu_active {
    background-color: rgb(18, 69, 66);
    background-position-y: 1px;
    background-position-x: 0;
    color: #FFF;
    background-image: none;
}
.ipsVerticalTabbed_tabs li {
    background: #2C2C2C;
    color: #808080;
    border-bottom: 1px solid #545454;
    font-size: 13px;
}
.ipsVerticalTabbed_tabs li.active a {
    width: 132px;
    position: relative;
    z-index: 8000;
    background: #263638;
    color: #E7E7E7;
    font-weight: bold;
    border: none;
}
#profile_background {
	background: rgb(26, 26, 26) !important;
    background-image: none !important;
}
p.citation {
    font-size: 12px;
    padding: 8px 10px;
    border-left: 2px solid #000000;
    background: rgb(20, 26, 27);
    border-top: 2px solid #000000;
    border-right: 2px solid #000000;
    -moz-border-radius: 5px 5px 0 0;
    -webkit-border-radius: 5px 5px 0 0;
    border-radius: 5px 5px 0 0;
    font-weight: bold;
    overflow-x: auto;}
  .post_body .post {
    color: #BABABA;
}
.ipsList_inline>li:first-child {
	margin-left: 10px;
}
.post_block h3 {
    background: #1F282A;
    padding: 0 10px;
    height: 36px;
    line-height: 36px;
    font-weight: normal;
    font-size: 16px;
}
blockquote.ipsBlockquote {
    font-size: 12px;
    padding: 10px;
    border: 2px solid #000000;
    border-left: 2px solid #000000;
    -moz-border-radius: 5px;
    -webkit-border-radius: 5px;
    border-radius: 5px;
    background: #303030;
    margin: 0 0;
    overflow-x: auto;
}
.ipsVerticalTabbed_tabs>ul {
    width: 149px !important;
    margin-top: 10px;
    border-top: 1px solid #000000;
    border-left: 1px solid #000000;
}
.general_box h3 {
    font: normal 14px helvetica, arial, sans-serif;
    padding: 8px 10px;
    background: #263638;
    color: #CBCBCB;
}
.general_box {
    background: #2F2F2F;
    margin-bottom: 10px;
}
#content {
    background: rgba(39, 39, 39, 0.78) !important;
    box-shadow: 0 5px 9px rgba(0, 0, 0, 0.79);
    -moz-box-shadow: 0 5px 9px white;
	box-shadow: 0 5px 9px rgba(0,0,0,0.1);
    max-width: 87%;
    min-width: 980px;
    margin-left: auto;
    margin-right: auto;
}
.maintitle {
    background-color: rgba(0, 0, 0, 0.66);
  	background-image: none;
    background-repeat-x: repeat;
    background-repeat-y: no-repeat;
    background-position: left top;
    color: #fff;
    font-size: 16px;
    font-weight: bold;
    -moz-border-radius: 4px 4px 0 0;
    -webkit-border-top-left-radius: 4px;
    -webkit-border-top-right-radius: 4px;
    border-radius: 4px 4px 0 0;
    -webkit-box-shadow: none;
    -moz-box-shadow: none;
    box-shadow: none;
    border-width: 0;
    border-color: transparent;
  	border-style: none;
}
.ipsBox {
  background: rgba(0, 0, 0, 0.32);
}
.ipsType_pagetitle, .ipsType_subtitle {
	font: 300 26px/1.3 Helvetica,Arial,sans-serif;
	color: #D0D0D0;
}
.post_wrap {
	top: 0px;
	background-color: rgb(32, 32, 32);
}
.statistics {
    margin: 20px 0 0 0;
    padding: 10px 0;
    border-top: 3px solid #626262;
    line-height: 1.3;
    overflow: hidden;
}
.ipsSideBlock h3 {
    font: normal 14px helvetica, arial, sans-serif;
    color: #FFF;
    padding: 5px 10px;
    background-color: rgba(0, 0, 0, 0.85);
    background-image: none;
    margin: -10px -10px 10px;
    text-shadow: 1px 1px 1px rgb(0,0,0);
}
#primary_nav {
    background-color: rgba(0, 0, 0, 0.91);
    background-image: none;
    background-repeat: no-repeat, no-repeat, repeat-x;
    background-repeat-y: no-repeat;
    background-position: right top, left top, left top;
    font-size: 13px;
    height: 51px;
    width: 87%;
    min-width: 960px;
    margin: 30px auto 0 auto;
    padding: 0 10px;
    position: relative;
    left: auto;
    right: auto;
}
#user_navigation ul.ipsList_inline {
    background-color: rgba(0, 0, 0, 0.8);
    background-image: none;
    background-repeat: no-repeat, no-repeat, repeat-x;
    background-position: top left, top right, top left;
    box-shadow: 2px 4px 7px -4px #000;
    padding: 0 25px 0 20px;
	border-radius: 0 0 5px 5px;
}
#admin_bar{
    background-color: rgba(0, 0, 0, 0.8);
    background-image: none;
    background-repeat: no-repeat, no-repeat, repeat-x;
    background-position: top left, top right, top left;
    box-shadow: 2px 4px 7px -4px #000;
    padding: 0 25px 0 20px;
	border-radius: 0 0 5px 5px;
}
#li.active a {
	color: #FF0000;
 	font-weight: 300;
}
.a:hover {
  	background-color: white;
  	background-image: none;
    transition: 0.5s;
}
.post_block.row1 {
	background-color: #000;
    }
.row1, .post_block.row1 {
	background-color: #1A1A1A;
}
.ipsBox {
	background: rgba(0, 0, 0, 0.73);
}
.ipsBox_container {
	background: #1A1A1A;
  	border: 1px solid #000000;
}
.row2 td {
    border-bottom: 1px solid #202020;
}
.row2, .post_block.row2 {
    background-color: #282828;
    border: 2px solid rgb(53, 53, 53);
}
a:hover {
    color: #31D8DA;
    transition: 0.5s
}
a {
    color: #009494;
    transition: 0.5s
}
#category_shoutbox .input_text {
    width: calc(100% - 335px);
    box-sizing: border-box;
    padding: 4px;
    border-width: 1px;
    border-style: solid;
    border-color: black;
    background: #848484;
    -moz-border-radius: 2px;
    -webkit-border-radius: 2px;
    border-radius: 2px;
}
body {
    background-position: center top;
    background-repeat: no-repeat;
    background-attachment: fixed;
    background-size: cover;
    color: #B5B5B5;
    margin: 0;
}
.ipsSideBlock {
    background: #2C2C2C;
    padding: 10px;
    margin-bottom: 10px;
}
#shouts-global-resizer, #shouts-resizer {
    background-color: #202020;
    cursor: n-resize;
    height: 6px;
}
table.ipb_table td {
    padding: 10px;
  	border-bottom: 1px solid #2D2D2D;
}
.post_block.row1 {
  	background-color: #000;
}
.post_block.row2 {
 	background-color: #272727;
}
.row2, .post_block.row2 {
  	background-color: #282828;
}
#shoutbox-shouts-table .row2 td { 
  	border-bottom: 1px solid #4C4C4C;
  	vertical-align: middle;
}
.userpopup h3 {
    background: black;
    border: none;
    box-shadow: none;
}
.ipsType_pagetitle, .ipsType_subtitle {
    font: 300 26px/1.3 Helvetica,Arial,sans-serif;
    color: #D2D2D2;
}
div.bbc_spoiler_wrapper {
    border: 1px inset #777;
    padding: 4px;
    background-color: rgb(42, 42, 42);
}
}
