@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("russian.rt.com"), domain("www.russian.rt.com") {

.b-shows_aside-header {
  margin: 0 !important;
  }
  
.b-shows_aside p, .content-wp.index-cont ul li, .b-shows_aside-header, p, dd, dt  {
  font-size: 1.0rem !important;
  } 
  
.b-promo_box-index_body dt {
  font-size: 13px !important;
  }
.podcast p {
  font-size: 1rem !important;
  }
  
  .block-advert,
  .article__share-item,
  .disclaimer,
  .header_social,
  .footer_social,
  .top-share,
  .hc-link,
  .smi2__article,
  .adsbygoogle-box,
  .widget__smi2,
  .aside-widget,
  .article__share-wrap,
  section[class*="breaking"],
  div[id*="breaking"],
  div.app-panel.show.is_common
  {
		display: none !important;
  }
  
  
  body > header nav.secondary ul.secondary-menu li a {
    color: #fff;
    float: left;
    height: 12px;
    line-height: 12px;
    padding: 0 20px 0 0;
    text-decoration: none;
}
  
 .navbar {
    background: none repeat scroll 0 0 #30302f !important;
    border-bottom: 0 solid #bebdbb !important;
    font-size: 13px !important;
    height: 30px !important;
    margin-bottom: 2px !important;
    opacity: 0.3 !important;
    overflow: hidden !important;
    position: relative !important;
    transition: opacity 0.15s ease 0s !important;
    width: 100% !important;
}
  
  .header-container {
    margin-bottom: 13px !important;
}
  #search-top {
    float: right !important;
    margin: 0 0 !important;
    min-width: 270px !important;
    text-align: right !important;
    transition: opacity 0.2s linear 0s !important;
}
  
  .article-intro {
    font-size: 14px !important;
    line-height: 1.4em !important;
    margin-bottom: 5px !important;
    padding: 0 0 1px !important;
}
.article-body {
    font-size: 14px !important;
    line-height: 1.4em !important;
    margin-bottom: 4px !important;
    padding: 0 0 1px !important;
}

.article-body p {
    font-size: 14px !important;
    line-height: 1.4em !important;
    margin: 0 0 1em !important;
}
  .link-overlay,
  article h4,
  article h3   {
    font-size: 13px !important;
    line-height: 1.2em !important;
}
  
  .short-story {
    font-size: 14px !important;
    line-height: 1.4em !important;
    margin-bottom: 5px !important;
    padding: 0 0 1px !important;
} 
  
  body.article article#content div.short-story {
    font-size: 1.3em !important;
    line-height: 1.4em !important;
    margin: 0 0 8px !important;
}
  
  .primary {
    font-size: 14px;
}
  
  h1 {
    font-size: 16px !important;
}
  h4 {
    font-size: 14px !important;
    opacity: 0.8 !important;
}
  
  .top-news_list,
   h3 {
    font-size: 14px !important;
}
  

.top-news_list figcaption h1, .top-news_list figcaption h2, .top-news_list figcaption h3 {
    font-size: 1em !important;
}
  
  select, textarea, input[type="text"], input[type="password"], input[type="datetime"], input[type="datetime-local"], input[type="date"], input[type="month"], input[type="time"], input[type="week"], input[type="number"], input[type="email"], input[type="url"], input[type="search"], input[type="tel"], input[type="color"] {
    background-color: #ffffff;
    border: 1px solid #ccc;
    display: inline-block;
    font-size: 14px !important;
    line-height: 13px !important;
    padding: 2px !important;
}
  
  .input-prepend {
    border-width: 1px 0 1px 1px !important;
    font-size: 12px !important;
    font-weight: normal !important;
    height: 12px !important;
    line-height: 12px !important;
    min-width: 16px !important;
    padding: 2px !important;
}
  #section_header {
    background: none repeat scroll 0 0 #c5c5c5;
    margin-bottom: 2px;
}
  
  #section_header aside {
    color: #1d77c9;
    display: inline-block;
    font-size: 1.1em;
    font-weight: 300;
    line-height: 1.1em;
    margin: 3px 0 1px 15px;
    vertical-align: -1px;
}
  
  nav.paginator ul li {
    display: inline-block;
    font-size: 1.2em;
}
  
  body {
    font-size: 13px;
    line-height: 1.2;
}

body > header nav.primary ul {
    font-size: 1em;
}

  body > header nav.trending ul.trending-tags li a {
    color: #777;
    float: left;
    height: 15px;
    line-height: 18px;
    padding: 0 2px 0 0;
    text-decoration: none;
    text-shadow: 0 1px 0 rgba(255, 255, 255, 0.8);
}
  .materials-preview_list h1, .materials-preview_list h2, .materials-preview_list h3 {
    font-size: 16px;
    font-weight: 600;
    line-height: 1.2;
    margin: 2px 12px;
}
  

  

.hc_c, .hc_c h1, .hc_c h2, .hc_c h3, .hc_c h4, .hc_c h5, .hc_c h6, .hc_c p, .hc_c td, .hc_c dl, .hc_c tr, .hc_c dt, .hc_c ol, .hc_c form, .hc_c select, .hc_c option, .hc_c pre, .hc_c div, .hc_c table, .hc_c th, .hc_c tbody, .hc_c tfoot, .hc_c caption, .hc_c thead, .hc_c ul, .hc_c li, .hc_c address, .hc_c blockquote, .hc_c dd, .hc_c fieldset, .hc_c li, .hc_c iframe, .hc_c strong, .hc_c legend, .hc_c em, .hc_c s, .hc_c cite, .hc_c span, .hc_c input, .hc_c sup, .hc_c label, .hc_c dfn, .hc_c object, .hc_c big, .hc_c q, .hc_c font, .hc_c samp, .hc_c acronym, .hc_c small, .hc_c img, .hc_c strike, .hc_c code, .hc_c sub, .hc_c ins, .hc_c textarea, .hc_c var, .hc_c a, .hc_c abbr, .hc_c applet, .hc_c del, .hc_c kbd, .hc_c tt, .hc_c b, .hc_c i, .hc_c hr, .hc_c article, .hc_c aside, .hc_c dialog, .hc_c figure, .hc_c footer, .hc_c header, .hc_c hgroup, .hc_c menu, .hc_c nav, .hc_c section, .hc_c time, .hc_c mark, .hc_c audio, .hc_c video {
    font-size: 1.2em !important;
 }
  
  
p {
  color: #4C4C4C !important;
  }
  
  .page figure > img {
    display: block;
    float: right;
    left: 0;
    position: relative;
    padding-left: 10px;
    padding-bottom: 0px;
    padding-right: 0px;
    padding-top: 0px;
    top: 0;
    width: 30%;
    border: 10px;
}
  

.page figure {
      display: block;
    margin: 0;
    padding-left: 0px !important;
    position: relative;
  padding-bottom: 0px !important;
  border: 10px;
}
  html {
    lang: ru;
  }
div.article-body div,
  p,
  *
  {
  -moz-hyphens: auto;
	-webkit-hyphens: auto;
	-o-hyphens: auto;
	-khtml-hyphens: auto;
	-ms-hyphens: auto;
	hyphens: auto;

}
  
}
