css:
@-moz-document url("http://www.pralek.cz/") {




h1  {
	font-family: Verdana;
	
	color: #FFEBCD;
}

a  {
	
	color: red;
}
  

body {
    background-color: black;
    font-family: Raleway;
    color: white;
margin-right: 25%;



} 


ul > li {padding: 2em 0 0.5em;
background-color: blue;
list-style-type: none;
color: white;
padding-left: 2em;
   

} 



ul { border-left: 0.2em solid red;
margin-left: 1em;
padding-left: 2em;

} 

p {
border: 0.2em solid white;
padding: 2em;
}





ol {
border-bottom: 0.2em solid green;


}

form {
border-bottom: 0.2em solid green;
} 

li {
color: black;
} 




@media screen and (max-width: 45em) {
  body { background-color: black;
margin-right: 0em;
margin-left: 0em;
padding-left: 0em;
padding-right: 0em; }

ul { border-left: none;
margin-left: 0em;
margin-right: 0em;
padding-left: 0em;
padding-right: 0em;

}

ul > li {padding: none;
background-color: black;
list-style-type: none;
color: white;
margin-left: 0em;
margin-right: 0em;
padding-left: 0em;
padding-right: 0em;

}

p {
border: none;
margin-left: 0em;
margin-right: 0em;
padding-left: 0em;
padding-right: 0em;
}
}

@media screen and (min-width: 130em) {
  body {  background-color: black;
    font-family: Raleway;
    color: white;
min-width: 100em;
max-width: 100em;


   
    }

ul > li {
background-color: blue;
list-style-type: none;
color: white;


padding: 1em;
min-width: 100em;
max-width: 100em;
   
   

} 



ul { border: none;

padding-left: 0em;
padding-right: 0em;
min-width: 100em;
max-width: 100em;
   

}
p {
border: 0.2em solid white;
padding: 2em;
min-width: 100em;
max-width: 100em;

}

#bottom {
margin-left:  23em;

}


h1  {
	font-family: Verdana;
	
	color: #FFEBCD;

margin-left:10em;

min-width: 80em;
max-width: 80em;
}

h2  {
	font-family: Verdana;
	
	color: #FFEBCD;

margin-left:10em;


min-width: 50em;
max-width: 50em;
text-align:center;
}

hr {
  border: 0;
  width: 80%;
color: #000000;
background-color: #000000;
height: 5px;
}


#navbar {
    background-color: #CCE0FF;
    padding: 0.2em 0.5em;
min-width: 100em;
max-width: 100em;

margin-left:23em;
}
}

@media print {

a {color: black;
}

ul { border-left:none;
margin-left: 0em;
padding-left: 0em;}

#red {
    color: black; 
}


:active, .red {
    color: black;
}
    
p {
border: none;
padding: 0em;
min-width: auto;
max-width: auto;

}



ol {
border-bottom: none;


}

form {
border-bottom: none;
} 

body {
    background-color: white;
    font-family: Raleway;
    color: black;}

ul > li {
background-color: white;
list-style-type: none;
color: black;


padding: 0em;
min-width: auto;
max-width: auto;} 



}

}
