css:
@-moz-document url("http://www.pralek.cz/"){
	
@media screen and (min-width:45em) and (max-width:130em){
body {
  font-family: "Trebuchet MS", Arial, Helvetica, sans-serif;
  background-color:#D8E4FF;
}

ul > li{
	max-width: 40em;
	background-color: #FFFFFF;
        padding: 2em 0 0.5em;
        margin:0;
/*
margin: 2em 0 0.5em;
padding:0;
*/
}
	h1, h2 {
		font-family: "Verdana";
	}
	
h2.o-webu {
	font-size: 2em;
	border:solid #C5C5C5;
	width: 12.5em;
	margin: 1em;
	float: right;
	padding-left: 0.3em;
}

div#text h2.o-webu + p {
	clear:both;
}
	h2.o-webu ~ p{
		margin-left: 1em;
		float:left;
		max-width: 30%;
	}
	p + .socialIcons{
		clear:both;
	}
div#text {
	border: 8px solid blue;
	border-radius: 15px;
	background-color: white;
}

h2.jaroslav_pavelka{
	clear:both;
}
}



@media screen and (max-width: 45em) {

h1,h2 {
	font-family: "Tahoma";
}
body { 
	background-color: #D0FFE8;
	color: black;
	margin-right: 0em;
	margin-left: 0em;
	padding-left: 1em;
	padding-right: 0em;
	font-family: "Lucida Sans Unicode";
}

a {
    color: #156D4E;
}
	div#text {
		background-color:inherit;
			border: inherit;
	}

ul { border-left: inherit;
	margin-left: 1em;
	margin-right: 0em;
	padding-left: 1em;
	padding-right: 0em;
}
ul > li{
	background-color: inherit;
  padding: 2em 1em 0.5em;
	margin:0;
}
h2.o-webu {
	border:inherit;
	width: inherit;
	margin: 0em;
	float: inherit;
	padding-left: 0em
}

}
	
@media screen and (min-width: 130em) {
	
body { 
		background-color: #C9DFEB;
		color: black;
		padding-left: 1em;
		font-family: "Lucida Sans Unicode";
}
h1 {
		font-family: "Tahoma";
    font-size: 80px;
}

h2 {color:#1D4357;
	font-family: "Tahoma";
    font-size: 200%;
}

p {
		padding-right: 2em;
} 
li{
}

a {
    color: #538098;
}
		
div#text {
		max-width: 150em;
}

ul {float:left;
}
ul > li{
	margin-bottom: 1em;
	max-width: 30em;
}

ul + ul{
}
	p + h2.o-webu {
		clear:both;
	}
h2.o-webu {
	padding-top: 1em;
		font-size: 4em;
		padding-left: 11em;

}
h2.koutek + ul {
		float: left;
}
div#text	h2 ~ ul {
		margin: 0em 0em 0em;
		position: relative;
}
h2.o-webu + p {
	clear:both;
}
h2.o-webu ~ p {
	float:left;
		display:inline;
		max-width: 40em;
	padding: 0em 1em 1em;
}
	p + .socialIcons {
		clear:both;
	}
p.socialIcons + h2 {
		align-self: center;
		clear:both;
}
}

@media print {

body {
	background-color: white;
	font-family: "Times New Roman";

}
#navbar {
	display: none;
}
div#text {
	border: inherit;
}
h2.o-webu {
	border:inherit;
	width: inherit;
	margin: 0em;
	float: inherit;
	padding-left: 0em
}
span.red{
	color:black;		
	}
}
}
