css:
/* credit : คุณ 	Dekkeng https://userstyles.org/styles/111097/for-skinex*/
@-moz-document domain("madoomeemk.com") {

/* ------------------------------ */
/*            ทุกหน้า               */
/* ------------------------------ */

/* เปลี่ยนภาพ banner */
#banner, #important_threads, .container.stats {
  /*background: url('http://s1426.storage.proboards.com/6181426/i/g1MtCTTP9GHmNaAmOSLu.png') !important;*/
  display: none !important;
}

/* Text decorate */
table.list {
  font-weight: normal !important;
  font-size: 15px !important;
}
table.list a.thread-link, table.list a.board-link {
  font-weight: bold !important;
  font-size: 14px !important; 
  font-family: 'Segoe UI', Tahoma, sans-serif !important;
}
l.link.target {
  font-size: 15px !important;
}
span.ui-micro-pagination > ul > li > a {
  text-align: center !important;
  font-size: 14px !important;
  line-height: 17px !important;
  width: 17px !important;
  height: 17px !important;
}
article > div.message {
  font-size: 102% !important;
}
article table.list tr td.content {
  background: #FFFAF0 !important; 
}

.posts .post .content{  
  background:#FFFAF0;
}

/* ล็อคตำแหน่ง Navigation Bar ด้านบน */
body {
  margin-top:55px !important; 
  background-color : #ad9e8b; 
  background: #ad9e8b url('http://madoomee.com:69/pic/gucci.jpg');
}
#navigation-menu {
  position: fixed !important;
  top: 0px !important;
  width: 100% !important;
  z-index: 9999 !important;
  /*background:#FFFAF0; */
}
.control-bar {
  top: 63px !important;  
}

/* ซ่อนแถบ tree */
#navigation-tree, .recent-threads-button {
  /*display: none !important;*/
  position: fixed !important;
  top: 40px !important;
  width: 100% !important;
  z-index: 9999 !important;
  padding-bottom: 2px;      
}
#nav-tree > li a {
  display: inline !important; 
}

/* ปรับขนาดภาพ icon ในส่วน stat ใหญ่ล้นกรอบ */
#content .container.stats .content .icon > img {
  max-width: 40px !important;
  max-height: 59px !important;
}

/* ลบโฆษณาของ proboards */
footer, #pbn-bar-wrapper ~ div {
  display: none !important;
}
#content {
  padding-bottom: 0 !important;
}

/* ลดขนาดภาพโปรไฟล์ให้ไม่ซ้อนทับชื่อ */
.avatar-wrapper.avatar_size_medium img {
  max-width: 90px !important;
  max-height: 60px !important;
}

/* หุบ chat box ตอนเข้าหน้า */
#chats {
  display: none;
}

.container>.content,.ui-dialog .ui-dialog-content,.ui-dialog .ui-dialog-buttonpane,.container.copy .clonem {
  background: #FAF0E6 !important;
}

  
/* ------------------------------ */
/*              หน้าแรก            */
/* ------------------------------ */
/* ปรับขนาดกรอบข่าวสารหน้าแรกด้านบนให้เล็กลง เพื่อประหยัดพื้นที่ */
.container.v4news {
  height:80px !important;
}
.container.v4news .content.pad-all, .container.v4news #fscroller {
  height: 30px !important;
}


/* ------------------------------ */
/*              หน้าฟอรั่ม           */
/* ------------------------------ */
            
/* ลบภาพ avatar ในหน้า forum ออก */
.container.threads td.icon, .container.threads th.icon {
  display: none !important;
}
.container.threads td.main, .container.threads th.main {
  padding-left: 20px !important;
}

table.list tr.board{
  background: #FAF0E6 !important;
}

.container.threads table.list tr{
  background: #FEFCFA;
}
.container.threads table.list tr.new{
  background: #FAF0E6;
}

.container.threads table.list tr.new table tr {
  background: #FAF0E6 !important;
}

.container.threads table.list tr.sticky {
  background: #E6EDFB !important;
}
.container.threads table.list tr.sticky table tr {
  background: #E6EDFB !important;
}        
  
/* เปลี่ยนสีพื้นหลังกระทู้ปักหมุด */
.container.threads table.list tr.sticky.new {
  background: #C0D1F5 !important;
}

.container.threads table.list tr.sticky.new table tr {
  background: #C0D1F5 !important;
}
  
.avatar-wrapper.avatar_size_medium > div > img {
  position: relative!important;
  margin-left: 7px !important;
}
  
.avatar-wrapper.avatar_size_medium > div > div > img {
  position: absolute!important;
  left: 9px !important;
}

.modified_time.time{
	color : #000 !important;
}

div#wrapper{
	width : 100%;
}
tr.head{
	font-weight: bold !important;
	color: #cbbbab !important;
	background-color: #ad9e8b !important;
}
table.list{
  /*border:1px !important;*/
  border-spacing: 1px !important; 
  /*border-color: #000 !important;*/
  border-collapse: inherit !important; 
  background: #000 !important;
} 

table.list thead tr{
	font-weight: bold !important;
	color: #cbbbab !important;
	background-color: #ad9e8b !important;
}
.notifications-container tr{
	background: #FAF0E6 !important;
}
}
