css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://picarto.tv/live/channel.php?watch="), url-prefix("https://www.picarto.tv/live/channel.php?watch=") {
  body {width:850px; margin: 0;}
  html {background-position: top right;}
.msgContent, .msgPremiumContent {letter-spacing:0 !important;}
  #bitchkopfdiveastereggs {float:none; display:inline;}
  #bitchkopfdiveasteregg {float:none; width:100%; margin:10px 0;}
  #bitchkopfdiveasteregg2 {float:none; width:100%; margin:0 0 10px 0;}
  #channeldescription {float:none; display:inline-block; width:100%; margin-top:0; margin:0;}
  #msgs {line-height:1.25; font-size:13px;}
  .timestamp {margin:0px !important;}
  .timestamp:after {content:" "}
  #popoutbuttons {float:none;}
  #channelinfobox {width:100%; margin:0; border-top-width: 0;}
  #chat {margin:479px 0 0 0; border-bottom-right-radius: 5px !important; border-bottom-left-radius: 5px !important; border-right:2px solid #fff; border-top-width: 0; border-left:2px solid #fff; border-bottom:2px solid #fff;}
  
  #backpage {width:481px; margin-left:366px; margin-top:0px;}
  #streamermk {width:841px; border-bottom:3px solid #fff;}
  #onlinestatus2, #onlinestatus {width:827px;}
  #registernamemk_premium, #registernamemk {width:837px;}
  header {min-width:0px;}
  body {overflow-x:hidden !important;}
  #search {width:190px;}
  #searchchannel {width:185px;}
  #searchmk {margin-right:0; width:200px;}
  #imagepanelcontainer {padding:0 !important;}
  #imagepanel1 {margin-bottom:10px !important; margin-right:0 !important;}
  #imagepanel2 {margin-bottom:10px !important;}
  #offlinecontent {width:837px;}

/*
  #chat .customicon img[src*=".gif"] {display:none !important;}
*/
/*
  #chat .premicon {display:none;}
*/
  
  #searchmk {position: absolute; left: 658px;}
  #loggedinasusrbuttons {position: absolute; left: 365px;}
  #menumk {position: absolute; left: 418px;}
}

@-moz-document domain("picarto.tv") {
  #gopremiumbanner, #premiumclose, #gopremiumlink, footer, #sharebuttons, #picartospecial {display:none !important;}
}
