css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.pcinpact.com"),domain("www.nextinpact.com") {
#body {font-size:100% !important;}
#header,
#bloc_actu_abonnez,
#sidebar,
#footer,
.commentaire_image,
.PHHO,
#sideBarArticle,
header,
#socialbar,
#top-share-container,
#soutenir_journaliste,
#actu_auteur,
#extra-services,
.avatar_user,
#carousel-lidd,
.non_auth_create_comment_area,
footer,
#toTop
{ display:none !important; }

#content_left_wrapper, 
#content_left, 
#content_news,
#actu_entete,
#bloc_commentaires
{ width:90% !important; }

.bandeau_colore,
.commentaire_entete, 
.commentaire_content
{ width:90% !important; }

#cadre, #cadre div, #copyright { float: none !important; position: relative; }

#content, #content2, #layout { 
  width: 90% !important;
  background: none !important; 
}

#layout { 
  margin-left: 100px !important;  
  margin-right: 100px !important; 
  padding-left: 30px !important;
  padding-right: 30px !important;
}

  .container_article {padding-right: 3em !important;}
#bloc_central_max { background-color: #fff !important; }

#bloc_central_max, #bloc_central_comment { margin: auto !important; width: 837px !important; }

#bloc_centre, #scroller_main_content , #main_content_section, #copyright { width: 95% !important; }

#top_section, #header { display: none !important; }

#cadre { border: none !important; margin-top: 5px; }

#bloc_centre, #copyright { margin: auto; }

#bloc_centre > .txt_marge > .news_source > div { width: auto !important; margin-right: 15px; }

#bloc_centre > .txt_marge > div { width: 100% !important; }

#bloc_gauche,
#bloc_droite,
#bloc_centre > :first-child,
.news_source > div.txt_marge_news > div
{ display: none !important; }

.entete_visuel_rubrique { position: absolute !important; margin-left: 0 !important; right: 5px !important; }

.carrousel_section, #carousel-dossier, #carousel-bonplan,
#header {display: none !important; }

}
