css:
@-moz-document domain(ogame.gameforge.com) 
{


#traderOverview #planet[style="background-position: -546px 0px; height: 250px;"]
{display: none !important; }
  
#div_traderAuctioneer p.stimulus
{display: none !important; }

 
  
#bar li#auctionTimekeeper[style="position: absolute; right: -70px; color: white;"]
{ position: absolute !important; color: #ccccff !important;
  left: 725px !important; top: 58px !important;
  width: 92px !important; height: 29px !important;
  font-size: 10px !important; font-weight: bold !important;
  background: url("http://mastervap-old.info/img/ogld/fondo_subasta1_v2.png") 
  no-repeat !important;
  border: 0px solid #252540 !important; border-radius: 0px !important;
  padding: 7px 0px 0px 0px !important; margin: 0px !important;
  text-indent: 9px !important; text-align: left !important;}
  
#bar li#auctionTimekeeper[style="position: absolute; right: -70px; color: lime;"],
#bar li#auctionTimekeeper[style="position: absolute; right: -70px; color: yellow;"],
#bar li#auctionTimekeeper[style="position: absolute; right: -70px; color: red;"]
{ position: absolute !important; 
  left: 725px !important; top: 58px !important;
  width: 92px !important; height: 29px !important;
  font-size: 10px !important; font-weight: bold !important;
  background: url("http://mastervap-old.info/img/ogld/fondo_subasta1_v2.png") 
  no-repeat !important;
  border: 0px solid #252540 !important; border-radius: 0px !important;
  padding: 8px 0px 0px 0px !important; margin: 0px !important;
  text-indent: 11px !important; text-align: left !important;}
  

  
#info #bar ul li + li + li + li + li + li + li + li + li + li#auctionTimer
{ position: absolute !important; color: #ccccff !important;
  left: 820px !important; top: 58px !important;
  width: 92px !important; height: 29px !important;
  font-size: 10px !important; font-weight: bold !important;
  background: url("http://mastervap-old.info/img/ogld/fondo_subasta2_v2.png") 
  no-repeat !important;
  border: 0px solid #252540 !important; border-radius: 0px !important;
  padding: 8px 0px 0px 0px !important; margin: 0px !important;
  text-indent: 8px !important; text-align: left !important;}
  


  
#div_traderAuctioneer .image_140px 
  { margin-top: 0px !important;}
  
#div_traderAuctioneer p.auction_info
  { font-size: 11px !important; padding-bottom: 30px !important;
    text-align: center !important;}
  
#div_traderAuctioneer .left_content label,
#div_traderAuctioneer .left_content .detail_value
  { text-align: center !important;}
  
#div_traderAuctioneer .left_content .detail_value
  { font-weight: bold !important;
    position: relative !important; top: -2px !important;}
  
#div_traderAuctioneer #auctionTimer
  { position: absolute !important; 
    left: 73px !important; top: 269px !important;
    font-size: 15px !important; font-weight: bold !important;
    width: 95px !important; text-align: center !important;
    background: url("http://mastervap-old.info/img/ogld/trans_50.png") 
    repeat scroll 0 0 rgba(0, 0, 0, 0) !important;
    border: 2px solid #343445 !important; border-radius: 3px !important;
    padding: 2px 0px 3px 0px !important; margin: 0px !important;}
  
  
#div_traderAuctioneer .table_ressources_sum tr td
  {width: 70% !important; font-size: 14px !important;}
  
#div_traderAuctioneer .table_ressources_sum tr td + td
  {width: 30% !important; font-size: 14px !important;}
  
#div_traderAuctioneer .table_ressources_sum 
  { position: absolute !important; top: 280px !important;}
  
#div_traderAuctioneer a.pay
  { position: absolute !important; top: 220px !important; }


}
