css:
@-moz-document domain("hipchat.com") {
.date-divider {
    margin: 0;
    text-align: left;
    color: #fff;
}

.date-block {
    width: calc(100% - 10px);
}

.hc-chat-from {
    display: table-cell;
}

.hc-msg-blue .hc-chat-from {
    display: table-cell!important;
    width: 25px;
    max-width: 25px;
    padding: 3px 10px!important;
}

.msg-line:first-of-type {
    border-top: #CC7832 solid 1px;
    padding-top: 5px!important;
}

span.time-separator {
    display: none;
}

span.hc-chat-time {
    float: right!important;
    padding-right: 8px!important;
}

span.sender-name {
    color: white!important;
}

.hc-chat-row.hc-msg-message {
    border: #FFC66A solid 1px;      
    border-radius: 8px;      
    margin: 10px 4px;
    padding: 4px;
    color: #fff;
}

.scroll-wrap {
    background-color: #000;
}

.hc-chat-row.hc-msg-blue.hc-msg-message {
    background-color: #444!important;            color: #fff!important;
}

.hc-msg-blue .hc-chat-from {
    color: #fff;
    display: block;
    padding: 0;
    margin: 0px 4px;
    font-size: 13px;
}

.hc-chat-msg {
    display: block;
    margin: 0px 4px;
    padding: 4px 0px 0px 0px;
	width: calc(100% - 8px);
}

.hc-msg-gray:before {
      display:none;
}
    
.hc-chat-row.hc-msg-gray {
    border: #FFC66A solid 1px;
    border-radius: 8px;
    margin: 10px 4px;
    padding: 4px;
    color: #fff;
    background-color: #000;
}
        
.hc-chat-row.hc-msg-nocolor {
    border: #FFC66A solid 1px;
    border-radius: 8px;
    margin: 10px 4px;
    padding: 4px;
    color: #fff;
    background-color: #000;
}
  
a {
    color: #6897BB;
}

.hc-chat-time {
    color: #AAA;
}

.hc-chat-panel {
    font-family: Consolas, Menlo;      
    font-size: 13px!important;
}

.hc-chat-from {
    display: block;
    padding: 0;
    color: #CC7832;
    margin: 0px 4px;
    text-align: left;
    font-size: 13px!important;
}

.date-divider span {
    background-color: #000;
    color: #aaa;
    font-size: 13px;
    padding: 5px;
}

.date-divider:before {
    display: none;
}

.hc-chat-msg {
  max-width: 100%;
}

.msg-line {
    font-size: 13px!important;
    line-height: normal!important;
}

.msg-line {
    margin: 0px!important;
}

.hc-chat-row.hc-msg-blue.hc-msg-file {
    background-color: #444;
    color: #fff;
    border: #FFC66A solid 1px!important;      border-radius: 8px!important;      
    margin: 10px 4px!important;
    padding: 4px;
}

textarea#hc-message-input {
    font-family: consolas,menlo;
    font-size: 13px;
}

.hc-chat-row.hc-msg-gray.hc-msg-link.fade {
    border: #FFC66A solid 1px!important;      border-radius: 8px!important;      
    margin: 10px 4px!important;
    padding:  4px;
    background-color: #000;
    color: #fff;
}

.hc-chat-row.hc-msg-nocolor.hc-msg-info {
    border: none;
}

.hc-msg-info .hc-chat-msg {
    border-top: none;
}

.hc-roster.hc-sidebar-scroll {
    background-color: #333;
    color: #aaa;
}

li.hc-roster-item {
    color: #aaa;
}

a.hc-roster-link {
    color: #aaa;
}

#hipchat .hc-roster-container .hc-roster-item a.hc-roster-link, body .hc-roster-container .hc-roster-item a.hc-roster-link {
    color: #bbb;
}

header.aui-page-header.room-header {
    background-color: #333!important;
}

#hipchat .aui-page-header.room-header h3, body .aui-page-header.room-header h3 {
    color: #bbb!important;
}

span {
    color: #bbb;
}

nav.aui-navgroup.aui-navgroup-vertical.hc-sidebar.hc-sidebar-scroll {
    background-color: #333!important;
}

#hipchat .hc-layout > div, body .hc-layout > div {
    background-color: #333!important;
  border-right: 0;
}

.hc-chat-box {
    background-color: #333!important;
}

#hipchat .hc-chat-box form.aui .hc-text-input .hc-textarea, body .hc-chat-box form.aui .hc-text-input .hc-textarea {
    background-color: #000!important;
    color: #fff!important;
}

#hipchat .aui-page-header, body .aui-page-header {
  border-bottom: 0;
}

#hipchat .hc-chat-box, body .hc-chat-box {
  border: 0;
}

table.aui>tbody>tr {
    background-color: #333;
}

#hipchat .aui-page-header.lobby-header, body .aui-page-header.lobby-header {
    background-color: #333;
    border-bottom: 3px #bbb solid;
}

.hc-chat-scrollbox.message-list {
    background-color: black!important;
}

div.hc-msg-blue div.hc-chat-from span.sender-name {
    color: #fff;
}

div.hc-msg-nocolor div.hc-chat-from span.sender-name {
    color: #CC7832;
}

a.aui-nav-item:hover {
  	background: #555 !important;
    background-color: #555 !important;
}

.msg-wrap pre {
    border: 1px solid #ccc;
    background-color: #000;
    padding: 10px;
    border-radius: 3px;
    font-size: 13px;
    margin: 5px 0px;
}

pre.prettyprint {
    background-color: #000;
}

pre.prettyprint span {
    font-weight: bold;
}

span.pln {
    color: #FFFFFF;
}

span.pun {
    color: #DCDCDC;
}

span.kwd {
    color: #CC7832;
}

span.typ {
    color: #FFC66A;
}

span.lit {
    color: #6897BB;
}

span.str {
    color: #A5C25C;
}

span.com {
    color: #808080;
}

span.atn {
    color: #fff;
}

span.tag {
    color: #CC7832;
}

span.atv {
    color: #A5C25C;
}

span.description {
    color: #fff;
}
}
