css:
@-moz-document domain("replay.pokemonshowdown.com") {
.backdrop {
    width: 640px;
    height: 360px;
    position: absolute;
    top: 0px;
    left: 0px;
    overflow: hidden;
    background: -50px -90px;
    opacity: 1 !important;
}

.backdrop:after{
	display: block;
	content: "";
	background: url(http://i.imgur.com/fXwl99u.png);
	position: absolute;
	top: 0;
	left: 0;
	width: 640px;
    height: 360px;
}

.leftbar {
	background: rgba(255,255,255,0);
  	border: none;
}
  
.rightbar {
	background: rgba(255,255,255,0);
	border: none;  
}


.trainer {
  	color: #000;
}
    
.battle-log {
  	background-image: url(http://i.imgur.com/uTTubXe.png);
  	background-repeat: no-repeat;
  	background-position: center;
}
    
.chooser em {
	background: #5E5E5E;
}

.linklist a {
  	color: #fff;
	background: #3C3C3C;
    border: 1px solid #262626;
    text-shadow: none;
}

.linklist a:hover {
  	color: #fff;
	background: #5E5E5E;
    border: 1px solid #262626;
    text-shadow: none;
}
      
.linklist a small {
	color: #fff;   
}

.linklist li a strong{
	color: #fff;
}

.linklist li a{
	color: #FF0000;
}
  
.pfx-panel {
	background: #191919;
  	color: #fff;
}

.pfx-panel div form p button {
  	font-size: 15px;
    color: #000 !important;
}

.pfx-topbar {
  	z-index: 2;
	background: linear-gradient(white, #8A8A8A);
  	box-shadow: 0 10px 10px #181818;
}
}
