css:
@-moz-document domain("mail.google.com") {

  /* We don't want everything clustered together. We're looking for efficiency. This fixes that. */
  .pm {
    margin-top:6px;
    background-color:rgba(0,0,0,0.2);
    border:1px solid rgba(255,255,255,0.5);
  }
 
  /* Lets make the links fill the entire div, plus a little bit of extra styling. */
  .po {
    transition: all .15s ease;
    display:block;
    width:100%;
    height:auto;
    text-decoration:none;
    padding:5px;
    text-align:left;
    font-size:1.1em;
    text-shadow:0 0 2px black;
  }
  .po:hover {
    transition: all .15s ease;
    text-shadow: 0 0 2px teal; /* Just a lil extra Team Spirit here. Feel free to change the color. ^_^  */
  }
 
  /* Moving the X to be closed to the center of the div, instead of right against the edge. */
  .pl {
    right:5px;
    top:3px;
  }
  .oZ-jc, .T-Jo .J-J5-Ji {
    height:20px;
    width:20px;
  }
  .QOxrP, .pU {
    transition: all .15s ease;
    display:block;
    width:100%;
    height:auto;
    text-decoration:none;
    padding:5px;
    text-align:left;
    font-size:1.1em;
    text-shadow:0 0 2px black;
    margin-top:6px;
    background-color:rgba(0,0,0,0.5);
    border:1px solid rgba(255,255,255,0.5);
    box-sizing:border-box;
  }
  
}
