css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://www.occultforum.org/forum/") {
div#topdiv {
 	display: none;
}
div.squareImg {
 	border: 1px solid #600; 
 	background: #000; 
}
div#contentarea {
 	background: #111; 
	color: #fff;
  margin-bottom: 50px;
}
div#contentarea td.mainCell {
 	background: #000 !important; 
	color: #fff;
}
div#page-header {
 	background: #000 !important; 
	color: #fff;
}
div.searchbox {
	background-color: #000;  
}
.mainCell .navbar {
 	border: 1px solid #600; 
	background-color: #111;  
}
.mainCell .forabg {
 	border: 1px solid #300; 
    background: #222;  
    background-image: none !important;  
    }
.mainCell span.corners-top {
    background-image: none !important;  
          }
.mainCell span.corners-bottom {
    background-image: none !important;  
          }
.mainCell span.corners-top span {
    background-image: none !important;  
          }
.mainCell span.corners-bottom span {
    background-image: none !important;  
          }
.mainCell .forumbg {
    background-image: none !important;  
    background: #222;  
	color: #fff;
  }
.mainCell ul.forums {
    background: #000;  
	color: #fff;
  }
.mainCell ul.forums li.row, .mainCell li.row {
    border-top-color: #111;  
    border-bottom-color: #111;  
  }
.mainCell ul.forums li.row, .mainCell li.sticky {
    background: #122 !important;  
  }
.mainCell ul.forums li.row, .mainCell li.bg1 {
    background: #011;  
  }
.mainCell ul.forums li.row, .mainCell li.bg2 {
    background: #000;  
  }
.mainCell ul.forums li.row:hover, .mainCell li.row:hover {
    background: #300;  
    border-top-color: #633;  
    border-bottom-color: #633;  
  }
.mainCell .bg1 {
 	border: 1px solid #600; 
    background: #111;  
    color: #fff;  
    }
.mainCell .bg2 {
 	border: 1px solid #300; 
    background: #000;  
    color: #fff;  
    }
.mainCell .content {
    color: #fff;  
    }
.mainCell blockquote {
 	border: 1px solid #333; 
    background: #222;  
    color: #eff;  
  }
div#bottomdiv {
 	background: #222; 
	color: #666;
  margin-bottom: 23px;
}
div#bottomdiv a {
	color: #633;
}
}
