css:
@-moz-document url-prefix("http://sync-video.com"), url-prefix("https://sync-video.com"), url-prefix("http://sync-video.com/"), url-prefix("https://sync-video.com/"), domain("sync-video.com") {
body {
margin: 0;
font-family: "Helvetica Neue",Helvetica,Arial,sans-serif;
font-size: 14px;
line-height: 20px;
color: #B62121;
background-color: #fff;
}


#wrap {
min-height: 100%;
height: auto!important;
height: 100%;
margin: 0 auto -45px;
background-color: #222;
}

.navbar-inner {
border-bottom: 0 none;
-webkit-box-shadow: 0 1px 2px rgba(0, 0, 0, 0.5);
-moz-box-shadow: 0 1px 2px rgba(0, 0, 0, 0.5);
box-shadow: 0 1px 2px rgba(0, 0, 0, 0.5);
filter: none;
padding: 3px 0 2px 0;
background: #041017;
}

.chat .dialog {
margin: 0;
height: 200px;
overflow: auto;
font-size: 12px;
background-color: #222;
}

.chat .dialog li:nth-child(even) {
background: #0A0707;
}

.chat .dialog li:nth-child(odd) {
background: #0A0707;
}
      
      .input-msg {
color: #E43737 !important;
background: #0F0D0D url('http://www.sync-video.com/public_assets/icons/keyboard.png') no-repeat 5px !important;
border: 1px solid #ecebeb !important;
width: 100%;
padding: 5px 2px 5px 38px !important;
display: inline-block !important;
margin: auto !important;
-webkit-border-radius: 0 !important;
-moz-border-radius: 0 !important;
border-radius: 0 !important;
}
    
#playlist>li.active {
background-color: #222;
}
    
#playlist a.orig-link:active, #playlist a.orig-link:link, #playlist a.orig-link:visited {
color: #DF2626;
}
      
#config-table {
width: 100%;
margin-bottom: 5px;
background-color: #222;
}
}
