css:
@-moz-document domain("www.mangapanda.com") {
.updates a {
font-weight: 400;
padding-top: 1px;
color: #E5412C;
}

#bodyalt {
background-color: #222;
box-shadow: 0 -3px 10px #98CFB4;
-moz-box-shadow: 0 -3px 10px #98CFB4;
-webkit-box-shadow: 0 -3px 10px red;
border-top: 1px solid red;
color: #000;
margin-top: 10px;
padding: 10px;
}

#popularlist {
background: none repeat scroll 0 0 #FEFFFE;
border: 1px solid #FD0303;
background-color: #222;
border-radius: 0 0 5px 5px;
-moz-border-radius: 0 0 5px 5px;
-webkit-border-radius: 0 0 5px 5px;
color: #FD0000;
display: inline;
float: right;
padding-bottom: 10px;
width: 300px;
}

#popularcaption h2 {
font-size: 123.1%;
color: #FC2817;
background-color: #222;
}

.manga {
background: none repeat scroll 0 0 #EFFEF4;
border-bottom: 1px solid #F50F0F;
background-color: #222;
border-radius: 0 5px 5px 0;
-moz-border-radius: 0 5px 5px 0;
-webkit-border-radius: 0 5px 5px 0;
border-right: 1px solid #C8C8C8;
border-top: 1px solid #C8C8C8;
float: left;
height: 44px;
overflow: hidden;
padding-left: 5px;
padding-top: 5px;
width: 245px;
}

#top_navigation {
background: none no-repeat scroll left top #222;
-webkit-box-shadow: -1px 1px 0 #325131;
-moz-box-shadow: -1px 1px 0 #325131;
box-shadow: -1px 1px 0 #000000;
border-bottom: 1px solid #FA1313;
border-left: 1px solid #FF0000;
border-right: 1px solid red;
border-top: 1px solid red;
clear: right;
float: right;
}

.popularitemcaption {
color: #EC2C14;
font-size: 93%;
}

div.c10 {
background-color: #222!important;
}

#logoimg, a.c2, .new, .hot, #searchbox, .manga_close, .manga_open {
background-color: #222;
}

#bodyust {
padding: 10px;
background-color: #222;
}

#wrapper_body {
width: 950px;
background-color: #000301!important;
margin: 0 auto;
padding: 0;
}

body, div, dl, dt, dd, ul, ol, li, h1, h2, h3, h4, h5, h6, pre, code, form, fieldset, legend, input, textarea, p, blockquote, th, td {
margin: 0;
padding: 0;
color: red;
background-color: #222;
}

#readmangasum {
background-color: #222;
border: 1px solid #333;
margin-top: 10px;
padding: 10px;
}

html, legend, #readmangasum p {
color: #F00;
}

#mangaproperties h1 {
color: #FF3202!important;
margin-bottom: 10px;
margin-top: 10px;
}

#searchbox {
border: 1px solid red;
-webkit-border-radius: 5px 5px 5px 5px;
-moz-border-radius: 5px;
border-radius: 5px 5px 5px 5px;
color: #F00;
font-family: "Lucida Grande",Tahoma,Arial,Sans-serif,sans;
font-size: 93%;
font-weight: 700;
height: 16px;
line-height: 18px;
padding-left: 20px;
padding-top: 4px;
text-transform: capitalize;
width: 200px;
background: #222 -38px -65px no-repeat;
margin: 5px 5px 0;
}

.chapters {
font-family: verdana, tahoma, arial, sans;
font-size: 85%;
color: #F90000;
}

#latestchapters h3 {
color: #F90707;
font-size: 123.1%;
font-weight: 700;
margin: 0 0 5px;
}

#latestchapters h2 {
color: #F90000;
margin-bottom: 20px;
}

.latesthotimages a {
color: #FF0000;
border-color:red;
}

.updates a.chaptersrec {
color: #FF0505;
font-size: 100%;
}

.logo {
float: left;
height: 70px;
width: 950px;
padding: 7px 0 0;
}

#top_navigation li {
background: none repeat scroll 0 0 transparent;
border-right: 1px solid red;
float: left;
height: 34px;
list-style: none outside none;
margin: 0;
padding: 0;
}

#latestchapters a, #listing a {
color: #FF0000;
}

#popularcaption {
background-color: #1C5100;
border-radius: 2px 14px 0 0;
color: #F00;
text-align: left;
}

.genretags {
background-color: #222;
border-radius: 5px 5px 5px 5px;
-moz-border-radius: 5px;
-webkit-border-radius: 5px 5px 5px 5px;
box-shadow: 1px 1px 2px #FC0000;
-moz-box-shadow: 1px 1px 2px #075800;
-webkit-box-shadow: 1px 1px 2px #222;
color: #ED0C0C;
font-size: 85%;
padding: 1px 5px;
}

.propertytitle {
color: #FF0000;
width: 130px;
}

.aname {
color: #FF0000;
font-size: 108%;
}

#readmangasum h2 {
color: #FF0000;
}

#listing th {
color: #FF0202;
font-size: 100%;
padding-left: 10px;
}

#listing {
border-collapse: collapse;
border-spacing: 0;
color: #2E4962;
font-size: 100%;
letter-spacing: .1px;
margin-top: 0px;
text-align: left;
width: 100%;
}

#top_navigation a {
color: #FF0A00;
float: left;
font-size: 116%;
line-height: 34px;
position: relative;
text-decoration: none;
padding: 0 12px;
}

.latesthotimages {
background-color: #FFF;
-webkit-box-shadow: 1px 1px 6px #1F692B;
float: left;
height: 181px;
margin-right: 8px;
width: 130px;
}

.note {
background: none repeat scroll 0 0 #222;
border-bottom: 1px solid #FF0000;
border-top: 1px solid #FF0000;
border-left: 1px solid red;
border-right: 1px solid red;
color: #F00;
text-align: center;
}

#chapterlist {
background: none repeat scroll 0 0 #F7FFF8;
float: left;
width: 100%;
}
}
