css:
@-moz-document domain("play.pokemonshowdown.com") {
  body {
    background: url("http://i.imgur.com/4utw280.png?1") no-repeat !important;
    background-color:#86C3E3 !important;
    background-size: 1000px;
  }
  
  .ps-room.ps-room-opaque {
    background:rgba(134, 195, 227, 0.9);
  }

  .mainmenufooter small:after{
    content:"Made by: Charles Paroissien"; 
    font-size:9pt; 
    color:#cccccc; 
    text-shadow:0 1px black; 
    position:absolute; 
    size:6px; 
    left:230px; 
    top:21px; 
    width: 300px;
  }
  
  .rightbar, .leftbar {
    background:rgba(134, 195, 227, 0.3);
  }
  
    .header .logo {
    opacity: 0; 
  }
  
  .battleform label {
    background: url("http://i.imgur.com/yFWeHx0.png") no-repeat scroll left center transparent !important;
    padding: 4px;
    opacity:0.9;
    color: #FFF;
  }
  
  .menugroup {
    background: rgba(0, 0, 0, 0);
  }

  .select {
    border-top-left-radius:0px;
  }
  
  .maintabbarbottom {
    background: #FFF;
    border: 1px solid #000;
    box-shadow: none;
  }
  
  .tabbar a.button.cur, .tabbar a.button.cur:hover {
    color: #000;
    background: #FFF;
    border: 1px solid #000;
    font-family: helvetica,arial,sans-serif;
    text-shadow: none;
  }
  
  .tabbar a.button {
    color: #000;
    background:#B7E1F9;
    border: 1px solid #000;
    font-family: helvetica,arial,sans-serif;
    text-shadow: none;
  }
  
  .tabbar a.button:hover {
    color: #000;
    background: #F2FBFF;
    border: 1px solid #000;
    font-family: helvetica,arial,sans-serif;
    text-shadow: none;
  }
  
  #header {
    background:url(http://i.imgur.com/XkjakIM.png);
  }
  
  .mainmenu .menugroup:nth-child(1) .button, .mainmenu .menugroup:nth-child(2) p:nth-child(1) .button, .mainmenu .menugroup:nth-child(2) p:nth-child(2) .button, .mainmenu .menugroup:nth-child(2) p:nth-child(3) .button, .mainmenu .menugroup:nth-child(3) p:nth-child(1) .button, .mainmenu .menugroup:nth-child(3) p:nth-child(2) .button {
    border:2px solid #383839;
    background:rgba(255, 255, 255, 0.9);
    color:#383839;
    font-family: helvetica,arial,sans-serif;
    text-shadow:none;
  }
  
  .mainmenu .menugroup:nth-child(1) .button:hover, .mainmenu .menugroup:nth-child(2) p:nth-child(1) .button:hover, .mainmenu .menugroup:nth-child(2) p:nth-child(2) .button:hover, .mainmenu .menugroup:nth-child(2) p:nth-child(3) .button:hover, .mainmenu .menugroup:nth-child(3) p:nth-child(1) .button:hover, .mainmenu .menugroup:nth-child(3) p:nth-child(2) .button:hover {
    border:2px solid #000;
    background:#FFF;
    color:#000;
    font-family: helvetica,arial,sans-serif;
    text-shadow:none;
  }
  
  .roomlist a.ilink {
    font-size:14px;
    font-family: Tahoma, Verdana, Segoe, sans-serif;
    font-weight:bold;
    opacity:0.9;
    color:#fbfbfb;  
    text-transform: uppercase;
    text-align:left;
    text-shadow: 1px 1px 1px #4d4d4d,
    1px 0px 1px #4d4d4d;
    background: url(http://i.imgur.com/DqmewkB.png);
    box-shadow:none; 
    border-radius:0px; 
    border-width:0; 
    width:642px;
    padding-left:20px;
    padding-top:10px;
    padding-bottom:12px;
  }
  
  .roomlist a.ilink:hover{  
    font-size:14px;
    opacity:0.9;
    font-family: Tahoma, Verdana, Segoe, sans-serif;
    font-weight:bold;
    color:#fbfbfb;  
    text-transform: uppercase;
    text-shadow: 1px 1px 1px #4d4d4d,
    1px 0px 1px #4d4d4d;
    background: url(http://i.imgur.com/UoUnGNL.png);
    box-shadow:none; 
    border-radius:0px; 
    border-width:0; 
    width:642px;
    padding-left:20px;
    padding-top:10px;
    padding-bottom:12px;
  } 
  
  .userlist {
    border-right:none;
  }
  
  .userlist li button {
    font-family: helvetica,arial,sans-serif;
  }
  
  .chat-log {
    border:1px solid #CECECE;
    font-family: helvetica,arial,sans-serif;
  }
  
  .pm-log, .battle-log {
    background:url(http://i.imgur.com/Q8vHT0Y.png);
    opacity: 0.9;
    font-family: helvetica,arial,sans-serif;
  }
  
  .pm-window h3 {
   border: 2px solid #383839;
    color: #383839;
    background:rgba(134, 195, 227, 0.8);
  }
  
  .pm-window h3:hover, .pm-window.focused h3, .pm-window.focused h3:hover  {
   border: 2px solid #000;
    color: #000;
  }
  
  .mainmenufooter a {
    color:#6853A0;
    text-shadow: none;
    text-decoration: none;
    font-weight:bold;
  }
  
  .mainmenufooter a:hover {
    color:#4F3E7D;
    text-shadow: none;
    text-decoration: none;
    font-weight:bold;
  }
  
  .chatbox textarea {
    font-family: helvetica,arial,sans-serif;
  }
  
  .ps-popup {
    background:url(http://i.imgur.com/Q8vHT0Y.png);
  }

  .ps-room .battle-controls {
    background: transparent !important;
  }
}
