css:
@-moz-document url("http://jsonlint.com/") {
body {
    background-color: #333;
}

h1, h2, h3, h4, h5, h6 {
    color: white;
}

footer {
    color: white;
}

a {
    color: green;
}

.CodeMirror-scroll {
    background-color: #444;
    color: white;
}

.CodeMirror-linenumber {
    background-color: #666;
    color: #FFF;
}

.CodeMirror-gutter {
    background-color: #666;
}

.cm-s-default .cm-string {
    color: white;
}

.cm-s-default .cm-number {
    color: #4EFF2F;
}

.cm-s-default .cm-atom {
    color: #40E3FF;
}

#result.success {
    background: #2E5800;
    color: #8FFF22;
    border-color: #469E19;
}

#result.error {
    background: #770005;
    color: #FF705D;
    border-color: #C5292E;
}
}
