css:
@-moz-document url("http://passon.memody.com/chat.php") {
.chatbox, .sidebar_frame
{
	background-color: #252525;
}

.room, .tabs
{
	background: #F5F5F5 -webkit-linear-gradient(top, #3C3C3C, #656565 35px, #252525 35px, #232323);
	color:#fff;
}

header
{
	height:0;
}

.content
{
	top:0;
}

.writer
{
	border-top:none;
}

.text
{
	color:#ccc;
}

#message, .sidebar_toggle
{
	background-color: #444;
	color:#fff;
}

.tabs {
    height: 35px;
  }
  
.sidebar_frame
{
  top: 35px;
    border-left: 1px solid #444;
}

}
