css:
@-moz-document domain("encyclopediadramatica.se") {
body {
font: x-small sans-serif;
background: #222 0 0 no-repeat;
color: red;
margin: 0;
padding: 0;
}


div#content {
background: #222;
color: red;
border: 1px solid #F00;
border-right: none;
line-height: 1.5em;
}

#p-cactions .pBody {
font-size: 1em;
background-color: #222;
color: inherit;
border-collapse: inherit;
border: 0;
padding: 0;
}

#p-cactions li a {
background-color: #222;
color: #1F9406;
border: none;
padding: 0 .8em .3em;
position: relative;
z-index: 0;
margin: 0;
text-decoration: none;
}

a {
text-decoration: none;
color: #2B9134;
background: none;
}



#toc, .toc, .mw-warning {
border: 1px solid red;
background-color: #222;
padding: 5px;
font-size: 95%;
}

h1, h2, h3, h4, h5, h6 {
color: red;
background: none;
font-weight: normal;
margin: 0;
overflow: hidden;
padding-top: .5em;
padding-bottom: .17em;
border-bottom: 1px solid #F00;
}

.catlinks {
border: 1px solid #F00;
background-color: #222;
padding: 5px;
margin-top: 1em;
clear: both;
}

div#footer {
background-color: #222;
border-top: 1px solid #FF0000;
border-bottom: 1px solid #FF0000;
margin: .6em 0 1em 0;
padding: .4em 0 1.2em 0;
text-align: center;
font-size: 90%;
}

#searchInput {
width: 10.9em;
margin: 0;
color: red;
background-color: #222;
font-size: 95%;
}

.pBody {
font-size: 95%;
background-color: #222;
color: black;
border-color:red;
border-collapse: collapse;
border: 1px solid #aaa;
padding: 0 .8em .3em .5em;
}

.mw-search-formheader {
background-color: #222;
margin-top: 1em;
border: 1px solid red;
}

.mw-search-formheader div.search-types ul li.current a {
color: #FF0000;
cursor: default;
}

pre {
padding: 1em;
border: 1px dashed red;
color: red;
background-color: #222;
line-height: 1.1em;
}

div.thumbinner {
border: 1px solid red;
padding: 3px!important;
background-color: #222;
font-size: 94%;
text-align: center;
overflow: hidden;
}

}
