css:
@-moz-document domain("play.pokemonshowdown.com"), domain("psim.us") {
  
  body{
    background: url(http://i.imgur.com/bVBXDEZ.jpg?1) no-repeat !important;
    background-size: 1100px;
    background-color:#FEF28C !important;
  }
  
  .ps-room.ps-room-light.scrollable {
    background: rgba(255, 240, 97, 0.7);
  }
  
  #header {
    background:url(http://i.imgur.com/XkjakIM.png) repeat;
  }

  .mainmenufooter small:after{
    content:"Made by: Charles Paroissien"; 
    font-size:9pt; 
    color:#cccccc; 
    text-shadow:0 1px black; 
    position:absolute; 
    size:6px; 
    left:230px; 
    top:21px; 
    width: 300px;
  }
  
  .battleform label {
    background: url("http://i.imgur.com/yFWeHx0.png") no-repeat scroll left center transparent !important;
    padding: 4px;
    color: #FFF;
  }
  
  .menugroup {
    background:rgba(255, 255, 255, 0);
  }
  
  .broadcast-red {
      background: none repeat scroll 0% 0% #EC5C4D;
      color: #000;
      border: 2px solid #000;
      padding: 5px;
      font-family: Futura,"Trebuchet MS",Arial,sans-serif;
      font-size: 10pt;
  }

  .broadcast-blue {
      background: none repeat scroll 0% 0% #8FA6B3;
      color: #000;
      border: 2px solid #000;
      padding: 5px;
      font-family: Futura,"Trebuchet MS",Arial,sans-serif;
      font-size: 10pt;
  }

  .broadcast-green {
      background: none repeat scroll 0% 0% #9BB38F;
      color: #000;
      border: 2px solid #000;
      padding: 5px;
      font-family: Futura,"Trebuchet MS",Arial,sans-serif;
      font-size: 10pt;
  }
  
  .logo {
    display:none;
  }
  
  .mainmenu .menugroup:nth-child(1) .button, .mainmenu .menugroup:nth-child(2) p:nth-child(1) .button, .mainmenu .menugroup:nth-child(2) p:nth-child(2) .button, .mainmenu .menugroup:nth-child(2) p:nth-child(3) .button, .mainmenu .menugroup:nth-child(3) p:nth-child(1) .button, .mainmenu .menugroup:nth-child(3) p:nth-child(2) .button {
    border: 2px solid #100E0F;
    color:#FFF;
    text-shadow:none;
    background:rgba(0, 0, 0, 0.8);
    font-size:11px;
    font-family: helvetica,arial,sans-serif;
  }
  
   .mainmenu .menugroup:nth-child(1) .button:hover, .mainmenu .menugroup:nth-child(2) p:nth-child(1) .button:hover, .mainmenu .menugroup:nth-child(2) p:nth-child(2) .button:hover, .mainmenu .menugroup:nth-child(2) p:nth-child(3) .button:hover, .mainmenu .menugroup:nth-child(3) p:nth-child(1) .button:hover, .mainmenu .menugroup:nth-child(3) p:nth-child(2) .button:hover {
    border: 2px solid #100E0F;
    color:#FFF;
    text-shadow:none;
    background:rgba(0, 0, 0, 0.9);
  }
  
  .mainmenu .menugroup:nth-child(1) .button {
    font-size: 13px;
  }
  
  .mainmenufooter a {
    color:#100E0F;
    text-shadow:none;
    font-weight:bold;
    text-decoration:none;    
  }
  
  .mainmenufooter a:hover {
    color:#2C2428;
    text-shadow:none;
    font-weight:bold;
    text-decoration:underline;
  }
  
  .pm-log {
    background:url(http://i.imgur.com/Q8vHT0Y.png) repeat;
    opacity: 0.9;
    font-family: helvetica,arial,sans-serif;
  }
  
  .pm-window h3 {
       border: 1px solid #100E0F;
    color:#EBEBEB;
    background:rgba(0, 0, 0, 0.8);
    font-family: helvetica,arial,sans-serif;
    font-size: 13px;
  }
  
  .pm-window h3:hover, .pm-window.focused h3, .pm-window.focused h3:hover  {
   border: 1px solid #100E0F;
    color:#FFF;
    background:rgba(0, 0, 0, 0.8);
  }
  
  .pm-log-add {
    background:rgba(0, 0, 0, 0.8);
  }
  
  .maintabbarbottom {
    background:#100E0F;
    border:1px solid #100E0F;
  }
  
  .tabbar a.button.cur, .tabbar a.button.cur:hover {
    color: #FFF;
    background:#100E0F;
    border:1px solid #100E0F;
    font-family: helvetica,arial,sans-serif;
    text-shadow: none;
  }
  
  .tabbar a.button {
    color: #000;
    background:#FFF;
    border:1px solid #100E0F;
    font-family: helvetica,arial,sans-serif;
    text-shadow: none;
  }
  
  .tabbar a.button:hover {
    color: #000;
    background: #EBEBEB;
    border:1px solid #100E0F;
    font-family: helvetica,arial,sans-serif;
    text-shadow: none;
  }
  
    .select, .select:disabled {
    border: 1px solid #100E0F;
    border-top-left-radius:0px;
  }
  
  .userlist {
    border-right:none;
  }
  
  .userlist li button {
    font-family: helvetica,arial,sans-serif;
  }
  
  .chat-log {
    border: 1px solid #C4C4C4;
    font-family: helvetica,arial,sans-serif;
  }
  
  .roomlist a.ilink {
    border:1px solid #100E0F;
    color:#000;
    font-family: helvetica,arial,sans-serif;
  }
  
  .roomlist a.ilink:hover {
    border:1px solid #100E0F;
    color:#100E0F;
    background:#E7F5F4;
  }
  
  .roomlist a.ilink small {
    color:#100E0F;
  }
  
  .roomlist {
    font-family: helvetica,arial,sans-serif;
    color:#100E0F;
  }
  
  .ps-popup {
    background:url(http://i.imgur.com/Q8vHT0Y.png);
  }

  .ps-room .battle-controls {
    background: transparent !important;
  }
  
}
