@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("ok.ru"), domain("odnoklassniki.ru") {
html, body {background: url(http://ya-vidoc3000.narod.ru/fon/15.jpg) no-repeat fixed !important; -webkit-background-size: cover !important; background-size: cover !important;}
  /*Убираем лишнее*/
  .portal-headline, #mainContainer.grid .cover_ov_t, #mainContainer.grid .cover_t, #mainContainer.grid .cover_b, #hook_Block_ThemeTopCenterImageRB, .fake-toolbar-ok, .user.fcofw #fthColWrp.grid {display: none !important;}
  #mainContent.grid .mctc_link, #mainContent.grid .covers_control, #mainContent.grid .covers_control_anim {display: none !important;}
   #hook_BannerForthColumn_ForthColumnTopBannerInner, .banner_new__loaded, .forthTopAdv, #hook_Block_StickyBannerContainer, #hook_Block_TargetBanner, #hook_Block_RecommendedGroups, #hook_Block_UserRecommendationsWithTarget, #hook_Block_ModerationLauncher, #hook_Block_LeftColumnAdCustom, #hook_Block_HolidaysPortlet, .mctc_link {display: none !important;}
  /***************************************************/
  /*Шапка*/
  .user #topPanel {background: none !important; box-shadow: none !important;}
  .toolbar {background: none !important; background-color: #FF1493 !important;}
  .toolbar_nav_a {color: black !important;}
  .toolbar_nav_a:hover {background: none !important; background-color: #FF1493 !important;}
  .toolbar.__pro-click .toolbar_logo:hover {background: none !important; background-color: #FF1493 !important;}
  .toolbar_ucard:hover {background: none !important; background-color: #FF1493 !important;}
  .toolbar_search .it_w {background-color: white !important;}
  .toolbar_dropdown_w.__user-menu .u-menu {background-color: #FF1493 !important;}
  .toolbar_dropdown_w .u-menu_a {color: black !important;}
  .fake-toolbar-mail {background: none !important; height: 48px !important; background-color: #FF1493 !important;}
  /*Контент*/
  .user #mainContainer {padding: 1px !important;}
  #mainContent.grid, .user #mainContent.grid, .user #footer.grid .ft {background: none !important;}
  #hook_Block_MainFeedsWrapper {background-color: lightpink !important; opacity: 0.7 !important;}
  #hook_Block_MainFeedsWrapper:hover {opacity: 1 !important;}
  /*Левая панель*/
  .user #mainContent.grid:before {background: none !important;}
  .portlet.__holiday, .portlet.__wish, .portlet.__invisible {background: none !important; border: none !important;}
  #mainContent.grid #leftColumn .lcTc_btns {background-color: pink !important;}
  #hook_Block_AboutUserRB {background-color: white !important; opacity: 0.7 !important;}
  #hook_Block_AboutUserRB:hover {opacity: 1 !important;}
  /*Друзья онлайн*/
  .fcofw .online-fr.grid .online-fr_block {background: url(http://ya-vidoc3000.narod.ru/fon/13.jpg) !important; background-color: lightpink !important;}
  /*Модальные окна*/
  .modal_overlay, .layer_ovr {background: url(http://ya-vidoc3000.narod.ru/fon/14.jpg) 100% no-repeat !important; background-size: cover !important;}
  .topPanel .modal_overlay {top: 0 !important;}
  .highlightMask {background: url(http://foto-cvety.narod.ru/images/flowers023.jpg) no-repeat fixed !important; background-size: cover !important; opacity: 1 !important;}
  /*Сообщения*/
  .topPanel_m .mdialog_list_tabs, .topPanel_m .mdialog_list_conversations {background: none !important; background-color: #FF1493 !important;}
  .topPanel_m .mdialog_chat_info {box-shadow: none !important; background: none !important; background-color: #FF1493 !important;}
  .topPanel_m .disc-i, .topPanel_m .disc-i .ic_online {background-color: lightpink !important;}
  .topPanel_m .disc-i:hover, .topPanel_m .disc-i:hover .ic_online:before {background-color: orange !important;}
  .topPanel_m .mdialog_list .disc-i_sel .disc-i_cnt_name, .topPanel_m .mdialog_list .disc-i_cnt_name {color: black !important;}
               /*****Фон сообщений*****/
  .topPanel_m .mdialog_chat_window {background: url(http://ya-vidoc3000.narod.ru/fon/13.jpg) no-repeat 100% transparent !important; background-size: cover !important;}
  /*Обсуждения*/
  .mdialog_list, .mdialog_list_conversations, .mdialog_chat_info {background: none !important; background-color: #FF1493 !important;}
  .mdialog_list_conversations:hover {background: none !important;}
  .mdialog, .mdialog_list_separator {background: none !important; border: none !important;}
  .mdialog_list_tabs {background: none !important; background-color: #FF1493 !important;}
  .mdialog_list_conversations {background: none !important; background-color: #FF1493 !important;}
  .mdialog .toggle-span-selected {background-color: lightpink !important;}
  .disc-i_sel .disc-i_cnt {background-color: pink !important;}
  .disc-i_sel .arrow_corn, .disc-i_sel .arrow_corn__b {border: none !important;}
  #searchResults .ust, .userCard .ust, .ust__sm {background-color: orange !important;}
  .disc-i_cnt:hover, .disc-i_sel:hover {background: none !important;}
  .nottouch .disc-i:hover .arrow_corn, .nottouch .disc-i:hover .arrow_corn__b, .nottouch .disc-i_marked .arrow_corn, .nottouch .disc-i_marked .arrow_corn__b {background: none !important;}
  .disc-i .arrow_corn__b, .disc-i .arrow_corn {border: none !important;}
  #searchResults .ust:after, .userCard .ust:after, .ust__sm.ust:after {border: none !important; background: none !important;}
  #searchResults .ust:before, .userCard .ust:before, .ust__sm.ust:before {border: none !important; background: none !important;}
  .disc-i__no-img .disc-i_cnt {background: none !important;}
  .disc-i_image .disc-i_cnt {background: none !important;}
  .disc-i:hover, .disc-i:focus, .disc-i:active {background: none !important;}
  .mdialog_chat_subject, .disc_header {background: none !important; border: none !important;}
  .mdialog_chat_window {background: url(http://ya-vidoc3000.narod.ru/fon/13.jpg) !important;}
  .d_comment_w_center, .d_comment_w_center__compressed {padding: 0 !important;}
  .d_comment_right_w {background-color: white !important; border-radius: 30px !important;}
  /*Оповещения*/
  .notifs_menu_btn.__active {background: none !important;}
  .notifs_menu .mml_cat_ul, .notifs_h, .notifs_lst {background: none !important; background-color: #FF1493 !important;}
  .notifs_cnt {box-shadow: none !important; background-color: #FF1493 !important;}
  .notifs_menu_btn.__active, .notifs_menu_btn {color: black !important;}
  .notifs_menu_btn:hover {background-color: white !important;}
  /**/
  .achiev_w .mctc_navMenuSec, .mainContent_w .achiev_w .mctc_navMenuDropdownSec {color: black !important;}
  .sep-list_item {background-color: white !important;}
}
