css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("heise.de") { 

  #bannerzone,
  #news_notify_hint,
  #mitte #mitte_rechts img,
  #mitte #mitte_rechts iframe,
  #mitte_news .startseite .indexlist .channel_balken {
    display: none;
  }

  #container_content {
    top: 25px;
  }

  #mitte #mitte_rechts {
    width: 25%;
  }
  
  #mitte #mitte_rechts * {
    max-width: 228px;
  }
  
  #mitte #mitte_links {
    width: 75%;
  }
}
