css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("basilmarket.com") {



/* For left column color */
.leftColumn {vertical-align:top !important;background:#b8b7a2 !important;}

/* For right column color */
.rightColumn {vertical-align:top !important;background:#b8b7a2 !important;}

/* Headers for front page */

.left {color:#00be00 !important;font-family:tahoma !important;font-size:12px !important;font-weight:bold !important;background:url("http://s220.photobucket.com/albums/dd121/666smalls666/Basil Halloween Theme/BasilHalloweenleft2.jpg") top repeat-x #8BAF4E !important;min-height:14px !important;}

/* Page Numbers Menu Text Color */

a, a:link, a:visited, a:active {color:#00be00 !important;text-decoration: none;}

/* Front Page, Mail, +friend Text Color */
.normal, .normal a, a.normal {color:#000 !important;}

/* Background */
.day {background:url("http://s220.photobucket.com/albums/dd121/666smalls666/Basil Halloween Theme/BasilHalloweenThemeBackground.gif") top repeat-x #000 !important;}
.night {background:url("http://s220.photobucket.com/albums/dd121/666smalls666/Basil Halloween Theme/BasilHalloweenThemeBackground.gif") top repeat-x #000 !important;}

/* Top Banner */
.innerWrap			{width:800px !important;height:80px !important;padding:0px 0 0 20px !important;margin-left:auto !important;margin-right:auto !important;color:#ccc !important;}
.innerWrap img	{}
.innerWrapday		{background:url("http://s220.photobucket.com/albums/dd121/666smalls666/Basil Halloween Theme/BasilHalloweenHeader.gif") top repeat-x !important;}
.innerWrapnight		{background:url("http://s220.photobucket.com/albums/dd121/666smalls666/Basil Halloween Theme/BasilHalloweenHeader.gif") top repeat-x !important;}
.innerWrapPP1	{float:left !important;padding:0 !important;width:249px !important;height:80px !important;}
.innerWrapPP2	{width:128px !important;height:80px !important;padding:0 !important;}

/* Menu */
.innerMenuWrap    {color:#b2b2b2 !important;background:url(http://www.basilmarket.com/img/menu-back.gif) no-repeat !important;padding:5px !important;}
.innerMenuWrap2	{background-color:#333 !important;height:80px !important;}
.bwnight	{background:url(http://www.basilmarket.com/img/bodywrap-night.gif) top no-repeat #b2b2b2 !important;}
.bwday		{background:url(http://www.basilmarket.com/img/bodywrap-night.gif) top no-repeat #b2b2b2 !important;}

/* Text color (where it says "Page:" and where it says "Quoting an inappropriate post will result in a suspension. If you see something bad, report it, don't quote it.") */
body {margin:0px !important;margin-bottom:10px !important;font-size:13px !important;font-family:tahoma, verdana, arial !important;color:#333 !important;text-align:center !important;background:#EFEEE7 !important;}

/* Text color for posts (except quotes) */
.lh, span.lh a {line-height:1.6em !important;color:#ff8000 !important;}

/* IGN */
.postib {width:113px !important;height:90px !important;text-align:center !important;float:left !important;font-size:9px !important;margin-right:10px !important;line-height:1.6em !important;color:#8fbf8f !important;}

/* Quote color (that took too much time to find) */
.quote{font-size:10px !important;color:#ffa64d !important;border-left:2px solid #ccc !important;}

/* Search Function Bar and Front Page "What this topic is" Part */
.misc1 {background-color:#b1ff00 !important;}

/* Body (2nd to back background) */
#bodyWrap 		{padding:0 10px 10px 10px !important;padding-top:0 !important;width:800px !important;margin-left:auto !important;margin-right:auto !important;text-align:left !important;clear:both !important;background:#d9d9d9 !important;}

/* Headers in Threads */
.right {background:url("http://i220.photobucket.com/albums/dd121/666smalls666/Basil Army Theme/BasilArmyRight2.jpg") repeat-x #5a5900 !important;color:#fff !important;font-size:12px !important;font-family:tahoma !important;font-weight:bold !important;}
.topicBar, .bbTop 		{color:#fff;padding:10px !important;background:url("http://i220.photobucket.com/albums/dd121/666smalls666/Basil Army Theme/BasilArmyTopicbar.jpg") top no-repeat #263f52 !important;}

/* Posts */
.postSubn 			{width:790px !important;color:#333 !important;font-size:9px !important;background:url("http://s220.photobucket.com/albums/dd121/666smalls666/Basil Halloween Theme/BasilHalloweenPostBack-1.gif") top repeat-x !important;height:100px !important;padding:0 !important;}
.postn				{background:#666 !important;padding:5px !important;width:790px !important;border-bottom:1px solid #666 !important;}
.postAuthorAn a strong {color:#00ff00 !important;}
a.link 			{font-weight:bold !important;text-decoration:underline !important;color:#FF6600 !important;padding-left:12px !important;background:transparent url("http://i220.photobucket.com/albums/dd121/666smalls666/Basil%20Halloween%20Theme/BasilHalloweenWebLink.gif") no-repeat center left !important;}

/*Transparent menus */
.sub_menu{opacity: 0.9 !important;}

/* Theme by LemonLicker */

}
